     list-style: square;
 }
 #top-bar ul {
     list-style-image: none; /* IE ONLY! IF list-style-image IS SET */
 }

   #top-bar a:hover {
       color: #000000;
}


 li, p {
     line-height: 141%;
 }

 a {
     color: #075eb3;
     text-decoration: none;
     background: transparent;
 }

#side-bar a:visited {
    color: #04b;
}

a:visited {
    color: #66a;
}

 a.newpage {
     color: #05e;
     text-decoration: none;
     background: transparent;
 }
 a:hover {
     text-decoration: underline;
     background-color: transparent;
     color: #0eb;
 }

 .form-control {
 width: 95%;
 }


 @media (max-width: 479px) {
 #search-top-box-input {
     display: none;
 }

