:root {
     --x-grey: #f5f5f5;
     --x-dark-grey: #c8c8c8;
     --x-very-dark-grey: #909090;
     --x-yellow: #FFF9E5;
     --x-blue: #D5D2E1;
     --x-red: #F4E3E8;
     --x-color-notice: #f2f2c2;
     }
 .x-log, .x-notice, .x-comment { border: solid 1px; padding: 5px; }
     .x-log, .x-notice { box-shadow: 2px 3px var(--x-very-dark-grey); }
     .x-log { background-color: var(--x-grey); }
     .x-logo { background-image: url(http://scp-wiki.wdfiles.com/local--files/the-great-hippo/scp_trans.png); background-repeat: no-repeat; background-position: calc(100% - 15px) 15px; background-size: 150px 150px; background-origin: padding-box; }
     .x-notice { background-color: var(--x-color-notice); text-align: center; }
     .x-comment { background-color: var(--x-grey); border-top: #777 1px solid; border-left: #777 1px solid; border-bottom: #ccc 1px solid; border-right: #ccc 1px solid; box-shadow: -1px -2px var(--x-very-dark-grey) inset; padding-top: 5px; padding-bottom: 5px; margin-top: 1px; margin-bottom: 1px; }
     .x-emboss, .x-email { margin-top: 15px; border-top: #ccc 1px solid; border-left: #ccc 1px solid; border-bottom: #777 1px solid; border-right: #777 1px solid; box-shadow: 2px 2px var(--x-very-dark-grey) inset; }

     /* standard report formatting */
     .x-log-head, .x-log-body { margin: 15px 2px; padding: 0px 10px; }
     .x-log hr { margin-left: 25px; margin-right: 25px; }
     .x-log-head { border-left: solid 3px black; border-radius: 2px; margin-bottom: 15px; }
     .x-log-head th { text-align: left; color: black; font-weight: bold; vertical-align: top; }
     .x-log-head td { padding-left: 15px; vertical-align: top; }
     .x-log-body { border-left: solid 3px var(--x-grey); }

     /* standard collapsible formatting */
     .x-log .collapsible-block-link { color: #303030; font-variant: small-caps; font-size: 110%; }
     .x-log .collapsible-block-unfolded-link:before { content: "■ "; color: grey; font-variant: small-caps; font-size: 110%; }
     .x-log .collapsible-block-folded:before { content: "■ "; color: grey; font-variant: small-caps; font-size: 110%; }
[[/module]]
