[[include theme:scp-offices-sigma]]

[[module CSS]]
.outside {
  margin: 2em -6em;
  padding: 1em 10em;
  --narration-color: rgba(247, 247, 12, 1);
  background: var(--narration-color);
  background: linear-gradient(90deg, rgba(255,255,255,0) 0%, var(--narration-color) 20%, var(--narration-color) 80%, rgba(255,255,255,0) 100%);
  z-index: -2;
}

#page-title {
  display: none;
}

.page-title {
  border-bottom: solid 1px #bbbbbb;
  color: #990011;
  font-weight: normal;
  margin: 0 0 0.6em;
  padding: 0 0 0.25em;
  font-size: 200%;
}
.page-title p {
  margin: 0;
}

@media only screen and (max-width: 1040px) {
  .outside {
    margin-right: 0 !important;
    margin-left: 0 !important;
    padding-left: 3em !important;
    padding-right: 3em !important;
  }
}

body {
  overflow-x: hidden;
}
[[/module]]

[[div class="page-title" style="display: block;" id="#page-title"]]
**##black|SCP-4205 -##** [[span style="display: inline-block; background: #1e1e1e; color: #ccc; padding: 0 2px; border-radius: 3px;"]]**{{В глазах смотрящего}}** 🖳 [[/span]]
[[/div]]

[[>]]
[[module rate]]
[[/>]]

[[module CSS]]
#header {
    filter: inherit;
    -webkit-filter: inherit;
}

@media (max-width: 775px) {
    #page-content > div:nth-child(1) > div {
        margin-top: 0vh !important;
    }
    .page-rate-widget-box {
        width: intrinsic;  
        width: -moz-max-content;    
        width: -webkit-max-content;
        margin: 0 auto 1vh auto !important;
    }
}

.shadow {
-webkit-border-radius: 20px/60px;
-webkit-box-shadow: rgba(0, 0, 0,.30) 0 2px 3px;
  width: 80%;
  margin: auto;
}
.container {
  margin: auto;
  margin-top: -5px;;

  background: #fff;
  border: 1px solid #000;
  box-shadow: 1px 1px 2px #fff inset,
              -1px -1px 2px #fff inset;
  border-radius: 20px/60px;  
}

a img {
    position: relative;
    z-index: 1;
}
[[/module]]

[[module CSS]]
.w-iframe-autoresize {
    overflow: hidden;
    height: 85vh !important;
    display: block;
}
    #main-content{
     position: -webkit-sticky; /* Safari */
  position: sticky !important;
     align-self: flex-start;
     top: -4.375rem;
     left: 0;
     right: 0;
     bottom: 2vh;
    }
@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
#main-content{
     top: 0!important;
    }
}
[[/module]]


