@keyframes fade-in {
    0% {opacity: 0;}
    50% {opacity: 0;}
    100% {opacity: 1;}}

/* Credit to Nanimono Daemon for the mouseover blackbox format, found here:
http://www.scp-wiki.net/scp-style-resource#toc11 */

.dblock {background: white; color: white; transition: 1s;}
.dblock:hover {color: #333; transition: 1.5s;}

/* Credit to James Kanjo for the inline hover tip CSS I butchered, found here:
http://community.wikidot.com/howto:inline-hover-tips */

/* The three different hover classes defined below are for three different background colors: the main text (white), the interview log (#f5f5f5) and the image caption (#eee). */

.hover1 {color: #333; transition: 1s;}
.hover1:hover {color: white; transition: 1s;}
.hover1 span {display: none;}
.hover1:hover span {position: absolute; display: inline; background-color: transparent; color: #333; animation: fade-in 1s;}
.hover1:hover span span {position: relative;}

.hover2 {color: #333; transition: 1s;}
.hover2:hover {color: #f5f5f5; transition: 1s;}
.hover2 span {display: none;}
.hover2:hover span {position: absolute; display: inline; background-color: transparent; color: #333; animation: fade-in 1s; width: 80%;}
.hover2:hover span span {position: relative;}

.hover3 {color: #333; transition: 1s;}
.hover3:hover {color: #eee; transition: 1s;}
.hover3 span {display: none;}
.hover3:hover span {position: absolute; display: inline; background-color: transparent; color: #333; animation: fade-in 1s; width: 24%;}
.hover3:hover span span {position: relative;}

.hover4 {color: #333; transition: 1s;}
.hover4:hover {color: #f5f5f5; transition: 1s;}
.hover4 span {display: none;}
.hover4:hover span {position: absolute; display: inline; background-color: transparent; color: #333; animation: fade-in 1s; width: 19%;}
.hover4:hover span span {position: relative;}
