<script type="text/javascript">
function addEvent(element, eventName, callback) {
    if (element.addEventListener) {
        element.addEventListener(eventName, callback, false);
    } else if (element.attachEvent) {
        element.attachEvent("on" + eventName, callback);
    } else {
        element["on" + eventName] = callback;
    }
}
window.toggle = function(cls) {
  var collapsed = document.querySelectorAll("." + cls + ".collapsed");
  var expanded = document.querySelectorAll("." + cls + ":not(.collapsed)");
  var i;
  for(i = 0; i < collapsed.length; i++) {
    collapsed[i].classList.remove("collapsed");
  }
  for(i = 0; i < expanded.length; i++) {
    expanded[i].classList.add("collapsed");
  }
};

var originalStrings = [];

window.censorTheHidden = function(reveal) {
  var hiddenElems = document.getElementsByClassName('hidden');
  var i = 0;
  for (i = 0; i < hiddenElems.length; i++) {
    var elem = hiddenElems[i];
    if (reveal === -1) {
      originalStrings[i] = elem.innerHTML;
    }
    if (reveal < 1) {
      var valOfElem = elem.innerHTML;
      var censoredString = '';
      var j = 0;
      for (j = 0; j < valOfElem.length; j++) {
        var chr = valOfElem[j];
        censoredString += (chr === ' ' || chr === '-' || chr === ',' || chr === '.' || chr === '"' ? chr : '█');
      }
      elem.innerHTML = censoredString;
      elem.classList.remove('blue');
    }
    else {
      elem.innerHTML = originalStrings[i];
      elem.classList.add('blue');
    }
  }
  var archNotices = document.getElementsByClassName('archwarning');
  for (i = 0; i < archNotices.length; i++) {
    var elem = archNotices[i];
    if (reveal < 1) {
      elem.classList.remove('vanished');
    }
    else {
      elem.classList.add('vanished');
    }
  }
};

var selTabs = [];
window.onload = function() { 

};

var initSelTabs = function() {
  selTabs = [
    document.getElementsByClassName('selTab1')[0],
    document.getElementsByClassName('selTab2')[0],
    document.getElementsByClassName('selTab3')[0],
    document.getElementsByClassName('selTab4')[0],
    document.getElementsByClassName('selTab5')[0]
  ];
};

window.onload = function()  {
  document.getElementById('selector').classList.remove('vanished');
  censorTheHidden(-1);
};

window.addTextToBox = function(text) {
  document.getElementById('togoto').value = text;
};

window.showSelTab = function(num) {
  if (selTabs.length === 0) {
    initSelTabs();
  }
  var selTab = selTabs[num];
  for (i = 0; i < selTabs.length; i++) {
    var st = selTabs[i];
    if (!(st.classList.contains('vanished')))
      st.classList.add('vanished');
  }
  if (selTab)
    selTab.classList.remove('vanished');
};

window.displayFile = function() {
  var name = document.getElementById('togoto').value.toLowerCase();
  var index = -1;
  var reveal = 0;
  switch (name) {
    case 'новостной_репортаж.pdf':
      index = 0;
      break;
    case 'протокол_событий_4800_ур4.docx':
      index = 1;
      break;
    case 'протокол_событий_4800_ур5.docx':
      index = 1;
      reveal = 1;
      break;
    case 'анализ_сигнала_4800.odt':
      index = 2;
      break;
    case 'дневник_5034_кэти.rtf':
      index = 3;
      break;
    case 'история_5034_ур4.thd':
      index = 4;
      break;
    case 'история_5034_ур5.thd':
      index = 4;
      reveal = 1;
      break;
    default:
      index = -1;
  }
  showSelTab(index);
  var error = document.getElementById('error');
  var beginning = document.getElementById('beginning');
  if (index === -1) {
    error.classList.remove('vanished');
    beginning.classList.add('vanished');
  }
  else {
    error.classList.add('vanished');
    beginning.classList.remove('vanished');
  }
  document.getElementById('name').innerHTML = name;
  censorTheHidden(reveal);
  return false;
};
</script>

