[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Bad+Script&display=swap');

.mobile-swipe {
  color: black;
  text-shadow: -1px -1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff, 1px 1px 0 #fff;
  width: 100%;
  text-align: center;
  display: flex;
  align-items: center;
  flex-direction: column;
  top: 80px;
  left: 0;
  right: 0;
  width: 100%;
  font-size: 90%;
  margin-bottom: 24px;
}

@media (min-width: 1248px) {
  .mobile-swipe {
    display: none;
  }
}

.white-text {
  color: white;
  text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;
  position: absolute;
  z-index: 1;
}

.dark-text {
  color: black;
  text-shadow: -1px -1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff, 1px 1px 0 #fff;
  position: absolute;
  z-index: 1;
}

.tree-shade-wrapper {
  margin: 0 auto;
  max-width: 900px !important;
  position: relative;

  mask-image: linear-gradient(to bottom, #00000001 0, #ffffff 64px, #ffffff calc(100% - 64px), #00000001 100%);
}

.tree-shade-wrapper::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  height: 100%;
  width: 1px;
  background: black;
  z-index: 99;
}


.tree-shade-wrapper::after {
  content: '';
  position: absolute;
  right: 0;
  top: 0;
  height: 100%;
  width: 1px;
  background: black;
  z-index: 99;
}

.tree-scroll-wrapper {
  overflow-x: auto;
}

.tree-container {
  width: 900px;
  min-width: 900px;
  position: relative;
  font-size: 13px;
  line-height: 110%;
}

.tree-container > p > br {
  display: none;
}

.tree-container > p {
  margin: 0;
  padding: 0;
  line-height: 110%;
}

.tree-container .tree {
  width: 100%;
  image-rendering: pixelated;
  image-rendering: -webkit-optimize-contrast;
  position: relative;
  z-index: 0;
  pointer-events: none;
  user-select: none;
}

.tree-interview {
  width: min(calc(100vw - 64px), 350px);
  min-width: min(calc(100vw - 64px), 350px);
  text-align: center;
  position: relative;
  margin-bottom: 48px;
  margin-left: auto;
  margin-right: auto;
  top: -16px;
}

@media (max-width: 600px) {
  .tree-interview {
    font-size: 90%;
  }
}
[[/module]]

