[[include component:fade-in speed=1]]
[[include theme:basalt darkmode=a | hidetitle=a | wide=a]]

[[module CSS]]

#top-bar div[class*="top-bar"] > ul > li > a {
  font-weight: 900;
}

.anom-bar-container .danger-diamond > .bottom-icon::before {
  background-image: url("https://scpfoundation.net/local--files/scp-7912/thaumiel-icon.svg");
  background-size: contain;
}

.text-part > .main-class::after {
    background-image: url("https://scpfoundation.net/local--files/scp-7912/thaumiel-icon.svg");
}

@import url('https://fonts.googleapis.com/css2?family=Zen+Kaku+Gothic+Antique:wght@300;400;500;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Mulish:wght@700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Spline+Sans:wght@400;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Roboto+Condensed:ital,wght@0,300;0,400;0,700;1,300;1,400;1,700&display=swap');
div.authorlink-wrapper{margin-top: var(--author-top-adjust, .2em) !important;}
:root {
--header-logo: url('https://scpfoundation.net/local--files/scp-7912/dept.png');
--title:"И.С. «МНЕМОЗИНА»";
--subtitle:"ОТДЕЛ УДАЛЕНИЙ";
--sidesubtitle: "УДАЛЕНИЯ";
--sidelogo: url(https://scpfoundation.net/local--files/scp-7912/dept.png);
--header-font: "Mulish", sans-serif;
--body-font: 'Zen Kaku Gothic Antique', sans-serif;
--ACS-font: var(--header-font);
--five-color: 116,71,88 !important;
--basalt-undertone: 60,9,33 !important;
--swatch-menubg-black-color: 10,10,10 !important;
--general-border-color: 1,1,1 !important;
--swatch-menutxt-dark-color: 1,1,1 !important;
--UI-font: "Mulish", sans-serif;
--basalt-main-text-color: 253, 241, 242 !important;
}
#page-content .widetable .wiki-content-table {
    width: 100% !important;
    font-size: 1.5em;
}
.hcontainer {

}
:is(span.specialtext, span.st) {
text-decoration: underline 2px;
    text-underline-offset: 3px;
}
@keyframes strike{
0%   { width : 0; }
20% { width: 50%; }
40% { width: 75%; }
60% { width: 100%; }
80% { width: 50%; }
100% { width: 0; }
}
.strike, .unstrike {
  position: relative;
}
.strike::after, .unstrike::after {
  content: ' ';
  position: absolute;
  top: 50%;
  left: 0;
  width: 100%;
  height: 2px;
  background: rgb(253, 241, 242);
  animation-name: strike;
  animation-duration: 4s;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
  animation-fill-mode: forwards;
}
@keyframes unstrike{
0%  { width: 0; }
20% { width: 25%; }
40% { width: 75%; }
60% { width: 50; }
80% { width: 100%; }
100% { width: 0; }
}
body {
    background-image: url(https://scpfoundation.net/local--files/scp-7912/sort.jpg);
    background-attachment: fixed;
    background-size: cover;
    background-blend-mode: difference;
}
.acs-extra-1 {

font-family: var(--ACS-font), var(--body-font) !important;
}
#page-content a.collapsible-block-link {

color: rgb(253, 241, 242);

}
#page-content div.modal {
    max-width: var(--main-content-width) !important;
}
.anom-bar > div.bottom-box::before {
display: none !important;
}
div.overwatch_memo {

--memo-heading: "ДАННЫЙ ДОКУМЕНТ ОТМЕЧЕН ДЛЯ УДАЛЕНИЙ";
--memo-logo: url(https://scpfoundation.net/local--files/scp-7912/deletion.png);
--memo-color: 253, 241, 242;
}

div.overwatch {
filter: brightness(65%);
}
div[class$="_memo"] {

background-color:rgba(69,69,69,0.6);
}
.series-nav {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-content: center;
    align-items: baseline;

    margin: 1rem 0;
    padding: .02rem 1rem;
    background: rgb(var(--basalt-tertiary-color));

    border-top-right-radius: .67rem;
    border-top-left-radius: .67rem;

    font-size: 1.2em;
    font-family: var(--UI-font);
}

.series-nav>* {
    margin: 0 0.3em;
}

.end_card {
    background: linear-gradient(145deg, rgba(var(--basalt-dark-element-color), 0.5) 0%, rgb(var(--basalt-tertiary-color)) 80%);
    padding: 1.25rem 2rem;
    text-align: center;
}
div:is(.jotting, .transcript) {
border: double 0.15rem #000;
}
#page-content :is(blockquote, .blockquote) {
    border-color: #000;
    font-weight: normal;
    background-color: #060b11;
    color: #cdd9d6;
}

.image-block, .scp-image-block {

border-bottom: none !important;
}

#page-content div:is(.jotting, .transcript) {

max-width: 44rem;
}

.text-part .risk-class::before, .text-part .risk-class::after, .bottom-box > div.diamond-part::before, .text-part .disrupt-class::after, .text-part .disrupt-class::before,.danger-diamond > .arrows {


}

