let code = '';

function writeln(text) { code += text + '\n'; }

writeln (`<!DOCTYPE html><html><head><meta charset="utf-8"></head>`);
writeln ("<body>");
writeln ("<h2 style='text-align:center;'>ЗАСЕКРЕЧЕНО ПРИКАЗОМ O5-█</h2>");             
writeln ("Версия для сотрудников с уровнем допуска " + form.input13.value + ".");
writeln ("<p><b>Объект №:</b> SCP-" + form.input2.value + "-J</p><p><b>Класс объекта: " + form.input15.value + "</b></p><p><b>Особые условия содержания:</b> SCP-" + form.input2.value + "-J следует хранить в устланной " + form.input4.value + " камере содержания " + form.input22.value + ", под охраной отряда из по меньшей мере " + form.input16.value + " " + form.input14.value + ", вооружённых " + form.input10.value + ".</p><p> Если SCP-" + form.input2.value + "-J начинает " + form.input25.value + " себе " + form.input34.value + ", " + form.input17.value + " должен " + form.input33.value + " SCP-" + form.input2.value + "-J вплоть до прекращения такого поведения. В случае нарушения ОУС МОГ " + form.input5.value + "-7 (''" + form.input6.value + "'') следует отправить к последнему известному местонахождению SCP-" + form.input2.value + "-J.</p>");              

writeln ("<p><b>Описание:</b> SCP-" + form.input2.value + "-J - это " + form.input7.value +" самец вида "+ form.input8.value +". Как и большинство представителей его вида, объект способен " + form.input19.value + ", и, питаясь " + form.input4.value+", поглощать за раз массы корма с общим весом в два раза больше его собственного." 
+"</p><p>Аномальные свойства SCP-" + form.input2.value + "-J проявляются в случае его контакта с " + form.input26.value + ", при котором он превращается в "+ form.input30.value + ". Когда это происходит, все " + form.input31.value + " в радиусе " + form.input9.value + " километров начинают сильно и беспорядочно " + form.input32.value +", что часто влечёт за собой потери среди гражданских. </p><p> Кроме того, многие исследователи при изучении объекта замечают довольно подозрительные отсылки к личности, известной мировой общественности как " + form.input20.value + ". На данный момент установление связи этих отсылок с основным аномальным свойством SCP-" + form.input2.value + "-J представляется затруднительным.</p>");

writeln ("<p><b>История обнаружения:</b> SCP-" + form.input2.value + "-J впервые был обнаружен " + form.input24.value + ", где его использовала преступная группировка, называвшая себя " + form.input18.value + ", планируя достичь " + form.input29.value + " с его помощью. К счастью, МОГ " + form.input5.value + "-7 (''" + form.input6.value + "'') удалось захватить объект и свести потери среди гражданских к количеству " + form.input21.value + " человек.</p>")

writeln ("<p><b>Приложение:</b> Протокол тестирования " + form.input2.value +"-1"
+ "</p><p><blockquote><b>Д-р "+ form.input12.value +":</b> Проферка, проферка. Эта штука рапотает? Карашо. Это токтор "+ form.input12.value +", и я сопираюсь протестирофать реакцию SCP-" + form.input2.value +" на расличные претметы. Сейчас у нас есть " + form.input3.value + ". Котофы начать, токтор "+ form.input1.value +"?"+  "</p><p><b>Д-р "+ form.input1.value +":</b> Я готов, доктор "+ form.input12.value +"." + "</p><p><b>Д-р "+ form.input12.value +":</b> Самечательно! Сейтщас "+ form.input3.value + " ф поле зрения " + form.input2.value + "... хмм, кашется, он уше расопрался, што к тщему." + "</p><p><b>Д-р "+ form.input1.value +":</b> Я пишу - ''демонстрирует высокую обучаемость''.</p><p><b>Д-р " + form.input12.value +":</b> Теперь опъект смотрит прямо на меня, кашется... О ПОШЕ! Мои " + form.input11.value + "! Он схфатил меня са " + form.input11.value + "! Как польно! КАК ПОООЛЬНОООО!"
+ "</p><p><b>КОНЕЦ ЗАПИСИ</b></blockquote>" + 
"<i>Ввиду инцидента " + form.input2.value +"-J-1 все испытания запрещены до особого распоряжения.</i> - O5-█</p>");

writeln ("<br>");   
