[[html]]
<style>
@font-face {
  font-family: Inter;
  font-style: normal;
  font-weight: 100 900;
  font-display: swap;
  src: url('https://scpfoundation.net/-/static/fonts/Inter-roman.var.woff2?v=3.19') format('woff2')
}
body {
min-height: 1500px;
}
body * {
font-family: "Inter", "Verdana" !important;
}
.gsc-adBlock {
display: none !important;
}
.gsc-control-cse .gsc-option-menu {
  display: block;
  border: 1px solid #ccc !important;
  border-radius: 8px;
  background: linear-gradient(to top, #eee 0%, #eee 80%, #fff 100%) !important;
  color: #666;
  box-shadow: 0 2px 3px #ccc !important;
  padding: 0px !important;
}
.gsc-option-menu-item-highlighted {
  background-color: #732717 !important;
  border-color: #eee;
  border-width: 1px 0;
  color: white !important;
}
.gsc-option-menu-item-highlighted:first-child {
  border-top-right-radius: 8px;
  border-top-left-radius: 8px;
}
.gsc-option-menu-item-highlighted:last-child {
  border-bottom-right-radius: 8px;
  border-bottom-left-radius: 8px;
}
.gsc-result-info {
  padding: 0px !important;
}
.gsc-control-cse {
  background: white;
  box-shadow: 0 2px 3px #ccc;
}
.gsc-selected-option-container {
  cursor: default;
  font-size: 11px;
  font-weight: bold;
  outline: 0;
  padding: 0 28px 0 6px;
  position: relative;
  margin: 6px auto;
  border: 1px solid #ccc;
  border-radius: 8px;
  background: linear-gradient(to top, #eee 0%, #eee 80%, #fff 100%);
  color: #666;
  box-shadow: 0 2px 3px #ccc;
  height: 27px;
  line-height: 27px;
}
input.gsc-input, .gsc-input-box, .gsc-input-box-hover, .gsc-input-box-focus {
  height: 33px;
  border-radius: 4px !important;
  border: 1px solid #d1cece;
}
.gsc-control-cse {
  border: 1px solid #ccc !important;
}
.gsc-search-button-v2, .gsc-search-button-v2:hover, .gsc-search-button-v2:focus {
  border: 1px solid #ccc !important;
  background: linear-gradient(to top, #eee 0%, #eee 80%, #fff 100%) !important;
  box-shadow: 0 2px 3px #ccc;
  margin-left: 0 !important;
  border-radius: 8px !important;
  height: 33px;
}
.gsc-search-button-v2:hover {
  background: linear-gradient(to top, #732717 0%, #732717 80%, #cb4536 100%) !important;
  border: 1px solid #621b0d !important;
  box-shadow: 0 2px 3px #777;
  color: white;
  cursor: pointer;
}
.gsc-search-button-v2:hover svg {
  fill: #FFF;
}
.gsc-result-info {
  font-style: italic !important;
}
.gsc-selected-option-container {
  border-radius: 10px !important;
}
.gcsc-more-maybe-branding-root {
  display: none !important;
}
.gs-result .gs-title *, .gsc-cursor-page, .gs-spelling a {
  color: #d52c1f !important;
}
</style>
<div class="gcse-search"></div>
<script src="https://cse.google.com/cse.js?cx=30e23d645bbf94ddb">
</script>
<script>
function trySetField() {
  const input = document.querySelector('input.gsc-input');
  const btn = document.querySelector('button.gsc-search-button');
  if (!input || !btn) {
    window.requestAnimationFrame(trySetField);
    return;
  }
  const q = %%path_expr|q%%;
  console.log('query', q);
  if (q !== '%\%path_expr|q%\%') {
    console.log('set query');
    input.value = q;
  }
  btn.click();
}
trySetField();
</script>
[[/html]]
