[[module CSS]]

.hr-main {
    margin: 16px 0;
    padding: 0;
    height: 3px;
    border: none;
    background-color: #707070;
    box-shadow: 0 2px 3px #ccc;
}

.scp-front-block {
    margin: 16px 0;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    position: relative;
    overflow: hidden;
}

.scp-content {
    padding: 8px 24px;
}

.scp-content ul {
    padding: 0 24px;
}

.scp-content ul li::marker {
    content: "➤ ";
    transform: rotate(90deg);
    color: #b01;
    font-size: 1.4em;
}

.scp-front-block>* {
    position: relative;
    z-index: 1;
}








.lb-title {
    border-bottom: 2px solid #6f6f6f;
    background: #cccccc;
    padding: 4px 24px;
    text-transform: uppercase;
    position: relative;
    display: flex;
    align-items: center;
    width: 25%;
}

.lb-title p {
    margin: 0;
}

.lb-title::before,
.lb-title::after {
    position: absolute;
    content: '';
    left: 100%;
    top: 0;
    bottom: -2px;
    width: 24px;
}

.lb-title::before {
    background: #cccccc;
    clip-path: polygon(0 0, calc(100% - 1px) 0, 0 calc(100% - 1px));
}

.lb-title::after {
    background: #6f6f6f;
    clip-path: polygon(0 calc(100% - 2px), calc(100% - 2.5px) 0, 100% 0, 1px 100%, 0 100%);
}



.scp-front-title {
    padding: 4px 24px;
    text-transform: uppercase;
    background: linear-gradient(to top, #d8d8d8, #ececec);
    border-bottom: 1px solid #dcdcdc;
}

@media (max-width: 979px) {
    .lb-title {
        padding: 4px 24px;
        text-transform: uppercase;
        background: linear-gradient(to top, #d8d8d8, #ececec);
        border-bottom: 1px solid #dcdcdc;
        width: 100%;
    }

    .lb-title::before,
    .lb-title::after {
        display: none;
    }

    .lb-single-article,
    .lb-many-articles,
    .lb-article-desc {
        margin: 16px 24px;
        padding: 0;
        border: 0;
        display: block;
    }
}

.scp-front-button,
.scp-front-button:visited,
.scp-front-button:link {
    display: block;
    margin: 6px;
    border: 1px solid #ccc;
    border-radius: 8px;
    padding: 8px;
    background: linear-gradient(to top, #eee 0%, #eee 80%, #fff 100%);
    text-decoration: none;
    color: #666;
    box-shadow: 0 2px 3px #ccc;
    min-width: 260px;
    text-align: center;
}

.scp-front-button:hover {
    background: linear-gradient(to top, #732717 0%, #732717 80%, #cb4536 100%);
    border: 1px solid #621b0d;
    box-shadow: 0 2px 3px #777;
    color: white;
}

#page-content .collapsible-block {
    margin: 16px 0;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    transition: all 0.3s ease;
}
#page-content .collapsible-block:hover {
    border: #505052 solid 1px;
    box-shadow: 0 2px 3px #ccc;
}
#page-content .collapsible-block-content {
    margin: 16px 24px;
}
#page-content .collapsible-block-unfolded-link {
    text-align: center;
}
#page-content .collapsible-block-folded {
    text-align: center;
    position: relative;
}
#page-content .collapsible-block-link {
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;
    padding: 18px 24px;
    display: block;
}
/* часть ниже убирает границы спойлера в спойлере */
#page-content .collapsible-block .collapsible-block {
    border: none;
    box-shadow: none;
}
/* а эта убирает отступы внутри спойлера в спойлере */
#page-content .collapsible-block-content .collapsible-block-content {
    margin: 0px;
}

.collaps .collapsible-block-folded::before {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    width: 28px;
    height: 100%;
    background-color: #901;
    clip-path: polygon(0 0, 0% 100%, 80% 50%);
}

.collaps .collapsible-block-unfolded::before {
    content: "";
    position: absolute;
    left: 0;
    top: 0;
    width: 28px;
    height: 51px;
    background-color: #901;
    clip-path: polygon(0 0, 0% 100%, 80% 50%);
}

.collaps .collapsible-block-folded::after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    width: 28px;
    height: 100%;
    background-color: #901;
    clip-path: polygon(100% 0, 20% 50%, 100% 100%);
}

.collaps .collapsible-block-unfolded::after {
    content: "";
    position: absolute;
    right: 0;
    top: 0;
    width: 28px;
    height: 51px;
    background-color: #901;
    clip-path: polygon(100% 0, 20% 50%, 100% 100%);
}

.collaps .collapsible-block-unfolded {
    text-align: center;
    border-left: 3px solid #901;
    border-right: 3px solid #901;
    position: relative;
    padding-bottom: 14px;
}


.rules {
    color: #901;
    font-weight: bold;
    font-size: 160%;
    text-align: center;
}

#page-content .collapsible-block:first-child {
    margin-bottom: 7rem;
}

.results {
    position: relative;
    display: flex;
}



.winner {
  width: 50%;
}

.second-winner {
  text-align: right;
}

.fa-award {
  padding-top: 1rem;
}

.fa-award::before {
  font-family: "Font Awesome 5 Free";
  position: relative;
  top: 15px;
  font-weight: 900;
  font-size: 50px;
  content: "\f559";
  font-style: normal;
}

.winner p {
  font-size: 120% !important;
  line-height: 170% !important;
}

.winner a {
  font-size: 110%;
  font-weight: 700;
}

.wins {
  font-size: 160%;
  color: #901;
  font-weight: 700;
  margin-top: 1rem;
}

@media (max-width: 767px) {
.results {
   flex-direction: column;
   text-align: center !important;
}
.winner {
   width: 100%;
}
.second-winner {
text-align: center;
}

[[/module]]
