+ Новые рассказы


[[module CSS]]

/* implement custom tables + color marks */
@media (max-width: 767px) {
#page-content div, #page-content div table {
    font-size: 90%;
}
#page-content div, #page-content div .fake table {
    font-size: 100% !important;
}
}
.noted .collapsible-block-folded {
    padding: 10px;
}
.noted .collapsible-block-unfolded {
    padding: 10px;
}
.fake .heading {
  border-top: 1px solid #888;
  font-weight: bold;
  background: #EEE;
  text-align: center;
}
.pager {
  display: none;
}
.fake .item {
  box-sizing: border-box;
  padding: 3px;
  border-left: 1px solid #888;
  border-bottom: 1px solid #888;
  position: relative;
}
.item>p {
  padding: 0.3em;
}
.fake td {
  position: relative;
}
.flags {
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  display: flex;
}
.flags > * {
  box-sizing: border-box;
  display: block;
  border-right: 1px solid #888;
}
.green-mark {
    border-left: solid 5px #44944A;
}
.yellow-mark {
    border-left: solid 5px #ffc000;
}
.red-mark {
    border-left: solid 5px red;
}

/* mobile */
@media (max-width: 1110px) {
.mobile-table {
  display: inherit !important;
}
.fake > div {
  display: grid;
  grid-template-columns: 3fr 2fr !important;
}
.fake p {
  margin: 0 !important;
  line-height: 150%;
}
.fake > div .item:nth-child(4n+2) {
    border-bottom: none !important;
}
.fake > div .item:nth-child(2n+2) {
    display: none;
}
.mob-rating {
    display: block !important;
    padding: 0.3em;
}
.fake > div .item:nth-child(4n+3) {
  border-right: 1px solid #888;
  padding-left: 6px !important;
}
.fake > div .item:nth-child(2n) {
  border-right: 1px solid #888;
  padding-left: 10px;
}
.fake > div .item:nth-child(2n+1) {
  padding-left: 16px;
}
}

/* desktop */
.fake > div {
  display: grid;
  grid-template-columns: 3fr 0.5fr 1.5fr 1fr;
}
.fake p {
  margin: 0;
}
.fake > div .item:nth-child(4n) {
  border-right: 1px solid #888;
}
.fake > div .item:nth-child(4n+1) {
  padding-left: 16px;
}

.fake .pager {
  width: 100%;
  position: absolute;
  bottom: 0;
  margin: 0.5em auto;
}
.w-list-pages {
  padding-bottom: 40px;
}
