[[module CSS]]
a[title="%%path|order%%"] {
    background: linear-gradient(to top, #732717 0%, #732717 80%, #cb4536 100%);
    border: 1px solid #621b0d;
    box-shadow: 0 2px 3px #777;
    color: white;
    cursor: default;
}
#page-title {
  display: none !important
}
.sub-page-title {
  border-bottom: 1px solid #bbb;
  margin: 0 0 0.6em;
}
.sub-page-title h1 {
  font-size: 200%;
  font-weight: normal;
  padding: 0 0 0.25em 0;
  margin: 0;
  font-family: inherit;
}
.sub-page-title h1 sup {
  line-height: 0;
}
.order-by {
  display: block;
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 8px;
  background: linear-gradient(to top, #eee 0%, #eee 80%, #fff 100%);
  color: #666;
  box-shadow: 0 2px 3px #ccc;
  width: 150px;
  text-align: center;
}
.order-by:hover {
  background: linear-gradient(to top, #732717 0%, #732717 80%, #cb4536 100%);
  border: 1px solid #621b0d;
  box-shadow: 0 2px 3px #777;
  color: white;
  cursor: pointer;
  text-decoration: none;
}
[[/module]]



[[module ListPages created_by="@URL|none" category="*" limit="1"]]
[[div class="sub-page-title"]]
+ Статьи %%created_by%%
[[/div]]
[[div_ style="display: flex; gap: 8px;"]]
[[a class="order-by desc" title="created_at desc" href="./created_at desc"]]По дате создания[[/a]]

[[a class="order-by desc" title="rating desc" href="./rating desc"]]По рейтингу[[/a]]

[[a class="order-by desc" title="votes desc" href="./votes desc"]]По голосам[[/a]]

[[/div]]
[[/module]]

[[module CSS]]
.author-single {
  display: block;
  padding: 24px;
  border: 1px solid #f7f7f7;
  box-shadow: 0px 2px 4px #00000011;
  margin-bottom: 8px;
  transition: all 0.2s ease;
  background: white;
  text-decoration: none !important;
  color: black !important;
}

.author-single br {
  display: none;
}

.author-single:hover {
  background: #f7f7f7;
  border: 1px solid #f0f0f0;
  box-shadow: 0px 2px 7px #00000022;
}

.author-single .title {
  display: block;
  font-size: 24px;
  line-height: 30px;
  font-weight: 500;
  color: #b01;
}

.author-single .rating {
  display: block;
  font-size: 11px;
  line-height: 11px;
  font-weight: bold;
  float: right;
}

.author-single .c-tags {
  display: block;
  margin-top: 8px;
  font-size: 90%;
}

.author-single .date {
  display: block;
  color: #666;
  font-style: italic;
  margin-top: 16px;
  font-size: 11px;
  line-height: 16px;
}
[[/module]]

[[module CountPages created_by="@URL|none" category="sandbox" tags="+ru -дополнение"]]
[[ifexpr %%total%%]]
++ Статьи на Полигоне (%%total%%)
[[/ifexpr]]
[[/module]]
[[module ListPages created_by="@URL|none" category="sandbox" perPage="200" tags="+ru -дополнение" order="@URL|none"]]
[[a class="author-single" href="/%%fullname%%"]]
[[span class="rating"]]Рейтинг: [[span]]%%rating%% (%%rating_votes%%)[[/span]][[/span]]
[[span class="title"]]%%title%%[[/span]]
[[span class="c-tags"]]Теги: [[span]]%%tags%%[[/span]][[/span]]
[[span class="date"]]Статья создана: %%created_at|%H:%M %d.%m.%Y%%, обновлена: %%updated_at|%H:%M %d.%m.%Y%%[[/span]]
[[/a]]
[[/module]]

[[module CountPages created_by="@URL|none" category="_default wl" tags="+ru -дополнение"]]
[[ifexpr %%total%%]]
++ Статьи в основном пространстве (%%total%%)
[[/ifexpr]]
[[/module]]
[[module ListPages order="@URL|none" created_by="@URL|none" category="_default wl" perPage="200" tags="+ru -дополнение" ]]
[[a class="author-single" href="/%%fullname%%"]]
[[span class="rating"]]Рейтинг: [[span]]%%rating%% (%%rating_votes%%)[[/span]][[/span]]
[[span class="title"]]%%title%%[[/span]]
[[span class="c-tags"]]Теги: [[span]]%%tags%%[[/span]][[/span]]
[[span class="date"]]Статья создана: %%created_at|%H:%M %d.%m.%Y%%, обновлена: %%updated_at|%H:%M %d.%m.%Y%%[[/span]]
[[/a]]
[[/module]]
