[[module SiteChanges]]


[[module CSS]]
@media(max-width: 799px) {
  .changes-list table tr td:nth-child(2),
  .changes-list table tr td:nth-child(3),
  .changes-list table tr td:nth-child(4) {
    width: max-content;
  }
.changes-list table tr td:nth-child(5) {
   width: 88px;
}
  .changes-list table tr td:nth-child(1) {
  width: 110px;
}
}
.changes-list-item .comments {
  word-break: break-all;
}
[[/module]]
