[[module:u CSS]]
#u-%%path|offset%% {
    background: linear-gradient(to top, #732717 0%, #732717 80%, #cb4536 100%);
    border: 1px solid #621b0d;
    box-shadow: 0 2px 3px #777;
    color: white;
    cursor: default;
}
.sub-page-title {
  border-bottom: 1px solid #bbb;
  margin: 0 0 0.6em;
}
.sub-page-title h1 {
  font-size: 200%;
  font-weight: normal;
  padding: 0 0 0.25em 0;
  margin: 0;
  font-family: inherit;
}
.sub-page-title h1 sup {
  line-height: 0;
}
.author-single {
  display: block;
  padding: 24px;
  border: 1px solid #f7f7f7;
  box-shadow: 0px 4px 6px #00000011;
  margin-bottom: 8px;
  transition: all 0.2s ease;
  background: white;
  text-decoration: none !important;
  color: black !important;
}

.author-single br {
  display: none;
}

.author-single:hover {
  background: #f7f7f7;
  border: 1px solid #f0f0f0;
  box-shadow: 0px 2px 7px #00000022;
}

.author-single .title {
  display: block;
  font-size: 24px;
  line-height: 30px;
  font-weight: 500;
  color: #b01;
}

.author-single .rating {
  display: block;
  font-size: 12px;
  line-height: 12px;
  font-weight: bold;
  float: right;
}

.author-single .c-tags {
  display: block;
  margin-top: 8px;
}

.author-single .date {
  display: block;
  color: #666;
  font-style: italic;
  margin-top: 16px;
  font-size: 11px;
  line-height: 16px;
}
[[/module]]

[[module CSS]]

.hr-main {
    margin: 16px 0;
    padding: 0;
    height: 3px;
    border: none;
    background-color: #707070;
    box-shadow: 0 2px 3px #ccc;
}

.scp-front-block {
    margin: 16px 0;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    position: relative;
    overflow: hidden;
}

.scp-content {
    padding: 8px 24px;
}

.scp-content ul {
    padding: 0 24px;
}

.scp-content ul li::marker {
    content: "➤ ";
    transform: rotate(90deg);
    color: #b01;
    font-size: 1.4em;
}

.scp-front-block>* {
    position: relative;
    z-index: 1;
}








.lb-title {
    border-bottom: 2px solid #6f6f6f;
    background: #cccccc;
    padding: 4px 24px;
    text-transform: uppercase;
    position: relative;
    display: flex;
    align-items: center;
    width: 25%;
}

.lb-title p {
    margin: 0;
}

.lb-title::before,
.lb-title::after {
    position: absolute;
    content: '';
    left: 100%;
    top: 0;
    bottom: -2px;
    width: 24px;
}

.lb-title::before {
    background: #cccccc;
    clip-path: polygon(0 0, calc(100% - 1px) 0, 0 calc(100% - 1px));
}

.lb-title::after {
    background: #6f6f6f;
    clip-path: polygon(0 calc(100% - 2px), calc(100% - 2.5px) 0, 100% 0, 1px 100%, 0 100%);
}



.scp-front-title {
    padding: 4px 24px;
    text-transform: uppercase;
    background: linear-gradient(to top, #d8d8d8, #ececec);
    border-bottom: 1px solid #dcdcdc;
}

@media (max-width: 979px) {
    .lb-title {
        padding: 4px 16px;
        text-transform: uppercase;
        background: linear-gradient(to top, #d8d8d8, #ececec);
        border-bottom: 1px solid #dcdcdc;
        width: 100%;
    }

    .lb-title::before,
    .lb-title::after {
        display: none;
    }

    .lb-single-article,
    .lb-many-articles,
    .lb-article-desc {
        margin: 16px 24px;
        padding: 0;
        border: 0;
        display: block;
    }
.scp-content {
padding: 8px 16px;
}
.scp-front-title {
padding: 4px 16px;
}
}

.scp-front-button,
.scp-front-button:visited,
.scp-front-button:link {
    display: block;
    margin: 6px auto;
    border: 1px solid #ccc;
    border-radius: 8px;
    padding: 8px;
    background: linear-gradient(to top, #eee 0%, #eee 80%, #fff 100%);
    text-decoration: none;
    color: #666;
    box-shadow: 0 2px 3px #ccc;
    width: auto;
    min-width: 70px;
    text-align: center;
}

.scp-front-button:hover {
    background: linear-gradient(to top, #732717 0%, #732717 80%, #cb4536 100%);
    border: 1px solid #621b0d;
    box-shadow: 0 2px 3px #777;
    color: white;
}

.scp-subtitle {
  border-bottom: 3px solid #707070;
  display: block;
  width: fit-content;
  padding-bottom: 4px;
}


[[/module:u]]
