[[module CSS]]
.options {
  display: flex; 
  flex-direction: row; 
  justify-content: space-around; 
  padding: 10px;
}
@media (max-width: 1040px) {
  .options {
    display: block;
}
}
@media (max-width: 571px) {
  .options {
    display: block;
}
.style .main input[type="submit"] {
   max-width: 100px;
}
}
.named {
  font-size: 85%; 
  margin: 10px;
}
.scp-front-block {
  margin: 16px 0;
  border: 1px solid #dcdcdc;
  background: white;
  box-shadow: 0 2px 3px #ccc;
  transition: all 0.3s ease;
}
.new-page-form {
  border: 1px solid #ccc;
  background: #eee;
  padding: 8px;
  text-align: center;
}
.new-page-form form {
  display: flex;
  flex-direction: column;
}
.new-page-form p {
  margin: 4px 0;
}
.spoil .collapsible-block-link{
  text-transform: uppercase; 
  font-weight: 600; 
  display: block;
  padding: 16px 24px;
}
.fb-featured {
  line-height: 20px;
}
.fb-featured-content {
  margin: 16px 24px;
}
@media (max-width: 979px) {
 .base {
  width: 100%;
}
}
.base .pager {
  display: none;
}
.sub-page-title {
  border-bottom: 1px solid #bbb;
  margin: 0 0 0.6em;
}
.sub-page-title h1 {
  font-size: 200%;
  font-weight: normal;
  padding: 0 0 0.25em 0;
  margin: 0;
  font-family: inherit;
}
.sub-page-title h1 sup {
  line-height: 0;
}
.author-single {
  display: block;
  padding: 24px;
  border: 1px solid #f7f7f7;
  box-shadow: 0px 4px 6px #00000011;
  margin: 5px;
  transition: all 0.2s ease;
  background: white;
  text-decoration: none !important;
  color: black !important;
}

.author-single br {
  display: none;
}

.author-single:hover {
  background: #f7f7f7;
  border: 1px solid #f0f0f0;
  box-shadow: 0px 2px 7px #00000022;
}

.author-single .title {
  display: block;
  font-size: 24px;
  line-height: 30px;
  font-weight: 500;
  color: #b01;
}

.author-single .rating {
  display: block;
  font-size: 12px;
  line-height: 12px;
  font-weight: bold;
  float: right;
}

.author-single .c-tags {
  display: block;
  margin-top: 8px;
}

.author-single .date {
  display: block;
  color: #666;
  font-style: italic;
  margin-top: 16px;
  font-size: 11px;
  line-height: 16px;
}
.yui-navset .yui-content {
  background-color: transparent;
  border: none;
}
 .yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a{background-color:inherit;background-image:inherit}.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus{background:inherit;text-decoration:inherit}.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:hover{color:inherit;background:inherit}.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav{border-color:inherit}.yui-navset li{line-height:inherit}

/* -------- YUI TAB CUSTOMIZATION --------
 * Стыбжено с некоего FlopStyle CSS
**/
 .yui-navset *{
     transition:
          color 80ms cubic-bezier(.4, 0, .2, 1),
          background-color 80ms cubic-bezier(.4, 0, .2, 1);
}
 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: calc(100% - .125rem);
     margin: 0 auto;
     border-color: hsl(360, 94%, 20%);
     box-shadow: 0 calc(.0625rem * 5) 0 0 hsl(360, 94%, 20%);
     border-width: 0.1em 0.1em 5px;
}
 .yui-navset .yui-nav a, /* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav a{
     color: hsl(0, 0%, 5%);
     /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: hsl(0, 0%, 99%);
     /* -------- */
     background-image: none;
     border: unset;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
     color: hsl(0, 0%, 96%);
     /* ---- Tab Background Colour | [HOVER] ---- */
     background-color: hsl(360, 100%, 27%);
     /* -------- */
}
 .yui-navset .yui-nav li, /* ---- Listitem Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav li{
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;

     background-color: hsl(0, 0%, 96%);
     border-color: transparent;
     box-shadow: 0 0 0 .0625rem hsl(360, 94%, 20%);
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav li em{
     border: unset;
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;

     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
 .yui-navset .yui-nav .selected, /* ---- Selection Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav .selected{
     flex-grow: 2;
     margin: 0;
     padding: 0;

     /* ---- Tab Background Colour | [SELECTED] ---- */
     background-color: hsl(360, 94%, 20%);
     /* -------- */
}
 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a em{
     border: hsl(360, 94%, 20%);
}
 .yui-navset .yui-nav .selected a{
     width: 100%;

     color: hsl(0, 0%, 96%) !important;
     background-image: none;
}
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:active{
     color: hsl(0, 0%, 96%);
     background-color: hsl(360, 94%, 20%);
}
 .yui-navset .yui-nav .selected a:hover{
     cursor: default;
}
 .yui-navset-left .yui-content{
     /* ---- Content Background ---- */
     background-color: hsl(0, 0%, 99%);
     /* -------- */
}
 .yui-navset .yui-content,
 .yui-navset .yui-navset-top .yui-content{
     padding: .5em;
     border-color: hsl(0, 0%, 60%);
}
.backg {
  background-color: whitesmoke;
  padding: 5px 10px 10px 10px;
  border-radius: 10px;
  border: 1px solid thistle;
}
[[/module]]
