[[include theme:flopstyle]]
[[module CSS]]
/* SITE HEADER */
 #page-content span,
 #page-content a{
     font-weight: bold;
}
 #header h1 a,
 #header h2 span{
     color: transparent;
     text-shadow: none;
}
 #header h1 a::before{
     content: "Птичья команда";
     font-family: "Verdana", sans-serif;
}
 #header h2 span::before{
     content: "Пока мы возвращаемся в Гнездо";
}

/* DIV BLOCKS */
 .header-image{
     margin: .5em 0;
     background: hsl(0, 0%, 100%);
     border: 2px solid hsl(0, 0%, 5%);
     font-size: 80%;
     font-weight: bold;
     text-align: center;
}
 .showcase{
     padding: 0 .5em;
     background: hsl(0, 0%, 100%);
     border: 2px solid hsl(0, 0%, 5%);
}
 .showcase hr{
     border-top: 2px solid hsl(45, 100%, 55%);
}
 .scm{
    margin-bottom: 4%;
}

/* FORMATTING */
 .bg-gradiant{
     margin: 0 -1.7em;
     padding: 0 1.7em;

     background:
       linear-gradient(to bottom,
               hsl(0, 0%, 97%),
            hsl(45, 100%, 55%),
            hsl(45, 100%, 55%),
             hsl(0, 0%, 100%));
}

/* TABLES */
 blockquote,
 .article blockquote,
 .continued blockquote{
     padding: 0;
     background: hsl(0, 0%, 100%);
     border: 1px solid hsl(45, 100%, 55%);
}
 .article table.wiki-content-table,
 .continued table.wiki-content-table{
     width: 100%;
     margin: 0 0 -1px;
}
 .article table.wiki-content-table th,
 .continued table.wiki-content-table th{
     padding: 0;
     border: 1px solid hsl(45, 100%, 55%);
     font-weight: normal;
}
 .article table.wiki-content-table td,
 .continued table.wiki-content-table td{
     border: 1px solid hsl(45, 100%, 55%);
     border-right: 0;
}
 .continued table.wiki-content-table td{
     border-top: 0;
}
[[/module]]

