[[include fragment:fix-sidebar-themes]]
[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Martel:wght@900&display=swap');

/* === ENTIRE PAGE AREA === */

body {
    background: #F4F4F4;
}

#container *::selection {
   background: #d77800;
   color: #ffffff;
}

/* === HEADER AREA === */

/* Header Background */

div#container-wrap {
    background-image: url("https://files.scpfoundation.net/local--files/theme:ad-abyssum/tacttheo_banner_4.png");
}

#header {
    background-image: url("https://scpfoundation.net/local--files/theme:ad-abyssum/Department%20of%20Tactical%20Theology%20Inverted%20Transparent.png");
    background-position: center;
    background-size: 23%;
    height: 15rem;
}

/* Header Foreground */

#header h1,
#header h2 {
    float: initial;
    text-align: center;
    margin-left: 0rem;
}

#header h1 a {
    line-height: 8.75rem;
}

#header h2 {
    line-height: 11rem;
}

#header h1 a span,
#header h2 span {
    display: none;
}

#header h1 a::before {
    color: #591900;
    content: "Отдел тактической теологии";
    font-family: 'Martel', serif;
    text-shadow: 0rem 0rem 0.2rem #fff;
}

#header h2::before {
    color: #591900;
    content: "Invenient Novum Numen";
    font-family: 'Martel', serif;
    text-shadow: 0rem 0rem 0.1rem #fff;
}

/* Top Navigation Bar */

#top-bar {
    display: flex;
    justify-content: center;
    top: 15.625rem;
}

#top-bar .top-bar > ul > li > a {
    text-shadow: 0em 0em 0.188em #000000;
}

#top-bar .top-bar > ul > li:hover > a {
    text-shadow: none;
}

#top-bar ul li:hover a {
    color: #952b00;
}

#top-bar ul li:hover a:hover {
    background-color: #f9dec6;
}

/* Login */

#login-status {
    color: #591900;
    left: 0;
    width: 70%;
}

#login-status a {
    color: #591900;
}

#account-options li a:hover {
    color: #700;
}

#account-options {
    position: relative;
    right: -3.75em;
}

/* Search Bar */

#search-top-box {
    top : 1rem;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #591900;
    border-color: #333333;
    border-radius: 0.3125rem;
    color: #ffa77a;
    opacity: 0.8;
    padding: 0.125rem;
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background-color: #591900;
    background-image: none;
    border: none;
    border-radius: 0rem;
    color: #ffffff;
}

/* === SIDEBAR AREA === */

#side-bar {
    left: -8em;
}

#side-bar a,
#side-bar a:visited,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #952b00;
    text-shadow: 0em 0em 0.125em #fff
}

#side-bar .heading {
    color: #591900;
}

.side-block {
    border: 0.0625em solid #591900;
    box-shadow: 0 0.125em 0.375em #591900;
}

/* Media Block */

#side-bar .side-block.media {
    background-color: #fff3ed;
    background-image: url("https://files.scpfoundation.net/local--files/theme:ad-abyssum/tacttheo_sidebar_small_compressed.png");
    background-position-y: -3em;
    background-size: 100%;
}

/* Resources Block */

#side-bar .side-block.resources {
    background-color: #fff3ed;
    background-image: url("https://files.scpfoundation.net/local--files/theme:ad-abyssum/tacttheo_sidebar_small_compressed.png");
    background-position-y: -11.6em;
    background-size: 100%;
}

/* Main Side Blocks */

#side-bar .side-block {
    background-color: #fff3ed;
    background-image: url("https://files.scpfoundation.net/local--files/theme:ad-abyssum/tacttheo_sidebar_compressed.png");
    background-size: 100%;
}

/* Interwiki Block */

#interwiki .side-block {
    background-color: #fff3ed;
    background-image: url("https://files.scpfoundation.net/local--files/theme:ad-abyssum/tacttheo_sidebar_compressed.png");
    background-position: bottom;
    background-size: 100%;
}

#interwiki .side-block .menu-item a {
    color: #952b00;
}

/* === BODY AREA === */

#main-content {
    margin: 0 12em 0 12em;
}

/* Page Title */

#page-title {
    border-color: #591900;
    text-align: center;
    color: #591900;
}

#breadcrumbs {
    text-align: center;
}

/* Rating Module */

.page-rate-widget-box {
    box-shadow: 0em 0em 0em #000000;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box > *,
.rate-box-with-credit-button {
    background-color: #591900 !important;
    border: none !important;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    background: transparent;
    color: #ffffff;
    transition-duration: 0.3s;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover,
.page-rate-widget-box .cancel a:hover {
    background: rgba(255, 255, 255, 0.3);
    transition-duration: 0.3s;
}

/* === PAGE ELEMENTS === */

div.blockquote,
blockquote {
    background-color: #ffc2814f;
    border: 0.063em solid #5919004f;
}

h1 {
    color: #952b00;
}

hr {
    background-color: #591900;
}

a {
    color: #bb2c00;
}

a:visited {
    color: #883a22;
}

table.wiki-content-table th {
    background-color: #ffc2814f;
}

/* Tabbers */

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-color: #952b00;
    background-image: none;
    border: none;
}

.yui-navset .yui-nav a,
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background-color: #591900;
    background-image: none;
    border: none;
    color: #ffffff;
}

.yui-navset .yui-content {
    background-color: #ffc2814f;
    background-image: url("https://files.scpfoundation.net/local--files/theme:ad-abyssum/Department%20of%20Tactical%20Theology%20Inverted%20Transparent.png");
    background-position: center center;
    background-repeat: no-repeat;
    background-size: 75%;
    border: 0.063em solid #5919004f;
    text-shadow: 0px 0px 1px #ffc2814f;
}

.yui-navset .yui-nav {
    align-items: end;
    border: none;
    display: flex;
    justify-content: center;
    padding-bottom: 0.0625rem;
}

.yui-navset .yui-nav a em {
    border: none;
}

/* === MOBILE ADJUSTMENT === */

@media (min-width: 767px) and (max-width: 1581px) {

    #side-bar {
        left: 2em;
    }

    #main-content {
        margin: 0 2em 0 22em;
    }

}

@media (max-width: 900px) {

    #header {
        background-size: 33%;
    }

}

@media (max-width: 767px) {

    #side-bar {
        left: -25em;
    }

    .mobile-top-bar {
        display: flex;
        justify-content: center;
        left: unset;
    }

    #main-content {
        margin: auto;
    }

    #login-status {
        margin-left: 4.125em;
        width: 65%;
    }

}

@media (max-width: 500px) {

    #header {
        background-size: 50%;
    }

}

/* === OTHER === */

/* "Preview" Notice */

.preview-message {
    background-color: #ffead4;
    border-style: solid;
    opacity: 0.95;
}

/* Wikidot Licensing Notice */



#license-area::after {
    content: "Да смилостивится Господь над нашими душами."
}

/* Ayers Info Bar */

.info-container .collapsible-block .collapsible-block-folded,
.info-container .collapsible-block .collapsible-block-unfolded-link,
#u-author_block > p > a {
    --barColour: #591900;
}

[[/module]]
