[[include fragment:fix-sidebar-themes]]

[[module CSS]]

@import url('https://fonts.googleapis.com/css?family=Exo+2:400,800');

/* HEADER */
div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background-image: url('https://scpfoundation.net/local--files/theme:ad-astra/cosmic-cliffs.png');
    background-size: cover;
} /* SPACE */

div#container-wrap {
    background-image: url(none);
}

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background-color: #5c1d24;
    background-image: linear-gradient(to bottom,
      #5C1D24,
      #42151a);
} /* Red-top bar */

#header {
    background: url('https://scpfoundation.net/local--files/theme:ad-astra/logo.svg') 15px 40px no-repeat;
    background-size: 6rem;
}
@media (max-width: 580px) and (min-width: 480px) {
    #header {
        background-position: 0.5em 4.5em;
        background-size: 66px 66px;
    }
}

@media (max-width: 479px) {
    #header {
        background-position: 0 5.5em;
        background-size: 55px 55px;
    }
}

#header h1 a::before {
    content: "AD ASTRA PER ASPERA";
    font-size: 80%;
    font-family: 'Exo 2', sans-serif;
    font-weight: 800;
    text-shadow: 4px 4px 4px rgba(0,0,0,.8);
}

#header h1 a span{
    visibility: hidden;
}

#header h2 span {
    color: transparent;
    text-shadow: 0px 0px 0px;
}

#header h2 span::before {
    content: "Через тернии к звёздам";
    color: #ffc780;
    font-family: 'Exo 2', sans-serif;
    font-weight: 400;
    font-size: 120%;
    text-shadow: 3px 3px 3px rgba(0,0,0,.9);
}

/* Log-In Info */
#login-status {
    color: #ffc780;
}
#login-status a {
    background: transparent;
    color: #ffc780;
}

#login-status ul a {
    color: #ffbe3d;
    background: #101d2e;
    border-color: #ffc780;
}

#login-status ul a:hover {
   color: #ffbe3d;
   font-weight: bold;
}

#account-topbutton {
    background: transparent;
    color: #ffc780;
    border: none;
}

#account-options {
   border-color: #ffc780;
}

/* Search Bar */
#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    border-color: #ffb961;
    background-color: #1b304d;
}

#search-top-box-input {
    color: #ffc780 !important;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    color: #ffb961 !important;
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border-color: #ffb961;
    background: #5C1D24;
    color: #ffb961;
}

/* TITLE */

h1,
#page-title {
    color: #ffb961;
}

#page-title {
    border-color: #ffc780;
}

/* TOP-BAR SETTINGS */
#top-bar a {
    color: #ccc
}

#top-bar ul li ul {
    border: 2px solid #ffb962;
    border-radius: 0px 0px 10px 10px;
    overflow: hidden;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
   border-top-color: #5C1D24;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #5C1D24;
    color: #ffbe3d;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #1b304d; /* top bar hover background color */
    color: #ffbe3d;
}

/* SIDEBAR SETTINGS */
#side-bar .side-block,
#interwiki .side-block {
    border-width: 2px;
    border-color: #ffb961;
    box-shadow: none;
    background-color: #1b304d;
    color: #ffc780;
}

#side-bar .side-block.media {
    background-color: #26152e;
}

#side-bar .side-block.resources {
    background-color: #5C1D24;
}

#interwiki .side-block {
    background: #333;
}

#interwiki body {
    background-image: none;
}    

#side-bar .heading,
#interwiki .heading {
    border-bottom-width: 2px;
    border-bottom-color: #ffb961;
    color: #ffb961;
}

#interwiki .menu-item img {
    filter: hue-rotate(60deg) brightness(300%);
}


 #side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 2px #ffb961;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #ffb961;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #ffbe3d;
}


/* Mobile Sidebar */
@media (max-width: 767px) {
    #side-bar {
        background-color: #101d2e;
    }

    #top-bar .open-menu a {
        border-color: #ffb961;
        background-color: #101d2e;
        color: #ffb961;
    }
}

/* BODY */
body {
    background-color: #101d2e;
    background-image: linear-gradient(
        #060b11,
        #101d2e 400px);
    color: #ccc;
}

/* FOOTER */
#footer {
    background: #5C1D24;
    color: #ccc:
}

#footer a {
    color: #ffbe3d;
}

#license-area {
     color: #ccc;
}

/* OTHER STRUCTURAL ELEMENTS */

/* Links */
a,
a.newpage,
a:visited,
#side-bar a:visited,
#interwiki a:visited {
    color: #ffbe3d;
}

/* Selection */
::selection {
    background: #ffb961;
    color: #fff;
}

/* Footnotes */
.hovertip {
     background-color: #1b304d !important;
     border-color: #ffb961 !important;
}

.footnotes-footer .title {
    color: #ffb961;
}

/* Scrollbar */
::-webkit-scrollbar-thumb {
    background: #ffb961;
}

/* Pop-Up Windows */
.owindow {
    background-color: #1b304d;
    border-color: #ffb961;
}

.owindow .modal-header {
    background-color: #1b304d;
}

.owindow .modal-body img {
    background-color: transparent !important;
}

.owindow .title {
    background-color: #5C1D24;
    color: #ffb961;
    border-bottom: 1px solid #ffb961;
}

.owindow .button-bar a {
    background-color: #1b304d;
    border-color: #808080;
}

.owindow .button-bar a:hover {
    background-color: #26152e;
}

/* Edit Buttons */
.buttons .btn {
    background-color: #1b304d;
    border-color: #ffb961;
    color: #ffb961;
    padding: 3px 5px;
}

.buttons .btn:hover {
    background-color: #26152e;
    color: #ffb961;
}

/* Edit Lock Info*/
#lock-info {
    background-color: #1b304d;
    border-color: #ffb961;
}

/* Close Button for Page Source, Rating, Etc */
a.action-area-close:hover {
    background-color: #5c1d24;
}

/* Page-History Current */
.pager .current {
    background-color: #1b304d;
    border-color: #ffb961;
}

/* Code */
.code {
    border-color: #666;
    background-color: transparent;
    font-size: 14px;
}

/* code highlighter stuff */
.hl-identifier,
.hl-code,
.hl-brackets {
    color: #ccc;
}

.hl-var {
    color: #21ff00;
}

.hl-special,
.hl-quotes {
    color: #ffff00;
}

.hl-reserved {
    color: #00b2ff;
}

.hl-number,
.hl-string {
    color: #ff0020;
}

/* Page Revisions Readability */
del,
ins {
    color: #333;
}

/* RATING AND INFO MODULES */
.page-rate-widget-box {
     box-shadow: 0px 0px 0px 2px #ffb961;
     background-color: #5C1D24;
     padding: .1em;
}

div.page-rate-widget-box .rate-points {
    background-color: #5C1D24;
    border: none;
    color: #ffb961 !important;
    text-transform: capitalize;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #5C1D24;
    border-top: none;
    border-bottom: none;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #ffb961;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #ffb961;
    color: #5C1D24;
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #5C1D24;
    border: none;
}
.page-rate-widget-box .cancel a {
    color: #ffb961;
}
.page-rate-widget-box .cancel a:hover {
    background: #ffb961;
    color: #5C1D24;
}

/* Credit Module */
#page-content .rate-box-with-credit-button {
    background-color: #5C1D24;
    border-color: #5C1D24;
    border-radius: 5px;
    box-shadow: 0px 0px 0px 2px #ffb961;
}
#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}

#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
#page-content .rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
}

.rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}

#page-content .rate-box-with-credit-button .fa-info {
     color: #ffb961 ;
}
#page-content .rate-box-with-credit-button .fa-info:hover {
     color: white;
}

#page-content .modalbox {
     background: #101d2e !important;
     color: #ccc;
     box-shadow: 0px 0px 0px 2px #ffb961;
}

.close-credits,
.credit-back {
    filter: hue-rotate(60deg) brightness(400%);
}

/* Ayer's Info Module */
:root {
    --barColour: #5C1D24;
    --linkColour: #ffb961;
}

.info-container .collapsible-block-unfolded {
    border-color: #ffb961;
}

/* PAGE ELEMENTS */
blockquote,
div.blockquote {
    margin: 1em 0;
    border: 3px solid #ccc;
    border-radius: 10px;
    background-color: #21212d;
}

.blue blockquote,
.blockquote.blue {
     background-color: #1b304d;
}

.pink blockquote,
.blockquote.pink {
     background-color: #5c1d24;
}

.gold blockquote,
.blockquote.gold {
      background-color: #442f03;
}

.purp blockquote,
.blockquote.purp {
    background-color: #26152e;
}

.gray blockquote,
.blockquote.gray {
    background-color: #333;
}

.scp-image-block .scp-image-caption {
    background-color: #1b304d;
}

.wiki-content-table tr th {
     background: #1b304d;
     color: #ffb961;
     border-color: #ffb961;
}

span.h-span {
    color: #ffb961;
    font-weight: bold;
    word-break: unset;
}

/* TABVIEW - Abandon All Hope Ye Who Enter Here */

.yui-navset * {
    -webkit-transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1), color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
    -o-transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1), color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
    transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1), color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    zoom: unset;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    border-color: #ffb961;
    width: -webkit-calc(100% - 0.125rem);
    width: calc(100% - 0.125rem);
    margin: 0 auto;
    border: 0;
    -webkit-box-shadow: 0 -webkit-calc(0.0625rem * 5) 0 0 #ffb961;
    box-shadow: 0 calc(0.0625rem * 5) 0 0 #ffb961;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    zoom: unset;
    background-image: none;
    background-color: #26152e;
    border: unset;
    color: #ffb961;
    font-weight: bold;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    zoom: unset;
    background-image: none;
    background: #5C1D24;
    font-weight: bold;
}

.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
    background: rgb(244, 244, 244);
    border-color: transparent;
    max-width: 100%;
    padding: 0;
    margin: 0;
    zoom: unset;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    position: relative;
    -webkit-flex-basis: 7vw;
    -ms-flex-preferred-size: 7vw;
    flex-basis: 7vw;
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
    -moz-box-flex: 2;
    -ms-flex-positive: 2;
    flex-grow: 2;
    -webkit-box-shadow: 0 0 0 0.0625rem #ffb961;
    box-shadow: 0 0 0 0.0625rem #ffb961;
}

.yui-navset .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset-bottom .yui-nav li a {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    vertical-align: bottom;
    zoom: unset;
    width: 100%;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -moz-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -moz-box-pack: center;
    -ms-flex-pack: center;
    justify-content: center;
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
    padding: 0.35em 0.75em;
    white-space: nowrap;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    overflow: hidden;
}

.yui-navset .yui-nav li em {
    /* Undoes border set by wikidot's default layout */
    border: unset !important;
}

/* Selected Tab */

.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected,
.yui-navset .yui-nav .selected:hover,
.yui-navset .yui-navset-top .yui-nav .selected:hover {
    padding: 0;
    margin: 0;
    -webkit-box-flex: 2;
    -webkit-flex-grow: 2;
    -moz-box-flex: 2;
    -ms-flex-positive: 2;
    flex-grow: 2;
    color: #ffb961;
    background-image: none;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border: none !important;
    border-color: transparent;
    color: #ffb961;
}

.yui-navset .yui-nav .selected a {
    /* Undoes a color set by wikidot's default layout */
    color: rgb(252, 252, 252) !important;
    width: 100%;
    background-image: none;
}

.yui-navset .yui-content {
    background-color: #1b304d;
    /* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: #ffb961;
    /* content border */
}

.yui-navset-left .yui-content {
    border-left-color: #ffb961;
    /* different border color */
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
        background-image: none;
        background-color: #1b304d;
        color: #ffb961;
        cursor: default;
        margin-bottom: -2px;
        font-weight: bold;
}

[[/module]]
