[[include fragment:fix-sidebar-themes]]

[[module CSS]]
@import url("https://scpfoundation.net/local--files/theme:black-highlighter-theme/normalize.min.css");
@import url("https://scpfoundation.net/local--files/theme:black-highlighter-theme/black-highlighter.min.css");

/* Замена моноширного шрифта на поддерживающий кириллицу */
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+Mono:wght@100..900&display=swap');

:root {
    --mono-font: 'Noto Sans Mono', Recursive, Consolas, monaco, monospace;
}
/**/

a[rel="license"]::after {
    content: ".";

#content-wrap {
    max-width: 1240px;
}
}

#who-rated-page-area span.printuser a {
  font-size: 13px;
}

#who-rated-page-area br {
  display: unset !important;
}

#who-rated-page-area span.printuser {
  text-align: left;
  width: max-content;
  padding-left: 0;
}

#action-area {
  text-align: left;
  padding: 0;
}

span.printuser {
  display: inline;
}

/* ====== Чето из этого работает, плз не трогать, а не то руки откушу! - еноты ====== */

:root:lang(ru) {
   --body-width-on-desktop: 938px !important;
}

:root {
   --body-width-on-desktop: 938px !important;
}

table.form > tbody {
  display: inherit !important;
}

#history-form-1 > table > tbody > tr:first-child > td:nth-of-type(2) input {
  margin-left: 0%;
}

#history-form-1 > table > tbody > tr:first-child > td:nth-of-type(2) {
  display: inherit !important;
}

.interwiki {
  border: none !important;
}

#header div.open-menu > p > a {
  font-size: xx-large !important;
}

#header div[class*="top-bar"] > ul {
  flex-wrap: wrap !important;
}

#edit-page-form>.form>tbody>tr {
  min-width: max-content;
  grid-area: title-box;
}

#edit-page-comments {
  min-width: 90%;
  background-color: #f4f1f1;
}

#source-code>textarea {
  text-align: left;
}

.w-stars-rate-popularity {
  font-weight: bold;
}

.w-stars-rate-cancel {
  font-size: .8em;
  line-height: initial;
}

.diff-box>table {
  display: flex;
  flex-wrap: wrap;
  max-width: 1240px;
}

.diff-box>table tbody {
  overflow: hidden;
  width: inherit;
  word-break: break-all;
  text-align: left;
}

@media (max-width: 479px) {
.w-stars-rate-cancel {
  font-size: .9em;
  line-height: normal;
}
}

/* ====== Чето из этого работает, плз не трогать, а не то руки откушу! - еноты ====== */

:root {
  --logo-image: url(https://scpfoundation.net/local--files/theme:black-highlighter-theme/logo.svg);
}

#top-bar ul li a {
  max-height: none;
}
#side-bar hr {
  height: auto;
}
#side-bar div.collapsible-block a.collapsible-block-link::after {
  display: none;
}

/* === Фикс mx === */

@media only screen and (max-width: 56.25rem) {
  :where(:lang(ru)) #header div[class*=top-bar]>ul>li>a {
    white-space: nowrap;
  }
  #header div[class*=top-bar]>ul>li>ul {
    top: var(--header-height-on-mobile);
  }
}

:where(:lang(ru)) #page-options-container div[id*=page-options-bottom-2]:not(:is(.page-rate-widget-box,#search-top-box-form))>a#child-page-button {
    --button-icon: var(--append-mask);
    line-height: 0.7;
}

:where(:lang(ru)) #page-options-container div[id*=page-options-bottom-2]:not(:is(.page-rate-widget-box,#search-top-box-form))>a.btn {
    height: 1.5em;
    text-align: center;
}

div#page-info {
    margin-right: 0;
}

:is(form#edit-page-form,form#edit-post-form,form#new-post-form,form#new-thread-form) .editor-area > #edit-page-comments {
    background-color: rgba(var(--swatch-primary-darker),.05);
}
:is(form#edit-page-form,form#edit-post-form,form#new-post-form,form#new-thread-form) .editor-area > #edit-page-comments:focus {
    background-color: rgba(var(--swatch-primary-darker),0);
}
:is(form#edit-page-form,form#edit-post-form,form#new-post-form,form#new-thread-form) .editor-area > p {
    margin: 1em;
    margin-bottom: 0.3rem;
}
:is(form#edit-page-form,form#edit-post-form,form#new-post-form,form#new-thread-form) .wd-editor-toolbar-panel {
    display: none;
}

.owindow {
    background-color: rgb(var(--modal-bg));
    box-shadow: inset 0 0 0 0.125rem rgba(var(--swatch-alternate-color),.25),0.5rem 0.25rem 1rem 0 rgba(var(--swatch-alternate-color),.25);
    color: rgb(var(--modal-body-text));
    border: none;
    display: grid;
}
.owindow .content {
    margin: 0 1rem;
    padding: 0.125rem;
    display: grid;
}
.owindow .button-bar {
    box-shadow: 0 -0.0625rem 0 0 rgba(var(--modal-header-stripe),.2);
    width: 100%;
    margin: 0 0 1em;
    padding: 1rem 0 0 0;
    gap: 0.5em;
}

:root {
    --text-tags: "ТЕГИ↴";
    --text-editor-desc: "Краткое Описание Изменений:";
    --text-pagehistory-rev: "рев.";
    --text-pagehistory-flags: "флаги";
    --text-pagehistory-actions: "действия";
    --text-pagehistory-by: "от";
    --text-pagehistory-date: "дата";
    --text-sitetools: "Инструменты";
    --text-creditview-back: "Назад";
    --text-tableofcontents: "Содержание";
    --text-messages: "Сообщения";
}

@media (max-width: 767px) {
#side-bar:target {
    border: none;
}
}
#side-bar div.collapsible-block div.collapsible-block-unfolded-link {
    border-bottom: 0;
}
#side-bar div.collapsible-block div.collapsible-block-content {
    border-right: 0;
    background-color: rgb(var(--sidebar-resources-bg-color));
    padding-top: 0.25rem;
}
#side-bar #u-side-footer {
    margin: 1rem 0.5rem 0 0.5rem;
}
#side-bar #u-side-footer > h6 {
    margin: 0 0 0.5rem 0;
}
#side-bar #u-side-footer > div {
    margin-top: 0 !important;
}
#side-bar #u-side-footer a,
#side-bar #u-side-footer a:visited {
    color: rgb(var(--link-color));
}
#side-bar #u-side-footer a:hover {
    color: rgb(var(--hover-link-color));
}
#side-bar #u-side-footer > h6 a,
#side-bar #u-side-footer > h6 a:visited {
    color: rgb(var(--sidebar-links-text));
}

.css-1ks9aww-gutter {
    min-width: min-content;
    padding: 0 3px;
}
.css-1ks9aww-gutter pre {
    white-space: nowrap !important;
}
.css-17vezug-marker {
    width: min-content;
    padding: 0 5px;
}
.css-1n5o7vh-diff-container pre, .css-1n5o7vh-diff-container pre * {
    white-space: break-spaces;
}

/* Фиксы из-за того что викидот использует устаревший тег <tt> */
code {
    --calt: 0;
    word-wrap: normal;
    white-space: pre-wrap;
    hyphens: auto;
    overflow-wrap: break-word
}

code {
    --MONO: 1;
    font-family: var(--mono-font);
    font-variation-settings: "wght" var(--mono-wght), "CASL" var(--CASL), "CRSV" var(--CRSV), "MONO" var(--MONO)
}

code * {
    --MONO: 1;
    font-family: var(--mono-font);
    font-variation-settings: "wght" var(--mono-wght), "CASL" var(--CASL), "CRSV" var(--CRSV), "MONO" var(--MONO)
}

code i {
    --slnt: -15
}

code i * {
    --slnt: -15
}

code em {
    --slnt: -15
}

code em * {
    --slnt: -15
}

code .italic {
    --slnt: -15
}

code .italic * {
    --slnt: -15
}

code [style*=italic] {
    --slnt: -15
}

code [style*=italic] * {
    --slnt: -15
}

code [style*=oblique] {
    --slnt: -15
}

code [style*=oblique] * {
    --slnt: -15
}
/* фиксы отображения отступов в ToC */
#toc #toc-list div:not([style*="0em"]) {
    margin-left: 1.25em !important
}

#toc #toc-list div:not([style*="0em"]):after {
    content: " ";
    box-shadow: inset .09375rem 0 0 0 rgb(var(--toc-directory-lines-color));
    pointer-events: none;
    -webkit-border-bottom-left-radius: .0625rem;
    border-bottom-left-radius: .0625rem;
    width: 100%;
    height: calc(50% + .09375rem);
    position: absolute;
    top: 0;
    bottom: auto;
    left: 0;
    right: auto
}

#toc #toc-list div[style*=margin-left]+div:not(:is([style*="0em"], :last-of-type)):after {
    -webkit-border-bottom-left-radius: 0;
    border-bottom-left-radius: 0;
    height: 100%
}

@supports selector(div:has(p)) {
    #toc #toc-list div:has(+div:not([style*="0em"]))+div:not(:is([style*="0em"], :last-of-type)):after {
        -webkit-border-bottom-left-radius: 0;
        border-bottom-left-radius: 0;
        height: 100%
    }
}

#toc #toc-list div[style*="4em"] {
    padding-left: 1.75em !important
}

#toc #toc-list div[style*="4em"]:before {
    content: "╶──"
}

#toc #toc-list div[style*="6em"] {
    padding-left: 2.75em !important
}

#toc #toc-list div[style*="6em"]:before {
    content: "╶───╴"
}

#toc #toc-list div[style*="8em"] {
    padding-left: 3.75em !important
}

#toc #toc-list div[style*="8em"]:before {
    content: "╶─────"
}

#toc #toc-list div:not(:is([style*="0em"], [style*="2em"], [style*="4em"], [style*="6em"], [style*="8em"], [style*="10em"])) {
    padding-left: 4.75em !important
}

#toc #toc-list div:not(:is([style*="0em"], [style*="2em"], [style*="4em"], [style*="6em"], [style*="8em"], [style*="10em"])):before {
    content: "╶──────╴"
}

#toc #toc-list div[style*="10em"] {
    padding-left: 4.75em !important
}

#toc #toc-list div[style*="10em"]:before {
    content: "╶──────╴"
}
/* .pager fix */
:is(#action-area,#main-content) .pager span.pager-no,
:is(#action-area,#main-content) .pager span.pager-no~span:last-of-type:not(.current) {
    grid-column: span 4;
}
/* fix wikiwalk font size */
.footer-wikiwalk-nav {
    font-size: 90% !important;
}
