#page-content div:is(.warning-top-box,.archived-top-box) {
    margin: 0 auto !important;
    border: none !important;
    -webkit-border-radius: 0 !important;
    border-radius: 0 !important;
    width: 100% !important;
    margin-top: 0 !important;
    margin-bottom: 1em !important;
    margin-left: auto !important;
    margin-right: auto !important;
    padding: 0 !important;
    max-width: calc(var(--body-width-on-desktop)/1.5);
}
#page-content div:is(.warning-top-box,.archived-top-box) > div:first-of-type, #page-content div:is(.warning-top-box,.archived-top-box) > p:first-of-type:not(#page-content div:is(.warning-top-box,.archived-top-box) > div + p) {
    --wght: 600;
    background-color: rgb(var(--swatch-primary));
    background-image: linear-gradient(125deg,rgba(var(--swatch-primary-darkest),.75)25%,transparent 25%,transparent 50%,rgba(var(--swatch-primary-darkest),.75)50%,rgba(var(--swatch-primary-darkest),.75)75%,transparent 75%,transparent 100%);
    color: rgb(var(--swatch-text-secondary-color));
    font-weight: var(--wght);
    text-transform: uppercase;
    background-size: 3rem 4.35rem;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    justify-content: flex-start;
    width: 100%;
    margin: 0;
    padding: 0.5em 1.25rem !important;
    display: -webkit-box;
    display: -webkit-flex;
    display: flex;
}
#page-content div:is(.warning-top-box,.archived-top-box)>p {
    width: auto !important;
    margin: 1em 0.5em;
    padding: 0 !important;
}
#page-content div:is(.warning-top-box,.archived-top-box) > .blockquote {
    margin-bottom: 0;
    margin-left: 2ch;
    margin-right: 2ch;
    padding: 1.5ex 2ch;
}
#page-content div:is(.warning-top-box,.archived-top-box) > .blockquote .code {
    padding: 1.5ex 2ch;
}
#page-content div:is(.warning-top-box,.archived-top-box) > p:first-of-type + p:not(#page-content div:is(.warning-top-box,.archived-top-box) > div + p + p) {
    margin-top: 1em;
}
