[[include fragment:fix-sidebar-themes]]
[[module CSS]]
/* Broken Masquerade CSS Theme by Cyantreuse on base code by InitHello with edits by CyrusFiredawn | Revisions by stormbreath | CC-by-SA-3.0 */
 
/* HEADER */
div#container-wrap {
    background-image: url(/local--files/theme%3Abroken-masquerade/background.png);
}

#header {
    background: url(/local--files/theme%3Abroken-masquerade/new_scp_logo.png) no-repeat left 20px;
    background-size: 186px 104px;
}

#header span {
     margin-left: 73px;
}
  
#header h2 span {
    color: transparent;
    text-shadow: none;
    margin-left: 73px;
}
 
#header h2 span::before {
    content: "Обезопасить, Утратив Секретность";
    color: red;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* User-Log In */
#my-account,
#account-topbutton {
    color: white;
}
 
/* Search-Bar */
#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #333 ;
}
 
#search-top-box-input {
    background-color: #2a2a2a;
    color: white;
}
 
#search-top-box-form input[type="submit"],
#search-top-box-form input[type="submit"]:hover {
    background: #2a2a2a;
    color: white;
}

/* TITLES */
h1,
#page-title {
    color: black;
}

/* TOP-BAR */
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #f4f4f4;
}

#top-bar ul li ul a,
#top-bar a:hover {
    background-color: silver;
}

/* BODY */
body {
    background-color: #fff;
    color: #282828;
}

/* SIDE-BAR */

#side-bar .side-block, #u-become-member {
    border: 2px solid gray;
    box-shadow: none;
    background-color: white !important;
}
 
#side-bar .heading {
    border-bottom: 2px solid gray;
    color: gray;
}

iframe.scpnet-interwiki-frame {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

/* LINKS (Throughout) */
a,
a:visited,
a.newpage,
#side-bar a:visited,
#top-bar a,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover {
    color: #d60000;
}

/* RATING AND INFO MODULES */
.page-rate-widget-box .rate-points {
    background-color: black !important;
    border: solid 1px #d60000;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #4a4a4a;
    border-top: solid 1px #d60000;
    border-bottom: solid 1px #d60000;
}
 
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: white;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: black;
    color: white;
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: black;
    border: solid 1px #d60000;
}
.page-rate-widget-box .cancel a {
    color: white;
}
.page-rate-widget-box .cancel a:hover {
    background: #4a4a4a;
    color: white;
}
 
/* INFO MODULE COMPATIBILITY */
#page-content .rate-box-with-credit-button {
    background-color: black ;
    border: 1px solid #d60000 ;
    border-radius: 5px ;
}
#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: #d60000;
}
.rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
}
.rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
}

/* PAGE ELEMENTS (QUOTES, TABS, TABLES) */
blockquote,
div.blockquote {
      margin: 1em 0;
      border: 1px solid #d60000;
      border-radius: 10px
}

.gray1 blockquote,
div.blockquote.gray1 {
    background-color:#f4f4f4;
}

.gray2 blockquote,
div.blockquote.gray2 {
    background-color: #e8e8e8;
}

/* Tabs */
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-color: #444;
    background-image: none;
}
 
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #848484;
    background-image: none;
    color: #efefef;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus,
.yui-navset .yui-navset-top .yui-nav a:hover,
.yui-navset .yui-navset-top .yui-nav a:focus {
    background-image: none;
}
    
table.wiki-content-table th {
    color: #000;
}
 

@media (max-width: 767px) {
    #header {
         background-size: 140px 78px;
         background-position-y: 50px;
    }

     #side-bar {
          background-color: #2a2a2a;
     }
}
[[/module]]
