[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Fira+Sans:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&family=Geologica:wght@100;200;300;400;500;600;700;800;900&display=swap');


/* Шрифты сверху */

/* Тема Коммуникационного форума, 
    дизайн — Kardalak 
    код разметки — ParussMan 
*/

/* Тело и всё самое основное */

html {
  scrollbar-color: #306ab9 #46495b;
}

body {
  background: url('https://scpfoundation.net/local--files/theme:communication-forum/forum-bg.svg');
  background-size: cover;
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-attachment: fixed;
  background-repeat: no-repeat;
background-position-y: 70px;
  font-family: 'Fira Sans', sans-serif;
  background-color: #46495b;
}

div#container-wrap {
  background-image: none !important;
}

#main-content .page-tags {
  color: white;
}

#page-content {
  color: white;
}

.page-options-bottom a:hover {
  background-color: #062e63;
  text-decoration: none;
}

.page-options-bottom {
  margin: 14px 0;
}

.page-options-bottom a {
  margin: 2px 2px;
  border-top: solid 3px #14447b;
  border-bottom: solid 3px #14447b;
  border-radius: 0;
  box-shadow: none;
  background: #082245;
  color: #3995ff !important;
  padding: 5px;
}

#page-content a {
  color: #07f;
}

#page-content a:visited {
  color: #4fa2ff;
}

#license-area {
  color: white;
}

#page-info {
  color: white;
}

#footer {
  background: #082245;
}

#page-title {
  color: #b4b8c6;
  border: none;
  text-shadow: 0 3px 3px black;
  font-family: 'Geologica', sans-serif;
}

#edit-page-form tr {
  color: white;
}

.editor-area p {
  color: white;
}

#action-area {
  color: white;
  background: #46495b99;
}

a:visited {
  color: #90c4ff;
}

blockquote, div.blockquote {
  color: black;
  background-color: white;
  border-left: solid 3px #4a74a4;
  border-top: none;
  border-bottom: none;
  border-right: none;
  padding: 0.5em 1em;
}

blockquote blockquote {
  background-color: #b7bbc9;
}

blockquote div.blockquote {
  background-color: #b7bbc9;
}

div.blockquote div.blockquote, div.blockquote blockquote {
  background-color: #b7bbc9;
}


hr {
  background-color: #4a74a4;
}

table.wiki-content-table th {
  color: black;
}

.rimg,
.limg,
.cimg {
  background: #306ab9;
  font-weight: normal;
  border-top: solid 3px #14447b;
  border-bottom: solid 3px #14447b;
}

.rimg span,
.limg span,
.cimg span {
  color: white;
  border-top: none;
  padding: 4px 0;
}

#page-content .collapsible-block .collapsible-block {
    box-shadow: none;
    background: transparent;
}

#page-content .collapsible-block .collapsible-block:hover {
  box-shadow: none;
}
#page-content .collapsible-block-content .collapsible-block-content {
    margin: 0px;
}
#page-content .collapsible-block .collapsible-block .collapsible-block-folded {
  background: none;
}
#page-content .collapsible-block .collapsible-block .collapsible-block-unfolded {
  background: none;
}

.more-authoer-articles {
  background: #46495b99;
}

.more-author-articles a {
  color: #90c4ff !important;
}

#main-content .page-tags span {
  background: #46495b99;
}

/* Шапка и значения */

#header {
  background: none;
}

#header h1 a,
#header h2 span {
  color: transparent;
  text-shadow: none;
}

#header h1 {
  position: relative;
  bottom: 18px;
}

#header h2 {
  position: relative;
  bottom: 12px;
}

#header h1 a::before {
  content: "Коммуникационный форум";
  color: #9ebdcd;
  text-shadow: none;
  padding-left: 10rem;
  font-family: 'Geologica', sans-serif;
  font-size: 0.7em;
}

#header h2 span::before {
  content: 'SCP Foundation';
  color: white;
  text-shadow: none;
  font-size: 2.8em;
  padding-left: 10rem;
  font-family: 'Geologica', sans-serif;
}

/* Фактическая шапка, серо-белый градиент, синий градиент и логотип */

#extra-div-1 {
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 10.1rem;
  background: white;
}

#extra-div-2 {
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 40%;
  height: 10.1rem;
  background: linear-gradient(90deg, rgba(153, 153, 153, 1) 0%, rgba(255, 255, 255, 1) 100%);
  pointer-events: none;
}

#extra-div-2::after {
  content: "";
  height: 10.1rem;
  background-image: url('https://scpfoundation.net/local--files/theme:communication-forum/header-logo.svg');
  background-size: 60%;
  background-position: right;
  background-repeat: no-repeat;
  display: block;
}

#extra-div-3 {
  display: block;
  position: absolute;
  top: 0;
  right: 0;
  width: 70%;
  height: 10.1rem;
  background: linear-gradient(0deg, rgb(21, 89, 180) 0%, rgb(8, 34, 69) 15%);
  pointer-events: none;
  clip-path: polygon(8rem 0, 100% 0%, 100% 100%, 0 100%);
}

#extra-div-4 {
  display: block;
  position: absolute;
  top: 0;
  right: 0;
  width: 100%;
  height: 10.1rem;
  border-bottom: solid #bebebe 3px;
}

/* Левое меню */

.random-article-block .menu-item:first-child {
  border-bottom: 1px solid #14447b;
}

.collapsible-side .collapsible-block-folded {
  background: linear-gradient(to left, #082245 50%, #3995ff 50%) right;
  background-size: 200% 100%;
}

.collapsible-side .collapsible-block-unfolded-link {
  background: #3995ff;
}

.collapsible-side .collapsible-block-folded::before {
  background-color: #3995ff;
}

.random-article-block .menu-item:first-child:hover {
  background: #03142b !important;
}

.random-article-block .menu-item:last-child:hover {
  background: #03142b !important;
}

.random-article-block > .menu-item a {
  color: #3995ff !important;
}

.new-side-block h6 a {
  color: #a6cffd !important;
}

.new-heading {
  border-top: solid 1px #669bd9;
}

.corner-side::before {
  display: none;
}

#side-bar .side-block,
#interwiki .side-block {
  border-left: none !important;
  border-right: none !important;
  border-top: solid 3px #14447b !important;
  border-bottom: solid 3px #14447b !important;
  border-radius: 0;
  box-shadow: none !important;
  background: #082245 !important;
  color: #3995ff;
}

#side-bar h5 {
  font-family: 'Fira Sans', cursive;
}

#side-bar a:visited,
a {
  color: #3995ff;
}

#side-bar .heading,
#interwiki .heading {
  color: #669bd9 !important;
  border-bottom: solid 2px #4a74a4;
  font-family: 'Geologica', sans-serif;
  text-transform: uppercase;
}

#side-bar div.menu-item img,
#interwiki div.menu-item img {
  display: none;
}

/* Верхнее меню и поиск */

#top-bar ul li ul a,
#top-bar a:hover {
  color: #082245 !important;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
  background: #eee;
  color: #082245 !important;
}

#search-top-box-input {
  background-color: #14447b;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
  background-color: #082245;
}

#search-top-box-form input[type="submit"] {
  background-image: linear-gradient(to top, #082245, #14447b);
}

#search-top-box-form input[type="submit"]:hover,
#search-top-box-form input[type="submit"]:focus {
  background-image: linear-gradient(to top, #082245, #14447b);
}

/* Заголовки */

h1 {
  color: #bebebe;
}


h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'Geologica', sans-serif;
  font-weight: 700;
}

/* Табы/списки */

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
  background: #2562a8;
}

.yui-navset .yui-nav .selected a {
  color: white !important;
}

.yui-navset .yui-content {
  background-color: #b7bbc9;
  color: black;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a {
  background: #082245;
  text-decoration: none;
  color: white !important;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
  border: none;
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
  border: none;
}

/* Мобилки */

@media (max-width: 767px) {
  #extra-div-2::after {
    background-size: 145%;
    background-position: right;
    background-position-y: center;
    background-repeat: no-repeat;
    display: block;
    background-position-y: bottom -45px;
  }

  #extra-div-3 {
    width: 93% !important;
    clip-path: polygon(6rem 0, 100% 0%, 100% 100%, 0 100%);
  }

  #header h2 span::before {
    font-size: 2.6em;
    padding-left: 0;
  }

  #header h2 {
    right: 12px;
    bottom: 0px;
  }

  #header h1 a::before {
    font-size: 0.7em;
    padding-left: 0;
  }

  #header h1 {
    bottom: -12px;
    right: 12px;
  }

  #search-top-box-form input[type="submit"] {
    position: relative;
    top: -10px;
  }

  .page-options-bottom a {
    display: inline-block;
  }
  #side-bar:target {
    border: none;
  }
  #side-bar {
    background-color: #3f4252;
  }
  #page-content .collapsible-block-folded {
    background: url('https://scpfoundation.net/local--files/theme:communication-forum/spoiler-bg-mobile.svg') no-repeat !important;
    background-size: 35% !important;
    background-position-x: right !important;
    background-position-y: bottom !important;
  }
  #page-content .collapsible-block-unfolded {
    background: url('https://scpfoundation.net/local--files/theme:communication-forum/spoiler-bg-mobile.svg') no-repeat !important;
    background-position-x: right !important;
    background-position-y: bottom !important;
    background-size: 35% !important;
}
