.stand {
  padding: 1.3em;
  background: #b7bbc9;
  overflow: hidden;
  min-height: 350px;
  transition: ease-in 0.2s;
  box-sizing: border-box;
  position: relative;
  color: black;
}

.stand:hover {
  box-shadow: 0px 10px 10px black;
}

.stand .stand-bg {
  position: absolute;
  right: -111px;
  opacity: 25%;
  z-index: 0;
  width: 220px;
}

.stand h1 {
  color: #14447b;
  font-size: 1.6em;
}

.stand:hover .stand-bg {
  right: -111px;
  transform: scaleX(-1);
}

.stand-hor {
