  padding: 0.5em 1em;
  margin: 24px;
  border-left: 1px solid #4a74a4;
  position: relative;
}

.lined::before, .lined::after {
  position: absolute;
  left: -4px;
  width: 7px;
  height: 7px;
  border-radius: 100%;
  background: #4a74a4;
  content: '';
}

.lined::before {
  top: -4px;
}

.lined::after {
  bottom: -4px;
