[!-- Оригинал темы: https://scp-sandbox-3.wikidot.com/crystlon-css-theme --]
[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap');

#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: none;
}

a {
    color: #444670 !important;
}

#header span {
    color: #ffffff;
    margin-left: 10px;
}

#my-account, #account-topbutton {
    color: #ffffff !important;
}

h1, #page-title {
    color: #444670 !important;
    font-family: 'Press Start 2P', cursive;;
}

#header h1 a::before {
    content: "SCP Foundation";
    color: #ffffff;
    font-size: 75%;
    font-family: 'Press Start 2P', cursive;;
    text-shadow: 4px 4px 4px #000000;
}

#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}

a.collapsible-block-link {
    color: #444670 !important;
}

#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h2 span {
    display: block;
    margin: 0;
    margin-left: 10px;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: 0px 0px 0px;
}

#header h2 span::before {
    content: "Super Cool Pixels";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #ffffff;
    font-family: 'Press Start 2P', cursive;;
    text-shadow: 4px 4px 4px #000000;
}

#header {
    height: 240x;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: url(https://files.scpfoundation.net/local--files/theme:crystlon-css-theme/crystloniconAGAIN.png) 10px 40px no-repeat !important;
}

div#container-wrap {
    background: url(https://files.scpfoundation.net/local--files/theme:crystlon-css-theme/crystlonheader3.png) top left repeat-x;
}

.page-rate-widget-box {
    border: 1px solid #444670;
    box-shadow: 0px 0px 0px #ffffff;
    border-radius: 3px;
    background-color: #444670 !important;
}

.page-rate-widget-box .rate-points {
    background-color: #444670 !important;
    border: none !important;
}

.page-rate-widget-box .rateup {
    background-color: #444670 !important;
    border: none !important;
}
.page-rate-widget-box .rateup a {
    color: white !important;
}

.page-rate-widget-box .rateup a:hover {
    background-color: #2b2c47 !important;
    color: white !important;
}

.page-rate-widget-box .ratedown {
    background-color: #444670 !important;
    border: none !important;
}
.page-rate-widget-box .ratedown a {
    color: white !important;
}

.page-rate-widget-box .ratedown a:hover {
    background-color: #2b2c47 !important;
    color: white !important;
}

.page-rate-widget-box .cancel {
    background-color: #444670 !important;
    border: none !important;
}

.page-rate-widget-box .cancel a {
    color: white !important;
}

.page-rate-widget-box .cancel a:hover {
    background-color: #2b2c47 !important;
    color: white !important;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: none; /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #2b2c47 !important; /* tab background */
    border-color: #000000;
    color:#ffffff;
}

.yui-navset .yui-nav a em {
    background-color: #2b2c47;
    box-shadow: 0px 1px 0px #444670;
    border-color: #000000;
    color:white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-image: none;
    background-color: #444670; /* selected tab background */
    color:white;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: 000000;
    box-shadow: 0px 0px 0px #ffffff;
    background-color: #444670;
    color:white;
}

.yui-navset .yui-content {
    background-color: #ffffff; /* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: #000000; /* content border */
    border-top-color: #000000; /* different border color */
}

.yui-navset-left .yui-content {
    border-left-color: #000000; /* different border color */
}
#search-top-box-input:hover, #search-top-box-input:focus {
    background-color: #2b2c47 !important;
}

#search-top-box-input {
    background-color: #2b2c47 !important;
    color: white !important;
}

#search-top-box-form input[type="submit"] {
    background: none !important;
    background-color: #444670 !important;
    color: white !important;
}

#side-bar a:visited { color: #2b2c47; }

#side-bar .side-block {
    border: 1px 000000;
    box-shadow: 0px 0px 0px #ffffff;
    background-color: white !important;
}

#side-bar .heading {
    color: #2b2c47;
    border-bottom: solid 1px #000000;
}

#hr {
    color: #000000;
}
[[/module]]
