#side-bar div.menu-item a[href*="/scp-"] {
        width: initial;
        /*padding: 0.125em 0; Закомментировано, потому что ломает отображение некоторых ссылок */
        text-align: initial;
}
#login-status:not(.page-rate-widget-box):not(#search-top-box-form)>a.login-status-create-account,
#login-status:not(.page-rate-widget-box):not(#search-top-box-form)>a.login-status-sign-in {
        color: rgb(var(--swatch-text-tertiary-color));
}

/* Фикс горизонтальных линий */
#main-content hr {
    height: 0.125rem;
    border: 0;
    border-top: 0.0625rem solid transparent;
    background: url(https://scpfoundation.net/local--files/theme:dustjacket/goldfoil.png);
    background-color: rgba(255, 219, 90);
    background-color: rgba(var(--bright-accent));
    background-size: cover;
}

/* === Фикс mx === */

#side-bar .side-block:nth-child(5) {
    margin: 1rem 0.5rem 0 0;
}
#side-bar .side-block:nth-child(5) > div a {
    color: rgb(var(--sidebar-links-text));
}
#side-bar .side-block:nth-child(5) > div a:hover {
    color: rgb(var(--sidebar-links-hover-text-color));
}
:where(:lang(ru)) #side-bar>hr+.side-block > div a {
    color: rgb(var(--sidebar-links-text));
}
:where(:lang(ru)) #side-bar>hr+.side-block > div br {
    display: inherit;
}
[[/module]]
[[/ifcategory]]
