/*
    Highroller Theme
    [2023 Wikidot Theme]
    by ROUNDERHOUSE
*/

@import url('https://fonts.googleapis.com/css2?family=Playfair+Display:ital,wght@0,900;1,900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Ysabeau+Office&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Telex&display=swap');

:root {
    --theme-id: "UNDERVEGAS THEME";
    --theme-name: "UNDERVEGAS THEME";
    --logo-image: url("https://scpfoundation.net/local--files/theme:highroller/finished2.gif");
    --header-title: "ЗОНА-666";
    --header-subtitle: "АНДЕРВЕГАС";
    --header-font: 'Playfair Display', serif;
    --title-font: 'Playfair Display', serif;
    --body-font: 'Ysabeau Office', sans-serif;

    --bright-accent: 255, 215, 0;
    /* golden */
    --medium-accent: 153, 8, 26;
    /* bright red */
    --dark-accent: 69, 0, 9;
    /* dark red*/
    --newpage-color: 99, 190, 255;

    --swatch-text-general: var(--swatch-text-light);
    --swatch-background: var(--black-monochrome);
    --swatch-text-tertiary-color: var(--bright-accent);
    --toggle-button-bg: rgb(var(--white-monochrome));

    --header-height-on-desktop: 13rem;
    --topbar-height-on-desktop: 2.5rem;

    --gradient-topmenu: radial-gradient(circle, rgb(var(--dark-accent)) 81%, rgb(var(--dark-accent)) 100%);
    --gradient-topmenu-mobile: none;

    --footnotes-footer-bg-color: transparent;
    --footnotes-footer-num-color: var(--bright-accent);

    --hoverblock-bg: var(--black-monochrome);

    --link-color: var(--bright-accent);
    --visited-link-color: 255, 88, 108;
    --hover-link-color: var(--swatch-primary);
    --newpage-color: 235, 143, 52;
}

/* SIDEBAR STUFF */

#interwiki, #side-bar {
  --sidebar-bg-color: var(--dark-accent);
  --facebook-icon-color:  255, 215, 0;
  --instagram-icon-color:  255, 215, 0;
  --instagram-icon-gradient: rgb(255, 215, 0);
  --reddit-icon-color:  255, 215, 0;
  --tiktok-icon-color:  255, 215, 0;
  --twitch-icon-color:  255, 215, 0;
  --discord-icon-color:  255, 215, 0;
  --twitter-icon-color: 255, 215, 0;
  --social-icon-hover-color: var(--medium-accent);
  --sideblock-heading-text-color: var(--swatch-primary-darker);
  --sidebar-border-color: var(--bright-accent), 0.08;
  --sidebar-links-text: var(--bright-accent);
  --sidebar-links-hover-bg-color: var(--swatch-primary);
  --sidebar-links-hover-text-color: var(--dark-accent);
  --sideblock-heading-text-color: rgb(var(--bright-accent));
}

#side-bar .close-menu::after {
    background-color: rgb(var(--dark-accent))!important;
    border-radius: 0%;
}

#side-bar .close-menu::before {
    background-color: rgb(var(--bright-accent))
}

/* HEADER LAYERS */

#extra-div-1 {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 13rem;
    background-image: url("https://scpfoundation.net/local--files/theme:highroller/Bellagio_Fountain.jpg");
    background-size: cover;
    background-position: 50% 65%;
    background-repeat: repeat-x;
    pointer-events: none;
}

#extra-div-2 {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 13rem;
    background: linear-gradient(0deg, rgb(74, 0, 0) 0%, rgba(53, 0, 0, 0.91) 50%);
    pointer-events: none;
    opacity: 0.65;
}

#extra-div-3 {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 13rem;
    background-image: url("https://scpfoundation.net/local--files/theme:highroller/dicediag.png");
    background-size: cover;
    background-position: center;
    background-repeat: repeat-x;
    pointer-events: none;
    opacity: 0.8;
}

#header::before {
    left: 0.5rem;
    background-position: center -0.5rem;
    opacity: 1;
}

#header h1, #header h2 {
    margin: 1.7rem 0;
    height: 11rem;
}

/* TOPBAR */

#header div[class*="top-bar"] > ul > li > ul li > a {
    background: rgb(var(--dark-accent));
}

#header div[class*="top-bar"] > ul > li > ul li:is(:hover, :active, :focus-within) > a {
    color: rgb(var(--dark-accent));
}

#header div.mobile-top-bar {
    background: rgb(var(--dark-accent));
}

/* BODY */

#main-content {
     font-size: 120%;
}

#content-wrap {
    background-color: #090000;
    background-image: url("data:image/svg+xml,%3Csvg width='180' height='180' viewBox='0 0 180 180' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M81.28 88H68.413l19.298 19.298L81.28 88zm2.107 0h13.226L90 107.838 83.387 88zm15.334 0h12.866l-19.298 19.298L98.72 88zm-32.927-2.207L73.586 78h32.827l.5.5 7.294 7.293L115.414 87l-24.707 24.707-.707.707L64.586 87l1.207-1.207zm2.62.207L74 80.414 79.586 86H68.414zm16 0L90 80.414 95.586 86H84.414zm16 0L106 80.414 111.586 86h-11.172zm-8-6h11.173L98 85.586 92.414 80zM82 85.586L87.586 80H76.414L82 85.586zM17.414 0L.707 16.707 0 17.414V0h17.414zM4.28 0L0 12.838V0h4.28zm10.306 0L2.288 12.298 6.388 0h8.198zM180 17.414L162.586 0H180v17.414zM165.414 0l12.298 12.298L173.612 0h-8.198zM180 12.838L175.72 0H180v12.838zM0 163h16.413l.5.5 7.294 7.293L25.414 172l-8 8H0v-17zm0 10h6.613l-2.334 7H0v-7zm14.586 7l7-7H8.72l-2.333 7h8.2zM0 165.414L5.586 171H0v-5.586zM10.414 171L16 165.414 21.586 171H10.414zm-8-6h11.172L8 170.586 2.414 165zM180 163h-16.413l-7.794 7.793-1.207 1.207 8 8H180v-17zm-14.586 17l-7-7h12.865l2.333 7h-8.2zM180 173h-6.613l2.334 7H180v-7zm-21.586-2l5.586-5.586 5.586 5.586h-11.172zM180 165.414L174.414 171H180v-5.586zm-8 5.172l5.586-5.586h-11.172l5.586 5.586zM152.933 25.653l1.414 1.414-33.94 33.942-1.416-1.416 33.943-33.94zm1.414 127.28l-1.414 1.414-33.942-33.94 1.416-1.416 33.94 33.943zm-127.28 1.414l-1.414-1.414 33.94-33.942 1.416 1.416-33.943 33.94zm-1.414-127.28l1.414-1.414 33.942 33.94-1.416 1.416-33.94-33.943zM0 85c2.21 0 4 1.79 4 4s-1.79 4-4 4v-8zm180 0c-2.21 0-4 1.79-4 4s1.79 4 4 4v-8zM94 0c0 2.21-1.79 4-4 4s-4-1.79-4-4h8zm0 180c0-2.21-1.79-4-4-4s-4 1.79-4 4h8z' fill='%23ff3800' fill-opacity='0.16' fill-rule='evenodd'/%3E%3C/svg%3E");
}

/* TABS (KILL ME) */

#main-content {
    --tabs-selected-txt: 70, 0, 0;
    }

:is(.yui-navset, .yui-navset-top, .yui-navset-bottom) .yui-nav li a {
    background-color: rgb(var(--dark-accent));
}


:is(.yui-navset, .yui-navset-top, .yui-navset-bottom) .yui-content {
   border: none;
   background-color: #0f0f0f;
   box-shadow: rgba(249, 9, 9, 0.17) 0px -23px 25px 0px inset, rgba(0, 0, 0, 0.15) 0px -36px 30px 0px inset;
}

/* MODAL */

#odialog-container .modal-header, #odialog-container .title {
    background-color: maroon;
    background-image: url("https://scpfoundation.net/local--files/theme:highroller/dicediag.png");
    background-size: cover;
}

#odialog-container div.owindow {
    background-color: #460000;
}

#odialog-container .owindow :not(.page-rate-widget-box):not(#search-top-box-form) > a.btn, #odialog-container .title.modal-header :not(.page-rate-widget-box):not(#search-top-box-form) > a.btn {
   background-color: rgb(var(--bright-accent));
   color: rgb(var(--dark-accent));
   box-shadow: none;
}

#login-status:not(.page-rate-widget-box):not(#search-top-box-form) > a:is(.login-status-create-account, .login-status-sign-in):is(:hover, :active, :focus, :focus-within), #odialog-container .owindow :not(.page-rate-widget-box):not(#search-top-box-form) > a.btn:is(:hover, :active, :focus, :focus-within), #odialog-container .title.modal-header :not(.page-rate-widget-box):not(#search-top-box-form) > a.btn:is(:hover, :active, :focus, :focus-within) {
    color: rgb(var(--medium-accent))
}

[id*="u-credit"] div.modalbox {
    background: #1c0000!important;
}

/* LOOSE DIVS */

.addendum {
  border: solid 2px #460000;
  background: #171717;
}

.dott {
    background-image: url(https://scpfoundation.net/local--files/theme:highroller/Department%20of%20Tactical%20Theology%20White%20Background%20Shiny%20transparent.png);
    border: 5px solid #591b1b;
    width: 90%;
    margin: auto;
    text-align: center;
    padding: 0px 20px;
    text-shadow: 0px 1px 1px #0000003b;
    color: black;
    background-size: 75%;
    background-color: white;
    background-position: center;
    background-repeat: no-repeat;
    background-blend-mode: multiply;
}

:is(blockquote, .blockquote, div.blockquote, [class*="blockquote"]) {
background-color: #0f0f0f;
box-shadow: rgba(249, 9, 9, 0.17) 0px -23px 25px 0px inset, rgba(0, 0, 0, 0.15) 0px -36px 30px 0px inset;
}

:is(div.image-block, div.scp-image-block) *, :is(div.image-block, div.scp-image-block) img.image {
    border-radius: 10px 10px 0px 0px;
}

:is(div.image-block, div.scp-image-block) :is(.image-caption, .scp-image-caption) {
    background-color: #460000;
    color: rgb(var(--bright-accent));
    border-radius: 0px 0px 10px 10px;
}

.scp-image-block img {
  box-shadow: 0px 0px 10px rgba(var(--medium-accent),0.5);
}

#main-content hr, #page-title::after, .meta-title::after, #page-title::before, .meta-title::before {
    background-color: transparent;
    border-top: 0.15rem dotted #b00000;
    border-bottom: none;
}

blockquote hr, .blockquote hr{
    border-top-color: #b00000;
}

#page-content div.b-tables table.wiki-content-table :is(th, td), #page-content table.wiki-content-table :is(th, td) {
    border: .0625rem solid rgba(var(--bright-accent),0.2)!important;
}

.code, .page-source {
    border: none;
    outline: 0.15rem dotted rgba(var(--bright-accent));
    background-color: rgb(var(--dark-accent));
}


/* FOOTNOTES */

:is(.footnotes-footer, .bibitems) .bibitem::after, :is(.footnotes-footer, .bibitems) a[href*="javascript"]::before {
    background-color: transparent;
}

:is(.footnotes-footer, .bibitems) {
background-color: #1717177d;
box-shadow: rgba(249, 9, 9, 0.17) 0px -23px 25px 0px inset, rgba(0, 0, 0, 0.15) 0px -36px 30px 0px inset;
}

:is(.footnotes-footer, .bibitems) .title {
display: block;
margin: auto;
background-color: #6a0000;
font-family: 'Playfair Display', serif;
color: rgb(var(--bright-accent))
}

/* PAGE OPTIONS */

div[id*="page-options-bottom"] > a::after {
    background-color: rgb(var(--bright-accent));
}

div[id*="page-options-bottom"] > a:is(:hover, :active, :focus, :focus-within)::after {
  background-color: rgb(var(--dark-accent));
}

div[id*="page-options-bottom"]:not(.page-rate-widget-box):not(#search-top-box-form) > a.btn:is(:hover, :active, :focus, :focus-within) {
  color: rgb(var(--dark-accent));
}

:not(.page-rate-widget-box):not(#search-top-box-form) > .btn, :not(.page-rate-widget-box):not(#search-top-box-form) > .button, :not(.page-rate-widget-box):not(#search-top-box-form) > .owindow .button-bar a, :not(.page-rate-widget-box):not(#search-top-box-form) > button, :not(.page-rate-widget-box):not(#search-top-box-form) > div.buttons input, :not(.page-rate-widget-box):not(#search-top-box-form) > input.button {
    background-color: #460000;
}

div[id*="page-options-bottom"]:not(.page-rate-widget-box):not(#search-top-box-form) > a.btn {
    box-shadow: none;
}

#action-area a[onclick*="PageRateModule"] {
    background-color: rgb(var(--dark-accent));
}

@supports selector(div:has(p)) {
    #action-area:has(form[onsubmit*=setParent],#who-rated-page-area,form[onsubmit*=PageTagsModule],table.page-files,#edit-meta-addbutton,ul[style="list-style: none"],#page-block-checkbox,#rename-option-rename,#rename-option-delete) p:not(:has(a[id*=backlinks],a[onclick*=showWho],a.btn)):first-of-type {
        background-color: rgba(var(--dark-accent),0.75);
    }
}

form#edit-page-form .wd-editor-toolbar-panel a::before, form#edit-post-form .wd-editor-toolbar-panel a::before, form#new-post-form .wd-editor-toolbar-panel a::before, form#new-thread-form .wd-editor-toolbar-panel a::before {
    background-color: rgb(var(--bright-accent));
}
form#edit-page-form .edit-page-bottomtable tr > td:nth-of-type(1) > div:nth-of-type(1) textarea, form#edit-post-form .edit-page-bottomtable tr > td:nth-of-type(1) > div:nth-of-type(1) textarea, form#new-post-form .edit-page-bottomtable tr > td:nth-of-type(1) > div:nth-of-type(1) textarea, form#new-thread-form .edit-page-bottomtable tr > td:nth-of-type(1) > div:nth-of-type(1) textarea {
    color: white!important;
}

/* MOBILE HEADER */

@media only screen and (max-width: 900px) {
    #extra-div-3, #extra-div-2, #extra-div-1 {
        height:10rem;
    }
    #extra-div-3 {
        background-size: 170%;
     }
}
[[/code]]
