[[module CSS]]

@import url('https://fonts.googleapis.com/css2?family=Open+Sans:wght@300;400;500;600;700;800&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Exo+2:ital,wght@1,400;1,500;1,600&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Josefin+Sans:wght@300;400;500;600;700&display=swap');


/*
    Last Frontier Theme
    [2021 Wikidot Theme]
    Created by Vivarium
    Based on Shattered Thoughts Theme created by Yossipossi.
*/

 body {
    background-color: #150100;
    color: #ffffff;
    font-family: 'Josefin Sans', sans-serif;
}

/* -=- HEADER -=- */
#header h1 a, #header h2 span {
    color: transparent;
    text-shadow: none;
}
#header h1 a::before {
    content: "Проект Бехолдер";
    color: #ffffff;
    font-family: 'Open Sans', sans-serif;
    font-weight: 500;
    font-size: 37.5px;
}

#header h2::before {
    content: "Открывая космос заново.";
    text-shadow: 0rem 0rem 0.3125rem #000000;
    margin-left: 2.5rem;
    font-size: 15px;
    font-style: italic;
    font-family: 'Exo 2', sans-serif;
    font-weight: 600;
}
#header h2 {
    margin-top: 0.5rem;
}
/* -=- HEADER BACKGROUND -=- */

div#container-wrap {
    background: url(https://scpfoundation.net/local--files/theme:last-frontier/Earth.jpg) top left repeat-x;
    background-size: 100%;
}

div#header {
    background-image: url('https://scpfoundation.net/local--files/theme:last-frontier/image.png');
    background-size: 10.5rem;
    background-position: -23px 10px;
}
/* -=- LINKS -=- */

a, a.newpage {
    color: #e3647c;
}

a:visited {
    color: #d2647c;
}

/* -=- BLOCKQUOTE -=- */

div.blockquote,
blockquote {
    background: rgba(135, 0, 9, 0.11);
    border: 0.125rem dotted #865757;
}


/* -=- OWINDOWS -=- */

.owindow, 
.owindow h1, 
.owindow .button-bar a {
    color: #000000;
    text-shadow: none;
}


/* This simply changed the karma bar colors in that user info pop-up menu. No functional purpose besides it looks neat, really. */
.modal-body td img[src*="userkarma.php"] {
    filter: hue-rotate(150deg);
}

.owindow ::selection {
    background: #cfeaff;
}

/* -=- RATING MODULE -=- */

.page-rate-widget-box .rate-points,
.page-rate-widget-box > *,
.rate-box-with-credit-button {
    background-color: rgba(107, 22, 22, 255) !important;
}

/* -=- PAGE TITLE AND H1 -=- */

#page-title,
#action-area h1 {
    color: #ffffff;
    font-weight: 500;
    font-family: 'Open Sans', sans-serif;
}

/* -=- SEARCH BAR -=- */

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #150100;
    color: #ffffff;
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background-color: #61000d;
    color: #ffffff;

}

/* -=- TOP BAR -=- */

#top-bar {
    background: linear-gradient(to right, transparent 25%, transparent 50%, #6b1616 75%, transparent 100%);
}

#top-bar ul li ul {
    border: 0.0625rem solid #2477ac;
}

#top-bar ul li a {
    color: #ffffff;
    text-shadow: 0rem 0rem 0.3125rem #000000;
}

#top-bar ul li:hover a,
#top-bar ul li:hover ul li a {
    background-color: #611e2c;
    color: #ffffff;
}

#top-bar ul li:hover a:hover {
    background: #8c465a;
}

#top-bar .open-menu a {
    background-color: #611e2c;
    border-color: #e3647c;
    color: #ffffff;
}

/* -=- SIDEBAR -=- */

#side-bar .heading {
     color: #ffffff;
}

#side-bar div.menu-item img {
    background: url("http://scp-wiki.wikidot.com/local--files/theme:last-frontier/Diamond.png");
}

#side-bar div.side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources {
    background: #2b1d1d;
    border: 0.3125rem double #000000;
    box-shadow: 0 0.125rem 0.375rem #000000;
    opacity: 0.9;
}

#side-bar a,
#side-bar a:visited {
    color: #ff7a98;
}

/* -=- TABBERS -=- */

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: #8c465a;
}

.yui-navset .yui-nav a,
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #360e0b;
    color: #ffffff;
}

.yui-navset .yui-content {
    background-color: #110500;
    border: 0.125rem dotted #865757;
}

/* -=- USER INFO -=- */

#login-status,
#login-status a {
    color: #e3647c;
}

#account-options li a,
#account-options li a:hover {
    color: #e3647c;
}

#account-options ul {
    background-color: #150100;
    border: solid 0.0625rem #e3647c;
    opacity: 1.0;
}

/* -=- IMAGES -=- */

.scp-image-block .scp-image-caption {
    background-color: #394046;
}

/* -=- TABLES -=- */

table.wiki-content-table td {
    border: 0.0625rem dotted #865757;
}

table.wiki-content-table th {
    background-color: #394046;
}

/* -=- MISCELLANEOUS -=- */

/* Changes the top border of the tags section, which is typically colored #888, to match the theme better. */
.page-tags span {
    border-top: solid 0.0625rem #f15c7d;
}

/* Changes the colors of the layout in the History tab of the page, which constrasts heavily with the background theme. */
.pager a,
.pager a:hover,
.optionstd a,
.optionstd a:hover {
    background: #83303f;
    border: solid 0.0625rem #f15c7d;
}

/* While the page is saving and loading, there is a popup on the screen. This popup was difficult to read due to the body's properties (color being white), so this changed the background to be more thematically appropriate and legible. */
.owait,
.osuccess {
    background-color: #2b1d1d;
    border: solid 0.0625rem #f15c7d;
}

/* -=- FOOTNOTES -=- */

.hovertip {
  background: rgba(17, 0, 5, 255) !important;
  border: solid 0.0625rem #865757 !important;

}

/* -=- MEDIA SELECTORS -=- */

@media screen and (max-width: 50rem) {

    #side-bar {
        background-color: rgba(21, 1, 0, .93);
    }
    div#header {
    background-size: 6rem;
    background-position: 0px 47px;
}
 #header h1 a::before {
    content: "Проект Бехолдер";
    color: #ffffff;
    font-family: 'Open Sans', sans-serif;
    font-weight: 500;
    font-size: 20px;
    margin-left: 1.3rem;
}

#header h2::before {
    content: "Открывая космос заново.";
    text-shadow: 0rem 0rem 0.3125rem #000000;
    margin-left: 2.5rem;
    font-size: 10px;
    font-style: italic;
    font-family: 'Exo 2', sans-serif;
    font-weight: 600;
}
}
.code {
    background-color: #2d0102;
    border: 0.0625rem #865757;
}

.scp-image-block {
    box-shadow: none;
}
.scp-image-block .scp-image-caption {
    background-color: #2d0102;
    color: #ffffff;
}
[[/module]]
