.collapsible-block-folded a:not(#side-bar):not(#side-bar div.collapsible-block-unfolded-link):not(#side-bar div.collapsible-block-folded-link):not(#side-bar div.collapsible-block-unfolded):not(#side-bar div.collapsible-block-folded):not(#side-bar .collapsible-block-folded .collapsible-block-link):not(#side-bar .collapsible-block-link),
.collapsible-block-unfolded-link a:not(.info-container):not(.info-container div.collapsible-block-unfolded-link):not(.info-container div.collapsible-block-folded-link):not(.info-container div.collapsible-block-unfolded):not(.info-container div.collapsible-block-folded):not(.info-container .collapsible-block-folded .collapsible-block-link):not(.info-container .collapsible-block-link):not(#side-bar):not(#side-bar div.collapsible-block-unfolded-link):not(#side-bar div.collapsible-block-folded-link):not(#side-bar div.collapsible-block-unfolded):not(#side-bar div.collapsible-block-folded):not(#side-bar .collapsible-block-folded .collapsible-block-link):not(#side-bar .collapsible-block-link) {
