[[module CSS]]
@charset "utf-8";
 
/*
    RAISA Theme
    [2018 Wikidot Theme]
    Created by Croquembouche (WIP)
    Based on Third Law Canon Theme by GreenWolf, with technical assistance from Randomini and aesthetic assistance from Dr Kens and thomas.
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan.
    The RAISA Logo was created by TSATPWTCOTTTADC using resources from https://opengameart.org/content/flat-folder-icon and is licensed under CC-BY-SA 3.0.
*/


div#container-wrap {
    background: url(http://scpfoundation.net/local--files/theme:raisa/body_bg.png) top left repeat-x;
}

#header {
    background-image: url(http://scpfoundation.net/local--files/theme:raisa/raisa-logo100.png);
}
#header h1 a {
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Verdana', Arial, sans-serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "Архив АПАИБ ";
    color: #333;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px #c8b259;
}
 
#header h2 span {
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: 'Администрация по архивно-информационной безопасности ';
    color: #333;
    text-shadow: 1px 1px 1px #c8b259;
}

#login-status {
    color: #333;
}
#login-status #my-account {
    color: #fff;
}

@media (max-width: 400px) {
  #header h2 span::before {
    content: 'Администрация по архивн.-информ. безопасности ';
    color: #333;
    text-shadow: 1px 1px 1px #c8b259;
  }
}

[[/module]]
