[[include theme:y2k]]

[[module CSS]]

/*
    RETRO AIAD Theme
    [2020 Wikidot Theme]
    By Placeholder McD
    Based on Y2K Theme created by Etoisle 
*/
 
div#container-wrap {
    background-image: url(https://scpfoundation.net/local--files/theme:retro-aiad-theme/retroaiadbg.png);
}
 
div#header {
    background-image: url(https://scpfoundation.net/local--files/theme:retro-aiad-theme/retroaiadlogo.png);
}
 
#header h1 span {
    color: transparent;
    text-shadow: none;
}
 
#header h1 span::before {
    font-family: "Microsoft Sans Serif";
    content: "Департамент ИТ";
    color: #ffffff;
    text-shadow: 3px 3px 0 rgba(0, 0, 0, 0.6);
}
 
#header h2 span::before {
    content: "Отдел по Применению Искусственного Интеллекта";
}
[[/module]]
