[[include fragment:fix-sidebar-themes]]
[[include component:interwiki-style
| priority=4
| theme=https://scpfoundation.net/local--files/theme:retro-gru/retro_gru2.css
]]

[[module CSS]]

@import url('https://fonts.googleapis.com/css2?family=Handjet&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Victor+Mono&display=swap');
@import url('https://fonst.googleapis.com/css2?family=JetBrains+Mono&display=swap');
@import url('https://fonst.googleapis.com/css2?family=Nanum+Gothic+Coding&display=swap');
@import url('https://fonts.cdnfonts.com/css/smw-text-nc'); 
/*
    Ретро-Тема Отдела «П» ГРУ, основанная на теме Techoblast, созданной:
    EstrellaYoshte
    Пробазировано на:
        Simple Yonder Theme от EstrellaYoshte
        Anderson Robotics Theme от Croquembouche
        Flopstyle CSS от Lt Flops
*/

.PUapn .w-message {
  background: rgb(10, 28, 28) !important;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
  font-face: 'Victor Mono', 'Nanum Gothic Coding', 'JetBrains Mono', monospace;
  color: #013737;
  text-decoration: none;
  background-color: #00ffff;
}
#license-area {
  color: #FFF;
}
.owindow {
  background-color: #013737 !important;
  border: solid 3px #d8b305 !important;
  color: #00ffff;
}

.cMDknM .w-upload-files .w-upload-control {
  border: 2px dashed #0ff;
  border-radius: 12px;
  background: #013737;
  box-shadow: none;
}

table.page-files .highlight {
  background-color: #013737;
}

div.buttons input, input.button, button, file, a.button {
  background-color: #013737;
  border: 1px solid #d8b305;
  color: #d8b305;
}

div.buttons input:hover, input.button:hover, button:hover, a.button:hover {
  background-color: #d8b305;
  color: white;
  cursor: pointer;
}

body {
    color: #00ffff;
    font-family: 'Victor Mono', 'Nanum Gothic Coding', 'JetBrains Mono', monospace;
    /* font-size: .74rem; */
    font-weight: 500;
    letter-spacing: 0.3px;
    line-height: 1.4em;
    word-break: unset;
    background-image: url(https://scpfoundation.net/local--files/theme:retro-gru/RetroScreen.png);
}

a {
    color: #d8b305;
}

a:visited {
    color: #d8b305;
}

h1 {
    font-size: 2.6em;
}
h2 {
    font-size: 2.3em; 
}
h3 {
    font-size: 2.1em;
}
h4 {
    font-size: 1.8em;
}
h5 {
    font-size: 1.6em;
}
h6 {
    font-size: 1.5em;
}
h1, h2, h3, h4, h5, h6 {
    color: #00ffff;
    font-family: 'Handjet';
    font-weight: 500;
    line-height: 1.15em;
 }
#account-topbutton{
    border: 0px solid #000;
}

/* ---- PAGE WRAP ---- */
 div#container-wrap{
     background-image: url(/local--files/theme%3Atechnoblast/bg.png);
}

div#header {
    background-image: url(https://files.scpfoundation.net/local--files/theme:retro-gru/pixellogo.png);
    background-size: 12.5%;
}

#page-title {
    font-family: 'Handjet', sans-serif;
    font-size: 2.4em;
    font-weight: 500;
    color: #00ffff;
}

#login-status,
#login-status a {
    font-size: 11px;
    font-family: 'Victor Mono', 'Nanum Gothic Coding', 'JetBrains Mono', monospace;
    font-weight: 600;
    font-size: 10px;
    color: white;
}

#footer {
    background: transparent;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    font-family: 'Victor Mono', 'Nanum Gothic Coding', 'JetBrains Mono', monospace;
    font-size: 11px;
    font-weight: 600;
    border-color: #284040;
    border-radius: 0px;
    background: #284040;
    box-shadow: none;
    color: #f4f4f4;
} 

#search-top-box input.empty {
    color: #bbbbbb;
}

#top-bar a,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover {
    font-family: 'Victor Mono', 'Nanum Gothic Coding', 'JetBrains Mono', monospace;
    font-weight: 600;
    background: #013737;
    color: #d8b305;
}
#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
  border-top: none;
  line-height: 2.1em;
}
#header h1 a, #header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h1 a::before {
    content: 'Отдел «П» ГРУ';
    color: rgb(238, 238, 238);
    text-shadow: 3px 3px 5px #000;
    font-family: 'Handjet', sans-serif;
    font-weight: 500;
    font-size: 2.1rem;
    margin-left: 2rem;
}

#header h2 span::before {
    content: 'Последний рубеж невидимого фронта';
    color: rgb(240, 240, 192);
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    margin-left: 2rem;
    font-family: 'Handjet', sans-serif;
    font-weight: 500;
    font-size: 1.1rem;
}


@media (min-width: 767px) {
     #header {
             background-position: 0em 1.46em;
     }
}

/* ---- SIDE BAR ---- */

 #side-bar .side-block{
     font-family: 'Victor Mono', 'Nanum Gothic Coding', 'JetBrains Mono', monospace;
     font-weight: 600;
     font-size: 10.9px;
     /* width: 12.7rem; */
     border: none;
     border-radius: 3px;
     box-shadow: none;
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png')!important;
     color: #00ffff;
}

/* Модуль щобы покрасить кнопку "Стать участником" */
#u-become-member {
     border: none;
     border-radius: 3px;
     box-shadow: none;
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png')!important;
     color: #00ffff;
}

 #side-bar .heading{
     color: #d8b305;
     border-bottom: solid 2px #d8b305;
}

#side-bar .menu-item > img {
  display: none;
}

#side-bar .menu-item > a:hover {
    background-color: #00ffff;
    color: #013737;
    text-decoration: none;
}

div.open-menu a {
    border-radius: 3px;
    box-shadow: 0px 1px 4px 0 rgba(0, 0, 0, 0.2), 0px 3px 10px 0 rgba(0, 0, 0, 0.19);
    color: #013737!important;
    border: none!important;
}

#side-bar a,
#side-bar a:visited {
    color: #00ffff;
}

#side-bar .collapsible-block-folded{
    background: none;
}

#side-bar .collapsible-block-unfolded-link{
    border-bottom: solid 2px #d8b305;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link{
    color: #d8b305;
}

@media (max-width: 767px) {
      div#header {
              background-image: url(https://cdn.discordapp.com/attachments/1040558369125183488/1040639226313723954                 /pixellogo.png);
              background-size: 15%;
     }
     #header h1 a::before {
              margin-left: -0.2rem;
     }
     #header h2 span::before {
              font-size: 0.5rem;
              margin-left: -0.2rem;
     }
    #main-content {
        padding: 0 /* 0 0 17em */;
        margin: 0 5%;
        border-left: none;
    }
    #page-title {
        margin-top: 0.7em;
    }
    #side-bar {
        background-color: #013737;
        left: -19em;
    }
    #side-bar:target {
        border: none;
        box-shadow: 3px 0 1px -2px rgba(0,0,0,0.04), 1px 0 5px 0 rgba(0,0,0,0.2);
    }
    #side-bar .close-menu {
        transition: width 0.5s ease-in-out 0.1s,
                    opacity 1s ease-in-out 0s;
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        right: 0;
        background: rgba(0,0,0,0.3);
        background-position: 19em 50%;
        z-index: -1;
        opacity: 0;
        pointer-events: none;
    }
    #side-bar:target .close-menu {
        width: calc(100% - 19em);
        right: 0;
        left: auto;
        opacity: 1;
        pointer-events: auto;
    }
    #page-content > hr, #page-content > .list-pages-box > .list-pages-item > hr {
        margin: 3em -5.5%;
    }
    #side-bar {
        top: 0;
    }
    #side-bar .heading {
        padding-left: 1em;
        margin-left: -1em;
    }
    #search-top-box {
        top: 107px;
    }
}

/* ---- TABS ---- */

/* ---- YUI TAB BASE ---- */
.yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a{background-color:inherit;background-image:inherit}.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus{background:inherit;text-decoration:inherit}.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:hover{color:inherit;background:inherit}.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav{border-color:inherit}.yui-navset li{line-height:inherit}

/* ---- YUI TAB CUSTOMIZATION ----*/

 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: calc(100% - .125rem);
     margin: 0 auto;
     border-color: #013737;
     box-shadow: none;
}
 .yui-navset .yui-nav a, /* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav a{
     color: #d8b305;
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: #013737;
     border: unset;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
     color: #f4f4f4;
    /* ---- Tab Background Colour | [HOVER] ---- */
     background-color: #013737;
}
 .yui-navset .yui-nav li, /* ---- Listitem Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav li{
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;
     color: #00ffff;
     background-color: #f4f4f4;
     border-color: transparent;
     box-shadow: none;
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav li em{
     border: unset;
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;

     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
 .yui-navset .yui-nav .selected, /* ---- Selection Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav .selected{
     flex-grow: 2;
     margin: 0;
     padding: 0;
    /* ---- Tab Background Colour | [SELECTED] ---- */
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png');
}
 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a em{
     border: none;
}
 .yui-navset .yui-nav .selected a{
     width: 100%;
     color: #00ffff;
}
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:active{
     color: #00ffff;
     background-color: #013737;
}
 .yui-navset-left .yui-content{
    /* ---- Content Background ---- */
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png');
}
 .yui-navset .yui-content,
 .yui-navset .yui-navset-top .yui-content{
     padding: .5em;
     border: none;
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png')
}

/* ---- TOP MENU ---- */

#top-bar a {
    color: #d8b305;
    font-weight: light;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #013232; /* top bar hover background color */
}
/* ---- INFO BAR ---- */
 body{
     --barColour: #013737;
}

 .info-container .collapsible-block-content{
     padding: 0 .5em 30px;
}
 .info-container .collapsible-block-content .wiki-content-table{
     width: 100%;
}

/* ---- INFO PANE ---- */

#page-content .creditRate{
     margin: unset;
}
#page-content .rate-box-with-credit-button {
    background-color: #013737;
    border: solid 1px #013737;
    box-shadow: none;
    border-radius: 0;
}

#page-content .rate-box-with-credit-button .fa-info {
    border: none;
    color: #ff9944;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #ff9944;
    color: #013737;
}

.rate-box-with-credit-button .cancel {
    border: solid 1px #013737;
}

/* ---- PAGE RATING ---- */

.page-rate-widget-box {
    box-shadow: none;
    margin: unset;
    border-radius: 0;
}

.page-rate-widget-box .rate-points {
    background-color: #013737 !important;
    color: #00ffff !important;
    border: solid 1px #013737;
    border-radius: 0;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #013737;
    border-top: solid 1px #013737;
    border-bottom: solid 1px #013737;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #00ffff;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #00ffff;
    color: #013737;
}

.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #013737;
    border: solid 1px #013737;
    border-radius: 0;
}

.page-rate-widget-box .cancel a {
    color: #00ffff;
}
.page-rate-widget-box .cancel a:hover {
    background: #00ffff;
    color: #013737;
    border-radius: 0;
}

/* ---- PAGE ELEMENTS ---- */

#page-content .wiki-content-table tr th {
    border: solid 1px #013737;
    color: #f4f4f4;
    background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png');
    /* set border for table title */
}

#page-content .wiki-content-table tr td {
    border: solid 1px #013737;
    /* set border for table content */
}

blockquote,
div.blockquote,
#toc,
.code {
     background-color: #013737;
     border-radius: 10px;
     border:dashed 1px #013737;
}

.scp-image-block {
    border: solid 1px #013737;
    box-shadow: none;
}
.scp-image-block .scp-image-caption {
    background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png');
    border-top: solid 1px #013737;
    color: #f4f4f4;
}

/* ---- Horizontal Rules ---- */

hr {
  background-color: rgba(1,55,55,0.46);
}
.hovertip {
    background: #013737 !important;
    border-radius: 2px;
    padding: 5px 5px 5px 5px;
    color: white;
    max-width: 400px;
}

.f-heading {
    text-transform: uppercase;
    font-weight: bold;
    font-size: 80%;
}

.limg span, .cimg span, .rimg span {
    background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url(/local--files/theme%3Atechnoblast/darkbg.png)!important;
} 

/* --- Фиксы для Мобилок --- */

@media (max-width: 767px) {
  #header h2 span::before {
      font-size: 0.8rem !important;
}
}

