.mobile-column {
  display: flex;
  flex-direction: row;
  justify-content: space-around;
}
@media (max-width: 571px) {
  .mobile-column {
    display: inline-block !important;
}
}

.gru {
    border: solid 3px #d8b305 !important;
    padding: 10px !important;
    margin: initial !important;
}
.gru #u-header {
    display: flex;
    font-family: 'Handjet', sans-serif;
    font-size: 1.3em;
    font-weight: 500;
    justify-content: space-between;
}
.gru #u-header > div:first-child {
    display: grid;
    text-align: center;
    width: 40%;
    align-items: center;
}
.gru #u-header > div:first-child img {
    max-width: 300px
}
.gru #u-header > div:last-child {
    display: flex;
    text-align: center;
    width: 40%;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
}
.stamp {
    border: 2px dashed #d8b305;
    color: #d8b305;
    font-weight: normal;
    padding: 0 1em;
    margin: 1em 3em;
}

.gru #u-title {
    text-align: center;
}

.gru #u-content {
    border-radius: 10px;
    background-color: #0a1c1c;
    padding: 1em;
    margin: 1em 3em;
}

.gru hr {
    background-color: #00ffff !important;
}
@media (max-width: 1192px) {
  .experiments-container table {
    display: flex;
  }
  .experiments-container table th {
    display: none;
  }
  .experiments-container table tr:not(:first-child) {
    display: flex;
    flex-direction: column;
    margin-bottom: 16px;
    padding-bottom: 16px;
    border-bottom: 1px solid #00ffff;
  }
  .experiments-container table tr td:nth-child(1)::before {
    color: white;
    font-weight: bold;
    content: 'Номер: ';
  }
