[[include fragment:fix-sidebar-themes]]
[[module CSS]]
@import url(http://fonts.googleapis.com/css?family=Cinzel);
@import url(http://fonts.googleapis.com/css?family=La+Belle+Aurore);
@import url(http://fonts.googleapis.com/css?family=Kristi);

@font-face {
	font-family: 'Bauhaus';
	src: url('../local--files/theme:s5/bauhaus.eot?v#iefix') format('embedded-opentype'), /* IE < 9 */
		url('../local--files/theme:s5/bauhaus.woff2?v') format('woff2'), /* Super Modern Browsers */
		url('../local--files/theme:s5/bauhaus.woff?v') format('woff'), /* Firefox >= 3.6, any other modern browser */
		url('../local--files/theme:s5/bauhaus.ttf?v') format('truetype'), /* Safari, Android, iOS */
		url('../local--files/theme:s5/bauhaus.svg?v#bauhaus') format('svg'); /* Chrome < 4, Legacy iOS */
}

body {
    background-color: #e7e7e7;
    font-size: 0.80em;
    color: #333;
}


/* Rating module */

.page-rate-widget-box .rate-points {
    background-color: #007eb3 !important;
    border: solid 1px #007eb3;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #333333;
    border-top: solid 1px #007eb3;
    border-bottom: solid 1px #007eb3;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: white;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #707070;
    color: white;
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #333333;
    border: solid 1px #007eb3;
}
.page-rate-widget-box .cancel a {
    color: 007eb3;
}
.page-rate-widget-box .cancel a:hover {
    background: #707070;
    color: white;
}
/* Tabs */

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border: 0;
    background: 0;
    padding: 0;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    color: ffffff;
    border-width: 0 0 2px;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
  background: #c4c4ff;
  border: 0;
  color: #007eff;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
  border: 0;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
  background: #a3a3ff;
  color: #1050ff;
  font-weight: bold;
}

/* Collapsibles */

#page-content .collapsible-block {
    position: relative;
    padding: 0.5em;
    margin: 0.5em;
    box-shadow: 0 1px 3px rgba(0,0,0,.2);
    overflow-wrap: break-word;
}

#page-content .collapsible-block-unfolded{
    color: black;
    overflow-wrap: break-word;

}

#page-content .collapsible-block-unfolded-link {
    text-align:center;
}

#page-content .collapsible-block-folded {
    text-align: center;
    color: dimgrey;
}

#page-content .collapsible-block-link {
    font-weight: bold;
    color: black;
    text-align: center;
}
.collapsible-block blockquote {
    position: relative;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.42);
    background-color: rgba(0, 0, 0, 0.42);
    border: 1px solid #ddd;
    color: #fff;
    margin: 2%;
    font-family: 'Roboto Mono', 'Noto Sans TC', 'Noto Sans JP', 'Noto Sans KR', monospace;
    font-weight: normal !important;
}
.collapsible-block blockquote h3 {
    color: #fff;
    font-family: 'Roboto Mono', 'Noto Sans TC', 'Noto Sans JP', 'Noto Sans KR', monospace;
    font-weight: normal !important;
}

/* RIDIA Theme */

.ridia-sfondo{
    background-color: #f8ecc2;
    color: #5a2d0c;
    width: 90%;
    padding-top: 5px;
    padding-right: 30px;
    padding-bottom: 30px;
    padding-left: 30px;
    margin: 0 auto;
}

.ridia-titolo{
    font-family: 'Cinzel';
    font-weight: bold;
    font-size: 26px;
    line-height: 150%;
}

.ridia-documento{
    font-family: 'Cinzel';
    font-weight: bold;
    font-size: 14px;
}

.ridia-testo{
    font-family: 'La Belle Aurore';
    font-weight: normal;
    font-size: 20px;
    line-height: 150%;
}

.ridia-firma{
    font-family: 'Kristi';
    font-size: 42px
}

.centra{
     text-align: center;
}


/* SIDE MENU */
 #side-bar {
     clear: none;
     float: none;
     position: absolute;
     top: 0.5em;
     left: 2em;
     width: 17em;
     padding: 0;
     border: none;
     display: block;
 }
 #side-bar .side-block {
     padding: 10px;
     border: 1px solid #007eb3;
     border-radius: opx;
     box-shadow: 0 2px 6px #007eb3;
     background: #fff;
     margin-bottom: 15px;
 }
 #side-bar .side-area {
     padding: 10px;
 }
 #side-bar .heading {
     color: #000;
     border-bottom: solid 1px #007eb3;
     padding-left: 15px;
     margin-top: 10px;
     margin-bottom: 5px;
     font-size: 8pt;
     font-weight: bold;
 }
 #side-bar p {
     margin: 0;
 }
 #side-bar div.menu-item {
     margin: 2px 0;
 }
 #side-bar div.menu-item img {
     width: 13px;
     height: 13px;
     border: 0;
     margin-right: 2px;
     position: relative;
     bottom: -2px;
     display: none;
 }
 #side-bar div.menu-item a {
     font-weight: bold;
}
 #side-bar div.menu-item.inactive img {
     opacity: 0.25;
 }
 #side-bar div.menu-item.inactive a {
     color: #999;
 }
 #side-bar div.menu-item .sub-text {
     font-size: 80%;
     color: #666;
 }
 #side-bar div.menu-item.sub-item {
 }

 #side-bar .collapsible-block-unfolded-link {
     border-bottom: solid 1px #007eb3;
 }
 #side-bar .collapsible-block-unfolded-link .collapsible-block-link {
     margin-top: 10px;
     margin-bottom: 5px;
     font-size: 8pt;
     color: #007eb3;
 }
 #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
     color: #007eb3;
     text-decoration: none;
 }

 #side-bar ul{
     list-style-type: none;
     padding: 0 5px 0;
 }

/* TOP-BAR SETTINGS */


#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    color: #075eb3;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    color: #075eb3;
}

/* Search bar */


 #search-top-box-input {
     border: solid 1px #007eb3;
     border-radius: 0px;
     color: #007eb3;
     background-color: #ffffff;
     box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
 }
 #search-top-box-input:hover,
 #search-top-box-input:focus {
     border: solid 0px #007eb3;
     color: #007eb3;
     background-color: #eeeeee;
     box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
 }
 #search-top-box-form input[type=submit] {
     border: solid 1px #007eb3;
     border-radius: 0px;
     padding: 2px 5px;
     font-size: 90%;
     font-weight: bold;
     color: white;
     background: linear-gradient(to bottom, #20284e, #3875a1, #007eb3);
     background-color: #007eb3;
     box-shadow: 0 1px 3px rgba(0,0,0,.5);
     cursor: pointer;
 }
 #search-top-box-form input[type=submit]:hover,
 #search-top-box-form input[type=submit]:focus {
     border: solid 1px #007eb3;
     color: #007eb3;
     background: linear-gradient(to bottom, #bbe1fa, #20284e, #2c4a78);
     text-shadow: 0 0 1px rgba(255,255,255,.25);
     background-color: #007eb3;
     box-shadow: 0 1px 3px #007eb3;
 }

div#container-wrap {
     background: url(https://scpfoundation.net/local--files/theme:s5/S5_body_bg.png) top left repeat-x;
     background-size: 100px;
 }

/* Login */

#login-status {
     color: #007eb3;
     font-size: 90%;
     z-index: 30;
 }
 #login-status a {
     background: transparent;
     color: #fff;
 }
 #login-status ul a {
     color: #007eb3;
     background: transparent;
 }

 #account-topbutton {
     background: #ccc;
     color: #007eb3;
 }

 .printuser img.small {
     margin-right: 1px;
 }

/* Header */

#header {
    height: 145px;
    position: left;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    color: #aa10aa;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px -70% 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: BauhausLTDemi. 'Nanum Gothic'. Arial. sans-serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "Совет Надзирателей S5";
    font-size: 3rem;
    color: #fff;
    letter-spacing: 0.02em;
    text-shadow: 3px 3px 5px #000;
    margin-left: 0.5rem;
    font-family: 'Bauhaus';
    font-weight: 1000;
}

@media screen and (max-width: 600px) {
#header h1 a::before {
    content: "Sovrintendenza S5";
    font-size: 1.8rem;
    color: #fff;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px #000;
}
  #header h2 span::before {
    font-size: 21px;
    padding-top: 20px;
  }
}

h1, #page-title {
     font-family: BauhausLTDemi. 'Nanum Gothic'. Arial. sans-serif;
     color: #1050aa;
     padding: 0 0 0.25em;
     margin: 0 0 0.6em;
     font-weight: normal;
     text-align: left;
 }
 h1 {
     margin-top: 0.7em;
     padding: 0;
     font-weight: bold;
 }
 h2, h3, h4, h5, h6, h7 {
     margin: 0.5em 0 0.4em;
     padding: 0;
     letter-spacing: 1px;
 }


#header h2{
    display: none;
}

 #page-title {
     border-color: #000;
 }
 ul {
     /* list-style-image: url(http://d3g0gp89917ko0.cloudfront.net/v--3b8418686296/common--theme/shiny/images/bullet.gif); */
     list-style: square;
 }
 #top-bar ul {
     /* list-style-image: none; /* IE ONLY! IF list-style-image IS SET */
 }

   #top-bar a:hover {
       color: #000000;
}


 li, p {
     line-height: 141%;
 }

 a {
     color: #075eb3;
     text-decoration: none;
     background: transparent;
 }

#side-bar a:visited {
    color: #3040bb;
}

a:visited {
    color: #1626aa;
}

 a.newpage {
     color: #075eb3;
     text-decoration: none;
     background: transparent;
 }
 a:hover {
     text-decoration: underline;
     background-color: transparent;
     color: #075eb3;
 }

 .form-control {
 width: 95%;
 }


 @media (max-width: 479px) {
 #search-top-box-input {
     display: none;
 }

 #page-content {
     font-size: 0.9em;
 }

 #main-content {
     margin: 0;
 }

 #recent-posts-category {
 width: 100%;
 }

 #header, .mobile-top-bar {
     max-width: 90%;
 }

 #side-bar {
     width: 80%;
     position: relative;
 }

 .top-bar {
     display:none;
 }

 .mobile-top-bar {
     display: block;
     padding: 0;
 }

 .page-options-bottom a {
     padding: 0 4px;
 }

 #header h1 a {
     font-size: 100%;
 }

 blockquote {
     margin: 1em 0;
 }

 .license-area {
     font-size: 0.8em;
 }

 #header {
     background-position: 0 5.5em;
     background-size: 55px 55px;
 }

 #header h1, #header h2 {
     margin-left: 66px;
 }

 table.form td, table.form th {
     float: left;
 }

 /*
 td.title {
     width: 30%;
 }
 */

 td.name {
     width: 15em;
 }

 table.form td, table.form th {
     padding: 0;
 }

 #edit-page-title {
     max-width: 90%;
 }

 .content-panel.left-column, .content-panel.right-column  {
 width: 99%;
 float: left;
 }

 #page-content div, #page-content div table {
 clear: both;
 }

 #page-content div.title {
 word-break: keep-all;
 }

 }

 /* Note Media Query */
 @media (min-width: 480px) and (max-width: 580px) {
 #search-top-box-input {
     width: 7em;
 }

 #main-content {
     margin: 0 2em 0 2em;
 }

 #header, .mobile-top-bar {
     max-width: 90%;
 }

 #side-bar {
     width: 80%;
     position: relative;
 }

 .top-bar {
     display:none;
 }

 .mobile-top-bar {
     display: block;
 }

 .page-options-bottom a {
     padding: 0 5px;
 }

 #header h1 a {
     font-size: 120%;
 }

 blockquote {
     margin: 0.5em;
 }

 .license-area {
     font-size: 0.85em;
 }

 #header {
     background-position: 0.5em 4.5em;
     background-size: 66px 66px;
 }

 #header h1, #header h2 {
     margin-left: 80px;
 }

 /*
 td.title {
     width: 30%;
 }
 */

 #page-content div.title {
 word-break: keep-all;
 }

 td.name {
     width: 15em;
 }

 .content-panel.left-column, .content-panel.right-column  {
 width: 99%;
 float: left;
 }

 #page-content div, #page-content div table {
 clear: both;
 }
 }

 /* Mini Tablet Media Query */
 @media (min-width: 581px) and (max-width: 767px) {
 #search-top-box-input {
     width: 8em;
 }

 #side-bar {
     width: 80%;
     position: relative;
 }

 #main-content {
     margin: 0 3em 0 2em;
 }

 #header, .mobile-top-bar {
     max-width: 90%;
 }

 .top-bar {
     display: none;
 }

 .mobile-top-bar {
     display: block;
 }

 .page-options-bottom a {
     padding: 0 6px;
 }

 #header h1 a {
     font-size: 140%;
}

.license-area {
     font-size: 0.9em;
}

#header {
     background-position: 1em 4em;
     background-size: 77px 77px;
}

#header h1, #header h2 {
     margin-left: 93px;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#main-content {
     margin: 0 4em 0 20em;
}

#header, #top-bar #side-bar {
     max-width: 100%;
}

.top-bar li {
     margin: 0;
}

#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    width: 110px;
}

.page-options-bottom a {
    padding: 0 7px;
}

#header h1 a {
    font-size: 160%;
}

.license-area {
    font-size: 0.95em;
}

#header {
    background-position: 1em 4em;
    background-size: 88px 88px;
}

#header h1, #header h2 {
    margin-left: 106px;
}

.content-panel.left-column, .content-panel.right-column  {
width: 99%;
float: left;
}

#page-content div, #page-content div table {
clear: both;
}
}

/* Desktop Media Query -----------
@media (min-width: 980px) and (max-width: 1399px) {

}
------------------------------------------ */

/* Wide Monitor Media Query -----
@media (min-width: 1400px) {

}
------------------------------------------ */

/* off-canvas */

.close-menu {
    display: none;
}

@media (max-width: 767px) {

.page-history tbody tr td:last-child {
width: 35%;
}

.owindow {
    min-width: 80%;
    max-width: 99%;
}

.modal-body .table, .modal-body .table ~ div {
    float: left;
}

.owindow .button-bar {
    float: right;
}

.owindow div .btn-primary {
width: 100%;
float: left;
}

.owindow div .btn-primary ~ div {
width: 100%;
}

.yui-navset {
z-index: 1;
}

#navi-bar, #navi-bar-shadow {
    display: none;
}

.open-menu a {
    position: fixed;
    bottom: 0.5em;
    left: 0.5em;
    z-index: 15;
    font-family: 'Nanum Gothic', san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    border: 0.2em solid #888 !important;
    background-color: #fff !important;
    border-radius: 3em;
    color: #888 !important;
}

  .open-menu a:hover {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -moz-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -ms-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -o-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
}

#main-content {
    max-width: 90%;
    margin: 0 5%;
    padding: 0;
    -webkit-transition: 0.5s ease-in-out 0.1s;
    -moz-transition: 0.5s ease-in-out 0.1s;
    -ms-transition: 0.5s ease-in-out 0.1s;
    -o-transition: 0.5s ease-in-out 0.1s;
    transition: 0.5s ease-in-out 0.1s;
}

#side-bar {
    display: block;
    position: fixed;
    top: 0;
    left: -25em;
    width: 17em;
    height: 100%;
    background-color: rgb(184, 134, 134);
    overflow-y: auto;
    z-index: 10;
    padding: 1em 1em 0 1em;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
}

#side-bar:after {
    content: "";
    position: absolute;
    top: 0;
    width: 0;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);

}

#side-bar:target {
    display: block;
    left: 0;
    width: 17em;
    margin: 0;
    border: 1px solid #dedede;
    z-index: 10;
}

#side-bar:target + #main-content {
    left: 0;
}

#side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgba(0,0,0,0.3) 1px 1px repeat;
    z-index: -1;
}

[[/module]]
