     }

     39% {
     opacity: .8;
     }

     42% {
     opacity: 1;
     }

     60% {
     opacity: 1;
     }

     60.5% {
     opacity: 0;
     }

     62% {
     opacity: 0;
     }

     63% {
     opacity: .2;
     }

     63.25% {
     opacity: 0;
     }

     65% {
     opacity: 1;
     }

     73% {
     opacity: 1;
     }

     75% {
     opacity: .8;
     }

     79% {
     opacity: 1;
     }

     100% {
     opacity: 1;
     }
    }
  /* фикс шрифтов */
  div[id*=page-options-bottom]:not(:is(.page-rate-widget-box, #search-top-box-form)) > a.btn,
  .w-stars-rate-rating {
    font-size: 0.8rem;
  }
}
[[/module]]
