}

body {
    background-color: rgb(var(--swatch-background));
    background-image: var(--space-body-bg);
    background-size: calc(50rem + 20%);
    background-attachment: fixed;
    background-repeat: repeat;
}

#main-content {
    --tabs-bg: var(--white-monochrome);
    --tabs-bottom-border-color: var(--light-gray-monochrome);
    --tabs-hover-bg: var(--tabs-bottom-border-color);
    --tabs-txt: var(--swatch-text-secondary-color);
    --tabs-content-bg-color: var(--swatch-secondary-color), 0.375;
    --tables-border: var(--space-nebula);
    --footnotes-footer-bg-color: var(--pale-gray-monochrome);

    margin: 0 auto;
}

tt, pre { font-size: 0.9125em; }

/*---------------------*/
/*------ HEADER ------*/
/*---------------------*/

/* Due to the way WL's header CSS is set up, this workaround is needed :< */
:root {
    --header-height-on-mobile: calc(23.75rem + 14.75vw);
    --topbar-height-on-mobile: 2.75rem;
    --header-height-on-desktop: var(--header-height-on-mobile);
    --topbar-height-on-desktop: var(--topbar-height-on-mobile);
}

#header::before, #header h1 a::before { content: unset; }
#header h1 a {
    pointer-events: none;
    place-items: center;
}
#header h1 a::after {
    --wght: 400;
    background: unset;
    -webkit-background-clip: unset;
    background-clip: unset;
    background-size: unset;
    -webkit-text-fill-color: unset;
    content: var(--header-title);
    color: rgb(var(--black-monochrome));
    text-shadow: 0 0 .375rem currentColor;

    position: relative;
    height: auto; width: auto;
    pointer-events: auto;
    padding: 0.75rem;
}

div#extra-div-1 {
    background-image: var(--space-header-layer-0);
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
}
/* fuck u safari */
@supports (-webkit-hyphens:none) {
div#extra-div-1 {
     background-attachment: scroll;
}
}

#extra-div-1::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background-color: rgb(var(--medium-accent), .15);
}
#extra-div-1 > span {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: var(--space-header-layer-1);
    background-position: center bottom;
    background-size: cover;
    background-repeat: no-repeat;
}
#extra-div-1::after {
    content: "";
    position: absolute;
    top: 100%;
    left: 0;
    height: 48rem;
    width: 100%;
    background: linear-gradient(to bottom, rgb(var(--dark-gray-monochrome)) 5%, rgb(var(--dark-gray-monochrome), 0));
}

div#extrac-div-1 { z-index: initial; }
#extrac-div-1::before {
    content: "";
    position: absolute;
    top: 0; left: 0;
    width: 100%; height: calc(var(--header-height-on-desktop)*.5 - .5rem);
    background-image: var(--logo-image);
    background-repeat: no-repeat;
    background-position: center center;
    background-size: auto 82.5%;
    mix-blend-mode: overlay;
}
div#extrac-div-1 span { z-index: 2; }

/*----------------------------*/

#header #login-status a.btn:is(.login-status-create-account,.login-status-sign-in) {
    color: rgb(var(--swatch-text-secondary-color));
}

#search-top-box { box-shadow: none; }

#login-status #account-topbutton::before {
    clip-path: unset;
    box-sizing: border-box;
    background-color: rgb(var(--space-planet));
    -webkit-mask-image: var(--space-star-mask);
    -webkit-mask-size: contain;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-image: var(--space-star-mask);
    mask-size: contain;
    mask-position: center;
    mask-repeat: no-repeat;
    transform: scale(0.75);
}

#login-status #account-options {
    background-image: unset;
}

#top-bar {
    --dropdown-bg-color: var(--swatch-menubg-light-color), 0.875;
}

/*---------------------*/
/*------ SIDE BAR ------*/
/*---------------------*/

#side-bar {
    box-sizing: border-box;
}

#side-bar .close-menu::before,
#side-bar .close-menu::after {
    width: var(--topbar-height-on-mobile);
    height: var(--topbar-height-on-mobile);
    top: -.15rem; left: -.15rem;
    border-radius: 0 0 0.75rem 0;
    border: none;
}

:is(#interwiki, #side-bar) .heading {
    color: rgb(var(--swatch-primary-darker));
}


/*---------------------*/
/*------ MAIN ------*/
/*---------------------*/

h1,h2,h3,h4,h5,h6 { color: rgb(var(--swatch-primary-darker)); }
:where(#main-content) :is(h1,h2,h3)::before {
    content: "";
    display: inline-block;
    position: relative;
    margin-inline-end: 0.325rem;
    background-color: currentColor;
    height: 0.95em;
    width: 1.05rem;
    top: 0.15em;
    -webkit-mask-image: var(--space-star-mask);
    -webkit-mask-size: contain;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-image: var(--space-star-mask);
    mask-size: contain;
    mask-position: center;
    mask-repeat: no-repeat;
}

#page-title, .meta-title {
    color: rgb(var(--swatch-primary));
}
#page-title::before,#page-title::after,
.meta-title::before, .meta-title::after {
    background-image: unset;
    background-color: currentColor;
}

.code, .page-source {
    border: none;
}

/*--- Page Rate ---*/

#page-content div.rate-box-with-credit-button, div.page-rate-widget-box {
    background: unset;
    background-color: rgb(var(--rating-module-bg-color));
    border: none;
}

/*--- Blockquote ---*/
:is(blockquote,.blockquote,div.blockquote,[class*=blockquote]) {
    box-shadow: none;
}
:where(blockquote,div.blockquote)::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    box-sizing: border-box;
    width: 100%;
    height: 100%;
    border: solid 0.1rem rgb(var(--swatch-primary-darker));
    clip-path: polygon(100% 0, 100% 100%, 0 100%, 0 1.425rem, 1.125rem 0);
    pointer-events: none;
}
:where(blockquote, div.blockquote)::after {
    content: "";
    position: absolute;
    top: 0; left: 0;
    box-sizing: border-box;
    width: 2rem;
    height: 2rem;
    background-color: rgb(var(--swatch-primary-darker));
    -webkit-mask-image: var(--space-star-mask);
    -webkit-mask-size: contain;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-image: var(--space-star-mask);
    mask-size: contain;
    mask-position: center;
    mask-repeat: no-repeat;
    transform: translate(-50%, -42.5%);
}

/*--- HR ---*/
#main-content hr {
    --hr-icon-mask: var(--space-constellation-mask);
    --hr-icon-size: 3.75rem;
    margin: .5rem 0;
    background: transparent;
    height: var(--hr-icon-size);
    display: grid;
    place-items: center;
    grid-template-columns: 1fr;
    grid-template-rows: 1fr;
}
hr::before, hr::after {
    content: "";
    display: grid;
    grid-column: 1/2;
    grid-row: 1/2;
    background-color: rgb(var(--swatch-primary));
}
hr::before {
    height: 0.05rem;
    width: 100%;
    -webkit-mask-image: linear-gradient(90deg, black calc(50% - var(--hr-icon-size)/2 - 0.125rem), transparent calc(50% - var(--hr-icon-size)/2 - 0.125rem), transparent calc(50% + var(--hr-icon-size)/2 + 0.125rem), black calc(50% + var(--hr-icon-size)/2 + 0.125rem));
