}
#main-content .fancyhr hr {
    height: var(--hr-icon-size);
    border: unset;
}
:where(#main-content) .fancyhr hr::before {
    height: calc(var(--hr-icon-size)/2.225);
    -webkit-mask-image: var(--fancy-hr-mask);
    -webkit-mask-repeat: repeat-x;
    -webkit-mask-size: auto;
    -webkit-mask-position: center;
    mask-image: var(--fancy-hr-mask);
    mask-repeat: repeat-x;
    mask-size: auto;
    mask-position: center;
}
:where(#main-content) .fancyhr hr::after {
    content: unset;
}

/*--- Image Block ---*/
.wl-image-block {
    position: relative;
    background-color: rgb(var(--swatch-alternate-color),0.25);
    -webkit-backdrop-filter: blur(0.125rem);
    backdrop-filter: blur(0.125rem);
    box-shadow: none;
    padding: 0.5rem;
    box-sizing: border-box;
}
.wl-image-block::before {
    content: "";
    position: absolute;
    top: 0; left: 0;
    width: 100%; height: 100%;
    border: solid 0.1rem rgb(var(--swatch-primary-darker));
    box-sizing: border-box;
    pointer-events: none;
    clip-path: polygon(0 0, 100% 0, 100% calc(100% - 1.325rem), calc(100% - 1.325rem) 100%, 0 100%);
}
.wl-image-block::after {
    content: "";
    position: absolute;
    bottom: 0; right: 0;
    width: 2rem; height: 2rem;
    background-color: rgb(var(--swatch-primary-darker));
    -webkit-mask-image: var(--space-orbit-mask);
    -webkit-mask-size: contain;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    mask-image: var(--space-orbit-mask);
    mask-size: contain;
    mask-position: center;
    mask-repeat: no-repeat;
    transform: translate(46.5%, 46.5%);
}
.wl-image-block .wl-image-caption {
    padding-top: 0.675em;
    color: rgb(var(--swatch-primary-darker));
    box-shadow: none;
    background-color: transparent;
}

/*--- Tabs ---*/

:is(.yui-navset,.yui-navset-top,.yui-navset-bottom) .yui-content {
    border: none;
}

/*--- Table ---*/
table.wiki-content-table th { box-shadow: none; }

/*--- Footnotes ---*/

.hovertip {
    -webkit-backdrop-filter: blur(0.125rem);
    backdrop-filter: blur(0.125rem);
}
.hovertip::before {
    box-shadow: none;
    border: solid 0.1rem rgb(var(--hoverblock-header-bg));
}

.footnote .f-content, .reference .r-content {
    font-family: var(--body-font);
}

.footnotes-footer, .bibitems {
    background-color: rgb(var(--footnotes-footer-bg-color),0.375);
}

:is(.footnotes-footer,.bibitems) .bibitem::after, :is(.footnotes-footer,.bibitems) a[href*=javascript]::before {
    right: -30%;
    background-color: transparent;
}


/*--- Tags ---*/
#main-content .page-tags span a:any-link:not([href^="/system:page-tags/tag/_"])::after {
    background: currentColor;
    box-shadow: none;
}

/*---------------------*/
/*------ BOTTOM ------*/
/*---------------------*/

div.page-watch-options a {
   --hover-link-color: var(--swatch-text-tertiary-color);
}

div[id*="page-options-bottom"]:not(.page-rate-widget-box):not(#search-top-box-form) > a.btn {
    --ui-button-txt: var(--bright-accent);
    border: solid 0.05rem rgb(var(--bright-accent));
    z-index: initial;
}

#who-rated-page-area>div>span[style*=color] {
    --swatch-text-tertiary-color: var(--swatch-tertiary-color);
}

#footer {
    --footer-link-color: var(--swatch-primary);
    --footer-link-hover-color: var(--swatch-alternate-color);
    --footer-link-hover-bg-color: var(--swatch-primary-darker);
    position: relative;
    margin-top: calc(6.25rem + 10vw);
    background: transparent;
    isolation: isolate;
}
#footer::before {
    content: "";
    position: absolute;
    bottom: 0; left: 0;
    height: calc(8rem + 13.25vw); width: 100%;
    background-image: var(--space-footer-bg);
    background-size: cover;
    background-position: center top;
    z-index: -1;
    pointer-events: none;
}

#license-area {
    --license-text-color: var(--swatch-text-secondary-color);
    --license-link-hover-color: var(--swatch-alternate-color);
    --license-link-hover-bg-color: var(--swatch-primary-darker);
}

/*---------------------*/
/*------ MODAL ------*/
/*---------------------*/
#odialog-container div.owindow>div.modal-body>img[style*="padding: 2px 8px;"]:first-child { box-shadow: none; }

[[/module]]
[[else]]
[[include fragment:fix-sidebar-themes]]
[[include component:sigma-plus]]
[[module CSS]]
/*
    Generic Space Theme
    [2023 Wikidot Theme]
    By EstrellaYoshte
    Based on:
       Oceanbound Theme by EstrellaYoshte
*/


@import url('https://api.fonts.coollabs.io/css2?family=Urbanist:ital,wght@0,400;0,700;1,400;1,700&display=swap');
@import url('api.fonts.coollabs.io/css2?family=DM+Sans:ital,wght@0,400;0,700;1,400;1,700&display=swap');
@import url('https://api.fonts.coollabs.io/css2?family=Martian+Mono:wght@300;700&display=swap');

:root {
    --header-title: "SCP FOUNDATION";
    --header-subtitle: "Созвездия, Кометы, Планеты";

    --sp_area-max-width: 67.25rem;
    --sp_side-bar-width: 14.675rem;

    --sp_header-logo: url(https://scpfoundation.net/local--files/theme:space/spacelogo.png);
    --sp_header-height: calc(22.5rem + 14.5vw);
    --sp_header-title-scale: 1.375;
    --sp_header-subtitle-scale: 1.75;

    --sp_top-bar-height: 2.25rem;
    --sp_top-bar-link-color: rgb(var(--space-planet));
    --sp_top-bar-link-hover-color: rgb(var(--space-sun));
    --sp_top-bar-link-hover-background: rgb(var(--space-moon) / 0.175);
    --sp_dropdown-background: rgb(var(--space-void) / 0.625);
    --sp_dropdown-link-hover-color: rgb(var(--space-void));
    --sp_dropdown-link-hover-background: rgb(var(--space-planet));
    --sp_dropdown-link-border: none;
    --sp_dropdown-link-color: var(--sp_top-bar-link-color);

    --sp_rate-module-background: rgb(var(--space-void));
    --sp_rate-module-text-color: rgb(var(--space-sun));
    --sp_rate-module-sub-color: rgb(var(--space-moon));
    --sp_rate-module-active-color: rgb(var(--space-planet));
    --sp_rate-module-button-color: rgb(var(--space-sun));
    --sp_rate-module-button-background: transparent;
    --sp_rate-module-button-hover-color: rgb(var(--space-void));
    --sp_rate-module-button-hover-background: rgb(var(--space-planet));

    --sp_tab-text-color: rgb(var(--space-starlight));
    --sp_tab-background: rgb(var(--space-void));
    --sp_tab-hover-text-color: rgb(var(--space-planet));
    --sp_tab-hover-background: var(--sp_tab-background);
    --sp_tab-selected-text-color: rgb(var(--space-void));
    --sp_tab-selected-background: rgb(var(--space-planet));

    --space-body-bg: url(https://scpfoundation.net/local--files/theme:space/spacebg.jpg);
    --space-header-layer-0: url(https://scpfoundation.net/local--files/theme:space/Milky_way.jpg);
    --space-header-layer-1: url(https://scpfoundation.net/local--files/theme:space/spacehead-layer1.png);
    --space-footer-bg: url(https://scpfoundation.net/local--files/theme:space/spacefooter.png);
    --space-blackhole: 0 0 0;
    --space-void: 22 23 33;
    --space-starlight: 255 255 255;
    --space-planet: 92 222 236;
    --space-nebula: 95 172 188;
    --space-sun: 255 191 165;
    --space-moon: 229 214 157;

