[[include fragment:fix-sidebar-themes]]
[[module CSS]]
/*
    SPC Theme
    [2018 Wikidot Theme]
    Based on Aelanna's Sigma 9 and Greenwolf's Third Law and djkaktus' Darkbody Theme
    Edited by PeppersGhost for the Doomsday Contest 
    SPC logo by BABAAL from CN branch.
*/
 
a:visited {
    color: #176784;
}
 
/* HEADER */
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: url(/local--files/theme:spc/SPCLogo50.png) 10px 35px no-repeat;
}
 
#header h1 a {
    content: "База данных СКП";
}
 
#header h1 a::before {
    content: "База данных СКП";
}
 
#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}
 
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "Сообщество Колочения Пластиножаберных";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #f0f0c0;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
 
div#container-wrap {
    background-image: url(/local--files/theme%3Aspc/SPCWrapper.jpg);
}
 
/* Source: https://www.flickr.com/photos/telemax/2818520042 */
 
/* SIDEBAR */
#side-bar .side-block {
    border-color: #0e7dcd;
    box-shadow: 0 2px 6px rgba(0,0,51,.5);
}
 
/* Модуль щобы покрасить кнопку "Стать участником" */
#u-become-member {
    border-color: #0e7dcd;
    box-shadow: 0 2px 6px rgba(0,0,51,.5);
}

#side-bar img {
  width:13px;
  height:13px;
  box-sizing:border-box;
  padding-left: 13px;
  background: url(/local--files/theme%3Aspc/FirstIconTransparent.png) left top no-repeat;
}
/* Source: https://commons.wikimedia.org/wiki/File:Noun_project_758.svg */
 
#side-bar .heading {
    color: #0e7dcd;
    border-bottom: solid 1px #0e7dcd;
}
 
#side-bar div.menu-item.inactive a {
    color: #0b65c0;
}
 
#side-bar a:visited {
    color: #0c499c;
}
 
#side-bar div.menu-item .sub-text {
    color: #0b65c0;
}
 
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #0b65c0;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #0b65c0;
}
 
.interwiki_title {
    color: #0b65c0;
}
 
.interwiki_entry a {
    color: #0b65c0;
}
 
/* LINKS */
 
a {
    color: #0c499c;
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #0c499c;
    text-decoration: none;
    background: transparent;
}
 
/* INFO PANE */
.rate-box-with-credit-button {
    background-color: #132639 !important;
    border-color: #0b65c0 !important;
}
 
.page-rate-widget-box{
    border: none !important;
}
 
.rate-box-with-credit-button .fa-info {
    border-color: #0b65c0 !important;
    color: #f3f3f3 !important;
}
 
.rate-box-with-credit-button .fa-info:hover {
    color: #3182d3 !important;
}
 
.rate-box-with-credit-button .cancel {
    border: none !important;
}
 
.close-credits{
    filter: hue-rotate(210deg);
}
 
.modalbox {
    box-shadow: 0px 1px 10px rgb(39,130,221,0.3) !important;
}
 
/* RATING MODULE */
.page-rate-widget-box .rate-points {
    background-color: #132639 !important;
    border: solid 1px #0b65c0;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: 132639;
    border-top: solid 1px #0b65c0;
    border-bottom: solid 1px #0b65c0;
    font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: 132639;
    color: #0b65c0;
    padding: 0 4px;
    margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #132639;
    color: #0b65c0;
    text-decoration: none;
}
.page-rate-widget-box .cancel {
    background-color: #132639;
    border: solid 1px #0b65c0;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #0b65c0;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #132639;
    color: #fffff0;
    text-decoration: none;
}
 
/* PAGE TITLEs */
h1, #page-title {
    color: #0c499c;
}
 
/* NAV DROPDOWN */
 
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    color: #0c499c;
}
 
/* SEARCH BAR */
#search-top-box-input {
    border: solid 1px #0b65c0;
    background-color: #132639;
}
 
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #0b65c0;
    background-color: #132639;
}
 
#search-top-box-form input[type=submit] {
    border: solid 1px #0b65c0;
    background-color: #132639;
    color: #0b2447;
    background: linear-gradient(to bottom, #2373cd,#319ec2,#183291);
}
 
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #0b65c0;
    color: #0b2447;
    background-color: #132639;
    background: linear-gradient(to bottom, #2373cd,#319ec2,#183291);
}
 
.scpnet-interwiki-frame{
     filter: hue-rotate(260deg) brightness(150%);
}
 
/* LOGIN STATUS */
#login-status ul a {
    color: #0c499c;
}
 
/* Changes background for collapsed sidebar */
@media (max-width: 767px) {
#side-bar{background-color: #0b65c0}
}

[[/module]]
