[[include fragment:fix-sidebar-themes]]
[[module CSS]]
/*
    Technoblast Theme
    [2020 Wikidot Theme]
    By EstrellaYoshte
    Based on:
        Simple Yonder Theme by EstrellaYoshte
        Anderson Robotics Theme by Croquembouche
        Flopstyle CSS by Lt Flops
*/

@import url('https://fonts.googleapis.com/css2?family=Ruda&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Russo+One&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Fira+Code:wght@300&display=swap');

body {
    color: #000000;
    font-family: 'Ruda', sans-serif;
    font-size: 14.2px;
    word-break: unset;
}

.page-source, .code pre, .code p, .code, tt{ /* ---- Code by Croquembouche ---- */
    font-family: "Fira Code", monospace;
    font-size: 13.8px;
}

a {
    color: #00AAB3;
}

a:visited {
    color: #013737;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #013737;
    font-family: 'Russo One', sans-serif;
    font-weight: normal;
}

#account-topbutton{
    border: 0px solid #000;
}

/* ---- PAGE WRAP ---- */
 div#container-wrap{
     background-image: url(https://files.scpfoundation.net/local--files/theme:technoblast/bg.png);
}

div#header {
    background-image: url(https://files.scpfoundation.net/local--files/theme:technoblast/logoaiad.png);
    background-size: 12.5%;
}

#page-title {
    font-family: 'Russo One', sans-serif;
    font-size: 2.1em;
    font-weight: normal;
    color: #013737;
}

#login-status,
#login-status a {
    color: white;
    font-weight: bold;
}

#footer {
    background: transparent;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: none;
    background: #282828;
    box-shadow: none;
    border-radius: 3px;
    color: #f4f4f4;
}

#search-top-box input.empty {
    color: #bbbbbb;
}

#top-bar a,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover {
    color: #00AAB3;
}

div#header h1 a span {
    font-family: 'Russo One', sans-serif;
    font-weight: normal;
    font-size: 0.88em;
    color: #00ffff;
    text-shadow: 0px 0px 0px #000;
}
div#header h2 span {
    font-family: 'Ruda', sans-serif;
    font-weight: normal;
    color: #ff9944;
    padding: 17px 0;
    text-shadow: 0px 0px 0px #000;
    white-space: pre;
}

.scp-image-block.block-right {
    margin: 0 2em 1em;
}

@media (min-width: 767px) {
     #header {
             background-position: 0em 1.46em;
     }
}

/* ---- SIDE BAR ---- */

 #side-bar .side-block{
     border: none;
     border-radius: 3px;
     box-shadow: none;
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('https://files.scpfoundation.net/local--files/theme:technoblast/darkbg.png')!important;
     color: #00ffff;
}

/* Модуль щобы покрасить кнопку "Стать участником" */
#u-become-member {
     border: none;
     border-radius: 3px;
     box-shadow: none;
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('https://files.scpfoundation.net/local--files/theme:technoblast/darkbg.png')!important;
     color: #00ffff;
}

 #side-bar .heading{
     color: #ff9944;
     border-bottom: solid 2px #ff9944;
}

#side-bar .menu-item > img {
  display: none;
}

#side-bar .menu-item > a:hover {
    background-color: #00ffff;
    color: #013737;
    text-decoration: none;
}

div.open-menu a {
    border-radius: 3px;
    box-shadow: 0px 1px 4px 0 rgba(0, 0, 0, 0.2), 0px 3px 10px 0 rgba(0, 0, 0, 0.19);
    color: #013737!important;
    border: none!important;
}

#side-bar a,
#side-bar a:visited {
    color: #00ffff;
}

#side-bar .collapsible-block-folded{
    background: none;
}

#side-bar .collapsible-block-unfolded-link{
    border-bottom: solid 2px #ff9944;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link{
    color: #ff9944;
}

@media (max-width: 767px) {
    #main-content {
        padding: 0;
        margin: 0 5%;
        border-left: none;
    }
    #page-title {
        margin-top: 0.7em;
    }
    #side-bar {
        background-color: #013737;
        left: -19em;
    }
    #side-bar:target {
        border: none;
        box-shadow: 3px 0 1px -2px rgba(0,0,0,0.04), 1px 0 5px 0 rgba(0,0,0,0.2);
    }
    #side-bar .close-menu {
        transition: width 0.5s ease-in-out 0.1s,
                    opacity 1s ease-in-out 0s;
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        right: 0;
        background: rgba(0,0,0,0.3);
        background-position: 19em 50%;
        z-index: -1;
        opacity: 0;
        pointer-events: none;
    }
    #side-bar:target .close-menu {
        width: calc(100% - 19em);
        right: 0;
        left: auto;
        opacity: 1;
        pointer-events: auto;
    }
    #page-content > hr, #page-content > .list-pages-box > .list-pages-item > hr {
        margin: 3em -5.5%;
    }
    #side-bar {
        top: 0;
    }
    #side-bar .heading {
        padding-left: 1em;
        margin-left: -1em;
    }
    #search-top-box {
        top: 107px;
    }
}

/* ---- TABS ---- */

/* ---- YUI TAB BASE ---- */
.yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a{background-color:inherit;background-image:inherit}.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus{background:inherit;text-decoration:inherit}.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:hover{color:inherit;background:inherit}.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav{border-color:inherit}.yui-navset li{line-height:inherit}

/* ---- YUI TAB CUSTOMIZATION ----*/

 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: calc(100% - .125rem);
     margin: 0 auto;
     border-color: #013737;
     box-shadow: none;
}
 .yui-navset .yui-nav a, /* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav a{
     color: #013737;
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: #f4f4f4;
     border: unset;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
     color: #f4f4f4;
    /* ---- Tab Background Colour | [HOVER] ---- */
     background-color: #00AAB3;
}
 .yui-navset .yui-nav li, /* ---- Listitem Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav li{
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;
     color: #00ffff;
     background-color: #f4f4f4;
     border-color: transparent;
     box-shadow: none;
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav li em{
     border: unset;
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;

     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
 .yui-navset .yui-nav .selected, /* ---- Selection Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav .selected{
     flex-grow: 2;
     margin: 0;
     padding: 0;
    /* ---- Tab Background Colour | [SELECTED] ---- */
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png');
}
 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a em{
     border: none;
}
 .yui-navset .yui-nav .selected a{
     width: 100%;
     color: #00ffff;
}
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:active{
     color: #00ffff;
     background-color: #013737;
}
 .yui-navset-left .yui-content{
    /* ---- Content Background ---- */
     background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png');
}
 .yui-navset .yui-content,
 .yui-navset .yui-navset-top .yui-content{
     padding: .5em;
     border: none;
}

/* ---- TOP MENU ---- */

#top-bar a {
    color: #f4f4f4;
    font-weight: normal;
}

/* ---- INFO BAR ---- */
 body{
     --barColour: #013737;
}

 .info-container .collapsible-block-content{
     padding: 0 .5em 30px;
}
 .info-container .collapsible-block-content .wiki-content-table{
     width: 100%;
}

/* ---- INFO PANE ---- */

#page-content .creditRate{
     margin: unset;
}
#page-content .rate-box-with-credit-button {
    background-color: #013737;
    border: solid 1px #013737;
    box-shadow: none;
    border-radius: 0;
}

#page-content .rate-box-with-credit-button .fa-info {
    border: none;
    color: #ff9944;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #ff9944;
    color: #013737;
}

.rate-box-with-credit-button .cancel {
    border: solid 1px #013737;
}

/* ---- PAGE RATING ---- */

.page-rate-widget-box {
    box-shadow: none;
    margin: unset;
    border-radius: 0;
}

.page-rate-widget-box .rate-points {
    background-color: #013737 !important;
    color: #00ffff !important;
    border: solid 1px #013737;
    border-radius: 0;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #013737;
    border-top: solid 1px #013737;
    border-bottom: solid 1px #013737;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #00ffff;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #00ffff;
    color: #013737;
}

.page-rate-widget-box .cancel {
    background: transparent;
    background-color: #013737;
    border: solid 1px #013737;
    border-radius: 0;
}

.page-rate-widget-box .cancel a {
    color: #00ffff;
}
.page-rate-widget-box .cancel a:hover {
    background: #00ffff;
    color: #013737;
    border-radius: 0;
}

/* ---- PAGE ELEMENTS ---- */

#page-content .wiki-content-table tr th {
    border: solid 1px #013737;
    color: #f4f4f4;
    background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png');
    /* set border for table title */
}

#page-content .wiki-content-table tr td {
    border: solid 1px #013737;
    /* set border for table content */
}

blockquote,
div.blockquote,
#toc,
.code {
     background-color: #f5f5f5;
     border: solid 1px #013737;
}

.scp-image-block {
    border: solid 1px #013737;
    box-shadow: none;
}
.scp-image-block .scp-image-caption {
    background: linear-gradient(rgba(1,55,55,0), rgba(1,55,55,0)), url('/local--files/theme%3Atechnoblast/darkbg.png');
    border-top: solid 1px #013737;
    color: #f4f4f4;
}

/* ---- Horizontal Rules ---- */

hr {
  background-color: rgba(1,55,55,0.46);
}
[[/module]]
