[[module CSS]]
/*
    Turbo Vision Theme
    [2021 Wikidot Theme]
    By JakdragonX
    Based on SCP Sigma 9-Theme created by Aelanna and Dr Devan
    Based on the original Turbo Vision Theme created by Croquembouche

    The Turbo Vision theme is split into two CSS fragments.

    This fragment styles general parts of the page, and requires the other
    fragment in order to function correctly.
*/

/* TODO Split these selectors across this one and the other one */

body {
  font-family: "IBM Plex Mono", sans-serif;
  font-size: 16px;
  line-height: 22px;
  color: black;
  letter-spacing: 0.5px;
  background-color: #bfcae3;
  image-rendering: pixelated;
}

a {
  color: #840603;
}

a:hover {
  text-decoration: none;
  background-color: #11ff00;
  color: black;
}

a:visited {
  color: #690ba0;
}

a.newpage {
  color: #f55;
  text-decoration: none;
  background: transparent;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: "Web437 IBM VGA 8x16", monospace;
}

h1,
#page-title {
  font-family: "Web437 IBM VGA 8x16", monospace;
  color: #940000;
  font-weight: normal;
  line-height: 0.85em;
}

#page-title {
  border-color: #fff;
}

hr {
  background-color: #fff;
  margin: 3.5em 1em 3.5em 1em;
}

blockquote,
div.blockquote {
  background-color: #aaaaaa;
  color: #000;
  padding: 0;
  margin-bottom: 2em;
  border-width: var(--turbo-block-border-width);
  border-style: solid;
  border-image: url("https://scpfoundation.net/local--files/theme:turbo-vision/border_white.svg") 16 round;
  box-shadow: var(--turbo-block-shadow);
}

/* ACCOUNT STUFF (OPTIONS, SEARCH BAR, ETC.) */

#account-options {
  background-color: #fff;
}

#login-status {
  color: #000;
  background: #aaa;
  border: 2px solid #fff;
  font-size: 90%;
  z-index: 30;
}

#login-status a {
  background: transparent;
  color: #840603;
}

#login-status a:hover {
  background: #11ff00 !important;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type="submit"],
#search-top-box-form input[type="submit"]:hover,
#search-top-box-form input[type="submit"]:focus {
  border: #fff solid 2px;
  background: #aaaaaa;
  box-shadow: none;
  border-radius: 0;
  color: #fff;
}

#side-bar .side-block.media {
  background: #aaaaaa;
}

/* HEADER */

div#container-wrap {
  background: url("https://scpfoundation.net/local--files/theme:turbo-vision/small%20%281%29.png")
    top left repeat-x;
}

div#header {
  position: relative;
  height: 142px;
  background: url("https://scpfoundation.net/local--files/theme:turbo-vision/logo%20%281%29.png")
    3.675em 1.25em no-repeat;
  background-size: auto 100px;
}

div#header {
  max-width: initial;
}

div#header,
#top-bar {
  width: 100%;
}

div#header h1 {
  opacity: 0;
  width: 310px;
  min-height: 135px;
}

div#header h2 {
  display: none;
}

#header h1,
#header h2 {
  margin-left: 55px;
}

/* TOP BAR */

#top-bar {
  font-size: 100%;
  top: 142px;
  max-width: none;
  height: 20px;
}

#top-bar ul li {
  margin: 0 4px;
}

#top-bar ul li a {
  font-family: "Web437 IBM VGA 8x16", monospace;
  color: #000;
  background-color: transparent;
  padding: 0;
  border: none;
  line-height: 19px;
  max-height: 36px;
}

#top-bar ul li a:hover,
#top-bar ul li:hover a {
  text-decoration: none;
  border: none;
  background-color: #11ff00 !important;
  color: #000;
}

#top-bar ul li a:hover::before,
#top-bar ul li a:hover::after {
  background-color: #11ff00;
}

#top-bar ul li a:visited {
  color: #000 !important;
}

#top-bar ul li a::first-letter {
  color: #840603;
}

#top-bar .open-menu a {
  border: 2px solid #3f3f47;
  border-radius: 0;
  background-color: #aaaaaa;
  color: #840603 !important;
}

/* SIDE BAR */

#side-bar .side-block,
#interwiki .side-block {
  position: relative;
  border-radius: 0;
  border-width: 22px;
  border-style: solid;
  padding: 0;
  border-image: url("https://scpfoundation.net/local--files/theme:turbo-vision/border_white2.svg")
    16 round;
  box-shadow: 0.5em 1em 0 0 #3e3f47;
  margin-bottom: 2em;
}

/* REMOVE
.turbo-block .heading {
  position: relative;
  border: none;
  margin: 2em 0 0 -11px;
  width: calc(100% + 22px);
  max-width: none;
}

.turbo-block .heading::before {
  content: "";
  border-top: 3px double white;
  position: absolute;
  width: 100%;
  top: calc(50% - 1.5px);
  left: 0;
}

.turbo-block .heading p {
  display: table;
  position: relative;
  padding: 0 8px;
  background: inherit;
  margin: 0 0 0 2em;
  color: white;
}
REMOVE */


#side-bar .heading,
#interwiki .heading {
  position: relative;
  font-size: 100%;
  border: none;
  padding: 0;
  margin: 1em 0 0 -11px;
  width: calc(100% + 22px);
}

#side-bar .heading::before,
#interwiki .heading::before {
  content: "";
  border-top: 1px solid white;
  position: absolute;
  width: 100%;
  top: calc(50% - 1.5px);
  left: 0;
}

#side-bar .heading p,
#interwiki .heading p {
  position: relative;
  display: inline-block;
  padding: 0 8px;
  margin-left: 1.5em;
  color: white;
  font-weight: normal;
}

#side-bar .side-block,
#side-bar .side-block .heading p,
#interwiki .side-block,
#interwiki .side-block .heading p {
  background-color: #64bec4;
  font-size: 90%;
}

#side-bar .side-block.resources,
#side-bar .side-block.resources .heading p {
  background-color: #aaaaaa;
  font-size: 90%;
}

#side-bar div.menu-item a,
#side-bar .collapsible-block-link,
#interwiki div.menu-item a,
#interwiki .collapsible-block-link {
  font-weight: normal;
}

#side-bar {
  background-color: #a4a6ba;
}

/* TABS */

div.yui-navset div.yui-content {
  color: #000000;
  background: #aaaaaa;
  border-radius: 0;
  border-width: 22px;
  border-style: solid;
  padding: 0;
  border-image: url("https://scpfoundation.net/local--files/theme:turbo-vision/border_white.svg")
    16 round;
  box-shadow: 0.5em 1em 0 0 #3e3f47;
  margin-bottom: 2em;
}

.yui-navset .yui-nav a,
div.yui-navset div.yui-navset-top .yui-nav a {
  /* protect nested tabviews from other orientations */
  border: #dfdfdf outset 2px;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
  font-family: "Web437 IBM VGA 8x16", monospace;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
  border-color: transparent;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
  border-color: #aaaaa;
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
  background: #aaaaaa;
  border: #dfdfdf solid 2px;
  border-bottom: transparent 0;
}

div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
  background: #11ff00;
  border: #fff solid 2px;
  border-bottom: transparent 0;
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
  background: #011657;
  border: #fff solid 2px;
  border-bottom: transparent 0;
}

/* FIXED ISSUE WITH BOLDING ON TAB NAVIGATION */
.yui-nav {
  font-weight: bold;
}

/* SIMPLE TABLES */

table.wiki-content-table {
  box-shadow: var(--turbo-block-shadow);
  margin-bottom: 2em;
}

table.wiki-content-table td {
  border: 1px solid #fff;
  background-color: #aaaaaa;
}

table.wiki-content-table th {
  border: 1px solid #011657;
  color: #fff;
  background-color: #011657;
}

/* RATING MODULE */

#page-content .page-rate-widget-box {
  box-shadow: none;
  font-family: "Web437 IBM VGA 8x16", monospace;
  font-weight: normal;
}

div.page-rate-widget-box .rate-points {
  background-color: #011657;
  border: 2px solid #011657;
  border-radius: 0;
  color: #fff;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #fff;
  border-radius: 0;
  border: 2px solid #011657;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background: transparent;
  color: #011657;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: transparent;
  color: #840603;
}
.page-rate-widget-box .cancel {
  background: transparent;
  border: 2px dashed #011657;
  background-color: #011657;
  border-radius: 0;
}
.page-rate-widget-box .cancel a {
  color: #fff;
}
.page-rate-widget-box .cancel a:hover {
  background-color: transparent;
  color: #840603;
}

/* CREDIT MODULE */

#page-content .rate-box-with-credit-button {
  background-color: #011657 !important;
  border: 1px solid #011657;
  line-height: 14px;
  border-radius: 0;
  color: #fff;
  box-shadow: none;
}
#page-content .rate-box-with-credit-button .creditButton p a {
  border-left-color: #011657;
  border: 3px solid #011657;
}
#page-content .rate-box-with-credit-button .creditButton a {
  color: #fff !important;
}
#page-content .rate-box-with-credit-button .creditButton a:hover {
  color: #840603;
}

#page-content .modalbox {
  background: #ced2eb !important;
  color: #000;
  box-shadow: none;
  border-radius: 0;
}

/* LOCK INFO /*

#lock-info {
   background-color: #011657;
}

/* FIXING MOBILE SHIT */

@media (max-width: 770px) {
  #top-bar ul li {
    height: 1.25em;
  }
}

@media (max-width: 650px) {
  div#header {
    background-position: 3.675em 2.95em;
    background-size: auto 75px;
  }
}
[[/module]]
