[[module css]]
/* ---------------------------------
War on All Fronts (Black Highlighter)
2020 Wikidot Theme
Created by stormbreath
Header image created by EstrellaYoshte
--------------------------------- */

/* -------------- ROOT -------------- */
@font-face {
    font-family: 'Trender';
    src: url("https://scpfoundation.net/local--files/theme:war-on-all-fronts-bhl/Trender.ttf") format('truetype');
}

:root {
    /* S-CSS-P Integration */
    --theme-base: "black-highlighter";
    --theme-id: "woaf-bhl";
    --theme-name: "War on All Fronts Theme (BHL)";

    /* Header */
    --logo-image: url("https://scpfoundation.net/local--files/theme:war-on-all-fronts-bhl/mksjr3.png");
    --header-title: "Война по всем фронтам";
    --header-subtitle: "Нам понадобится лодка побольше";

    /* Typefaces */
    --header-font: 'Trender', cursive;
    --title-font: 'Trender', cursive;

    /* Standard Colors */
    --white-monochrome: 245, 245, 245;
    --pale-gray-monochrome: 235, 235, 235;
    --pale-accent: 60, 40, 230;
    --bright-accent: 5, 0, 133;
    --medium-accent: 44, 46, 100;
    --dark-accent: 15, 3, 100;
    --alt-accent: 17, 102, 221;
    --deepsea: linear-gradient(to bottom, rgb(0, 0, 101), rgb(23, 0, 82), rgb(0, 0, 0));

    /* Header Gradients */
    --gradient-header: var(--deepsea);
    --diagonal-stripes: none;

    /* Other Variables */
    --rating-module-bg-color: var(--pale-gray-monochrome);
}

#side-bar,
#interwiki {
    --sidebar-links-text: 173, 171, 181;
    --sideblock-heading-text-color: var(--white-monochrome);
    --social-icon-hover-color: 173, 171, 181;
}

/* -------------- HEADER -------------- */
#header h1 a:before,
#header h2 span:before {
    color: rgb(173, 171, 181);
    font-variant: small-caps;
}

#top-bar div.top-bar>ul>li>ul,
#top-bar div.mobile-top-bar>ul>li>ul {
    background-image: var(--deepsea);
}

/* -------------- SIDE-BAR -------------- */
#side-bar {
    background-image: var(--deepsea);
    background-attachment: fixed;
    background-size: unset;
}

#side-bar div.menu-item a:hover,
#interwiki div.menu-item a:hover {
    background-color: rgba(173, 171, 181, .15);
    color: white;
}

#side-bar div.menu-item a:before,
#side-bar div.menu-item a:hover:before,
#interwiki div.menu-item a:before,
#interwiki div.menu-item a:hover:before {
    background-color: rgba(173, 171, 181, .25);
}

#side-bar .side-block.media,
#side-bar .side-block.resources {
    background: transparent !important;
}

#side-bar .heading p,
#interwiki .heading p,
#side-bar .side-block>.collapsible-block .collapsible-block-link {
    font-family: var(--header-font);
    font-weight: initial;
}

#side-bar::-webkit-scrollbar-track {
    background-image: var(--deepsea);
}

#side-bar::-webkit-scrollbar,
#side-bar::-webkit-scrollbar-thumb,
#side-bar::-webkit-scrollbar-corner {
    border-color: rgba(71, 79, 107, .6);
}

/* -------------- BODY -------------- */
#page-title {
    color: rgb(var(--medium-accent));
    border-color: rgb(var(--medium-accent));
    text-align: center;
}

.close-credits,
.credit-back {
    -webkit-filter: hue-rotate(260deg);
            filter: hue-rotate(260deg)
}

#page-content .modalbox {
    box-shadow: 0 2px 6px rgba(0, 0, 100, .5);
    background: linear-gradient(rgb(200, 200, 200) 51px,
     rgb(240, 240, 240) 51px,
     rgb(240, 240, 240)) !important;
}

.info-container {
    --barColour: #336;
}

.anom-bar-container {
    --title-font: Poppins, sans-serif;
}

blockquote,
div.blockquote {
    background: rgb(var(--pale-gray-monochrome));
}

div.woaf-hr {
    height: .3em;
    background: linear-gradient(to right,
     rgb(0, 0, 0),
     rgb(23, 0, 82),
     rgb(0, 0, 101),
     rgb(23, 0, 82),
     rgb(0, 0, 0));
    margin: 1em 0;
    border-radius: 5px;
}

div.woaf-hr p,
div.woaf-hr ul {
    display: none;
}

#footer a,
#footer a:visited {
    color: white;
}
[[/module]]
