[[module css]]
/*
    Xmas Theme
    [2020 Wikidot Theme]
       Originally made by Dc_Yerko for the Spanish Branch
       Ported to -EN by stormbreath
*/

div#container-wrap {
    background-image: url('https://scpfoundation.net/local--files/theme:xmas-global/GIF-111.gif');
}
#header {
    background-image: url('https://scpfoundation.net/local--files/theme:xmas-global/logo-xmas.png');
}

#top-bar div.open-menu a {
    color: #990011;
    border-color: #33552D;
};
[[/module]]

[[module css]]
#w-extra-div-4, #w-extra-div-5, #w-extra-div-6 {
    -webkit-mask-image: linear-gradient(to bottom, rgba(0,0,0,1) 50%, rgba(0,0,0,0));
    mask-image: linear-gradient(to bottom, rgba(0,0,0,1) 50%, rgba(0,0,0,0));
    background-repeat: repeat;
    height: 270px;
    width: 100%;
    position: absolute;
    left: 0;
    z-index: 2;
    pointer-events: none;
    top: 0;
    left: 0;
}

/* ---- SNOWFALL, adapted from https://github.com/elena-in-code/Css-Snow-Fall-Animation ---- */

#w-extra-div-4 {
    background-image: url('https://scpfoundation.net/local--files/theme:xmas-global/sn2.png');
    background-size: 230px;
    animation: snowOne 83s infinite linear;
    filter: blur(1.3px);
}
#w-extra-div-5 {
    background-image: url('https://scpfoundation.net/local--files/theme:xmas-global/sn.png');
    background-size: 370px;
    animation: snowTwo 39s infinite linear;
    filter: blur(0.4px);
}
#w-extra-div-6 {
    background: url('https://scpfoundation.net/local--files/theme:xmas-global/sn.png');
    background-size: 720px;
    animation: snowThree 21s infinite linear;
    filter: blur(2.4px);
}
@keyframes snowOne{
    0%
    {
     background-position: 0px 0px;
            opacity: 0;
    }
    10%
    {
            opacity: 0.6;
    }
    90%
    {
            opacity: 0.5;
     }
    100%
    {
     background-position: 100px 750px;
     opacity:0;
    }
}
@keyframes snowTwo{
    0%
    {
     background-position: 0px -100px;
     opacity: 0;
    }
    8%
    {
            opacity: 0.74;
    }
    90%
    {
            opacity: 0.65;
     }
    100%
    {
     background-position: -50px 700px;
     opacity: 0;
    }
}
@keyframes snowThree{
    0%
    {
     background-position: 0px 100px;
     opacity: 0;
    }
    13%
    {
            opacity: 0.9;
    }
    91%
    {
            opacity: 0.8;
    }
    100%
    {
     background-position: 240px 750px;
     opacity: 0;
    }
}
[[/module]]
