[[module CSS]]
.themes-block, .themes-warning {
  background-color: #a2a2a24d;
  padding: 1em;
  border-bottom: #a2a2a2 3px solid;
  margin: 1em 0;
}

.themes-warning {
  background-color: #9900111f;
  border-color: #901;
}

.marker-red {
  background: #901;
  color: white;
  border-radius: 50%;
  width: 25px;
  height: 25px;
  justify-content: center;
  align-items: center;
  display: flex;
  margin-right: 10px;
}

.rule-content {
  padding: 0px 0px 0px 35px;
  margin: 0;
}

.rule-heading {
  display: flex;
  align-items: center;
  font-weight: 600;
  font-size: 125%;
}

.rule-heading p {
  margin: 0;
  max-width: 85%;
}

#u-back a:visited {
    color: #b01;
}

.page-block {
  display: block;
  padding: 24px;
  border: 1px solid #f7f7f7;
  box-shadow: 0px 2px 4px #00000011;
  transition: all 0.2s ease;
  background: white;
  text-decoration: none !important;
  color: black !important;
}

.page-block br {
  display: none;
}

.page-block:hover {
  background: #f7f7f7;
  border: 1px solid #f0f0f0;
  box-shadow: 0px 2px 7px #00000022;
}

.page-block .title {
  display: block;
  font-size: 24px;
  line-height: 30px;
  font-weight: 500;
  color: #b01;
  width: 80%;
}

.page-block .link-name {
  display: block;
  margin-top: 8px;
}

.page-block .date {
  display: block;
  color: #666;
  font-style: italic;
  margin: 8px 0px 8px 0px;
  font-size: 11px;
  line-height: 16px;
}

.list .list-pages-box {
  display: flex;
  flex-direction: column-reverse;
  align-items: flex-end;
}

.list .list-pages-box > p {
  margin: 0;
}

.list .pager {
  position: sticky;
  top: 2rem;
  background: #ffffff99;
  backdrop-filter: blur(5px);
  border-radius: 5px;
  padding: 5px;
  margin: 0 0 0.5rem 0;
  display: flex;
  flex-wrap: wrap;
  align-items: baseline;
  gap: 2px;
  justify-content: flex-end;
}

.list .pager > *, .list .pager > * > a {
  margin: 0 !important;
}

.list .pager-no {
  padding: 0 0.5em 0 0;
}

@media (min-width: 900px) {
  .list .list-pages-box > p {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 8px;
  }

  .list .list-pages-box > p > br {
  display: none;
  }
}

@media (max-width: 900px) {
  .list .pager {
  max-width: 80% !important;
  top: 1rem;
  }
}

