[[include component:translation-bars name=%%name%%]]
[[include component:article-bars]]

%%content%%

[[module CSS]]
#page-content {
  position: relative;
  padding-bottom: 32px;
}
.more-author-articles {
  position: absolute;
  left: 0;
  bottom: 0;
  padding: 4px;
  padding-bottom: 0;
  border-top: 1px solid #BBB;
}
.more-author-articles p {
  margin: 0; padding: 0;
}
[[/module]]

[[iftags +ru]]
