[[module css]]
/* фикс toc'а */
:root {
    --link-color: var(--medium-accent);
    --visited-link-color: var(--medium-accent);
    --hover-link-color: var(--dark-gray-monochrome);
}

.article {
    border: solid 1px #888880;
    border-radius: 1px;
    overflow: hidden;
    margin-bottom: 20px;
    box-shadow: 3px 3px 6px #666;
    background: rgb(var(--gray-monochrome));
}

.article-title {
    display: inline-block;
    float: left;
    padding-left: 15px;
    margin-top: -8px;
    margin-bottom: -8px;
    font-weight: bold;
    font-size: 90%;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.35);
    color: white;
}

.article-title a{
    color: rgb(var(--bright-accent));
}

.rating {
    display: inline-block;
    float: right;
    padding-right: 15px;
    margin-top: -8px;
    margin-bottom: -8px;
    font-weight: bold;
    font-size: 90%;
    color: #fff;
}

.article-body {
    display: inline-block;
    width: auto;
    height: 140px;
    padding-left: 15px;
    padding-right: 15px;
    font-style: italic;
    background: rgb(var(--bright-accent));
}

.article-footer {
    display: inline-block;
    width: 100%;
    height: 17px;
    padding-left: 15px;
    padding-top: 3px;
}

.article-footer a {
    margin-right: 15px;
}

.col-left {
    float: left;
    width: 48%;
}
[[/module]]
