[[include theme:dustjacket]]

[[module CSS]]
:root {
    /* Когда будете делать тематическое оформление, поменяйте значение этой переменной на нужный цвет в формате "r,g,b" (например "127,127,127"). Изменяет рамку основного блока и цвет горизонтальных линий внутри него. Текущий цвет взят из первого крыла. */
    --wing-color: var(--gray-monochrome);
}

.wing {
    position: relative;
    background-color: #FFFEED;
    border: 3px solid rgb(var(--wing-color));
    border-radius: 1.5rem;
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5), inset 0 1px rgba(255, 255, 255, .3), inset 0 10px rgba(255, 255, 255, .2), inset 0 10px 20px rgba(255, 255, 255, .25), inset 0 -15px 30px rgba(0, 0, 0, .1);
    padding: 1rem;
}

/*.wing::after {
    content: "";
    /*border-image: url('Сюда вставлять изображение для контура основного блока страницы. Для примера смотрите первое и второе крылья, а также первый зал. Как только вы вставите сюда изображение правильного разрешения, отображение нижнего края страницы автоматически пофиксится. Не забудьте вытащить всё это CSS-правило из комментария') 55% 40% 1% 50% /     240px;*/
    border-style: solid;
    width: 106%;
    height: 100%;
    position: absolute;
    top: -1.8rem;
    pointer-events: none;
    left: -1.5rem;
}*/

hr {
    background: rgb(var(--wing-color));
    border: 1px solid rgb(var(--wing-color));
    height: 2px;
    border-radius: 1.5rem;
}
[[/module]]

